INSERT INTO `mst_instructions` (`instruction_id`, `instruction_name`, `instruction_type`, `instruction_description_hindi`, `instruction_description_english`, `instruction_description_marathi`, `isActive`, `created_at`, `updated_at`) VALUES
(1, 'Compression', 'adjunct', '{\"0\":\"                    \",\"1\":\"   /           /            / \"}', '{\"0\":\"Apply compression to the affected area using a compression garment suggested by your therapist or using a bandage.\",\"1\":\"Loosen up the compression if you find any tingling/numbness or if the area distal to the compression is pale/bright red or cold to touch.\"}', NULL, 1, '2022-06-30 05:22:56', '2022-06-30 05:22:56'),
(2, 'Contrast bath', 'adjunct', '{\"0\":\"    ( )   ( )            \",\"1\":\"            \"}', '{\"0\":\"Insert the affected part in lukewarm (4 mins) and cold water (1 min) alternatively for specified time. \",\"1\":\"Start the procedure with lukewarm water and end with cold water.\"}', NULL, 1, '2022-06-30 05:24:33', '2022-06-30 05:24:33'),
(3, 'Crutches', 'adjunct', '{\"0\":\"                          \",\"1\":\"          \"}', '{\"0\":\"Use Crutches for support and move around with or without weight bearing on the affected part to a percentage as taught by the therapist.\",\"1\":\"Adjust the height of the crutches as specified by your therapist.\"}', NULL, 1, '2022-06-30 05:32:00', '2022-06-30 05:32:00'),
(4, 'Elevation', 'adjunct', '{\"0\":\"  /            \",\"1\":\"         \"}', '{\"0\":\"Keep the affected part elevated while lying down above the heart level by supporting it on pillows/wedge.\",\"1\":\"Do not keep the part hanging unsupported while sitting.\"}', NULL, 1, '2022-06-30 05:33:53', '2022-06-30 05:33:53'),
(5, 'Icing', 'adjunct', '{\"0\":\"       /    \",\"1\":\"  /        \"}', '{\"0\":\"Apply ice over the affected area. Keep moving the ice pack/bag.\",\"1\":\"Stop for some time if it starts burning/excessive redness.\"}', NULL, 1, '2022-06-30 05:36:05', '2022-06-30 05:36:05'),
(6, 'Heat Therapy', 'adjunct', '{\"0\":\"      /         \",\"1\":\"   /     \",\"2\":\"  /        \"}', '{\"0\":\"Apply a hot water bag/heating pad over the affected area. Keep giving yourself small breaks.\",\"1\":\"Do not lie over the hot water bag/heating pad.\",\"2\":\"Stop for some time if it starts burning/excessive redness.\"}', NULL, 1, '2022-06-30 05:38:31', '2022-06-30 05:38:31'),
(7, 'Jogging', 'adjunct', '{\"0\":\"      \",\"1\":\"       -  - \",\"2\":\"   /    \",\"3\":\"    -  \"}', '{\"0\":\"Go for light jogging.\",\"1\":\"Perform adequate warm-up and cool-down before and after the activity.\",\"2\":\" Ideally jog on a walking/jogging track.\",\"3\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 05:43:00', '2022-06-30 05:43:00'),
(8, 'Massage', 'adjunct', '{\"0\":\"/        \",\"1\":\"     /  /  *      *\",\"2\":\"        \"}', '{\"0\":\"Massage the affected area with hands/massage stick.\",\"1\":\"Apply oil/pain reliever cream/lotion to reduce friction. *Do not massage over bony area*.\",\"2\":\"Massage only the area which is demonstrated by your therapist.\"}', NULL, 1, '2022-06-30 05:45:45', '2022-06-30 05:45:45'),
(9, 'Meditation', 'adjunct', '{\"0\":\"                    \",\"1\":\"    /           \"}', '{\"0\":\"Practice meditation. You can use specific online modules or join classes for the same.\",\"1\":\"Avoid certain positions/activities if advised by the therapist.\"}', NULL, 1, '2022-06-30 05:47:23', '2022-06-30 05:47:23'),
(10, 'Movement in water', 'adjunct', '{\"0\":\"                  \",\"1\":\"                     \"}', '{\"0\":\"Immerse the part in lukewarm water and perform movements in all safe directions and range as told by the therapist.\",\"1\":\"You can use the other limb to give comfortable overpressure at the end range.\"}', NULL, 1, '2022-06-30 05:49:20', '2022-06-30 05:49:20'),
(11, 'Skipping', 'adjunct', '{\"0\":\"           -  - \",\"1\":\"    -  \"}', '{\"0\":\"Perform skipping activity. Have adequate warm-up and cool-down before and after the activity.\",\"1\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 05:50:15', '2022-06-30 05:50:15'),
(12, 'Cycling', 'adjunct', '{\"0\":\"                 \",\"1\":\"       -  - \",\"2\":\"        ,              \",\"3\":\"    -  \"}', '{\"0\":\"Adjust bicycle height and specifications according to therapists advice and cycle.\",\"1\":\"Perform adequate warm-up and cool-down prior to and after the activity.\",\"2\":\". Unless specified by the therapist, use only stationary exercise bike.\",\"3\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 05:57:55', '2022-06-30 05:57:55'),
(13, 'Walking Stick', 'adjunct', '{\"0\":\"               \",\"1\":\"           \",\"2\":\"         \"}', '{\"0\":\"Use a walking stick prescribed by your therapist as a support to walk around.\",\"1\":\"Adjust the height of the stick as specified by your therapist.\",\"2\":\"Hold the stick on the side suggested by your therapist.\"}', NULL, 1, '2022-06-30 06:00:55', '2022-06-30 06:00:55'),
(14, 'Swimming', 'adjunct', '{\"0\":\"     \",\"1\":\"       \",\"2\":\"    -  \"}', '{\"0\":\"Go for swimming as an aerobic activity.\",\"1\":\"Perform only the strokes advised by the therapist.\",\"2\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 06:01:58', '2022-06-30 06:01:58'),
(15, 'Walker', 'adjunct', '{\"0\":\"                          \",\"1\":\"          \"}', '{\"0\":\"Use a walker prescribed by your therapist to move around without or with weight bearing to a percentage on the affected area as taught by the therapist.\",\"1\":\"Adjust the height as specified by your therapist.\"}', NULL, 1, '2022-06-30 06:03:28', '2022-06-30 06:03:28'),
(16, 'Walking', 'adjunct', '{\"0\":\"        -      - \",\"1\":\"   /   \",\"2\":\"    -  \"}', '{\"0\":\"Go for a walk. Have adequate warm-up prior to and cool-down after the activity.\",\"1\":\"Ideally walk on a walking/jogging track.\",\"2\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 06:04:46', '2022-06-30 06:04:46'),
(17, 'Walking in water', 'adjunct', '{\"0\":\"      /       \",\"1\":\"       \",\"2\":\"    -  \"}', '{\"0\":\"Walk inside waist/chest deep water as per your therapists advice.\",\"1\":\"Walk in the directions suggested by your therapist.\",\"2\":\"Take small breaks if you feel tired.\"}', NULL, 1, '2022-06-30 06:05:32', '2022-06-30 06:05:32'),
(18, 'Bracing', 'adjunct', '{\"0\":\"            \",\"1\":\"                             \",\"2\":\"               \"}', '{\"0\":\"Keep the part supported with a brace. Wear and remove the brace as per direction from the therapist.\",\"1\":\"Do not keep the brace dirty. Apply sufficient powder to the part where it is safe to, to avoid any harmful skin reactions.\",\"2\":\"Take care that there is no friction between any stitches or wounds and the brace.\"}', NULL, 1, '2022-06-30 12:54:58', '2022-06-30 12:54:58'),
(19, 'Scar tissue mobilization', 'adjunct', '{\"0\":\"        /           \",\"1\":\"  ,                      \",\"2\":\"             /      \",\"3\":\"  /            \"}', '{\"0\":\"Perform light massage over or around the scar tissue using your fingers whichever is mentioned by your therapist.\",\"1\":\"Perform only to a depth, direction and intensity as taught and, in a manner, as advised by the therapist.\",\"2\":\"If allowed by the therapist perform light stretches over the scar tissue using your fingers.\",\"3\":\"Please report to your therapist/surgeon if you find any opening over the scar area.\"}', NULL, 1, '2022-07-01 05:01:23', '2022-07-01 05:01:23'),
(20, 'Adductor Copenhagen Level 1', 'exercise', '{\"0\":\"         ,    \",\"1\":\"           \",\"2\":\"          \"}', '{\"0\":\"Lie down on the exercising side.\",\"1\":\"Bend the top leg from hip and knee and keep it near your stomach.\",\"2\":\"Now lift your lower leg towards the ceiling keeping the knee straight.\"}', NULL, 1, '2022-07-01 05:29:51', '2022-07-01 05:29:51'),
(21, 'Adductor Copenhagen Level 1A', 'exercise', '{\"0\":\"         ,    \",\"1\":\"      \",\"2\":\"               \"}', '{\"0\":\"Lie down on the exercising side.\",\"1\":\"Keep your top leg on a chair supporting till just below your knee.\",\"2\":\"Lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\"}', NULL, 1, '2022-07-01 05:31:43', '2022-07-01 05:31:43'),
(22, 'Adductor Copenhagen Level 2', 'exercise', '{\"0\":\"         ,           \",\"1\":\"                 ,                  \",\"2\":\"                              \"}', '{\"0\":\"Lie down on the exercising side. Keep your top leg on a chair supporting your ankle and shin bone in line with your body.\",\"1\":\"Lift up your body sideways by supporting it on your exercising side forearm (in line with your shoulder), non-exercising leg on the chair and exercising leg below the chair (side plank).\",\"2\":\"Transfer the weight to forearm and non-exercising leg and lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\"}', NULL, 1, '2022-07-01 05:34:21', '2022-07-01 05:34:21'),
(23, 'Adductor Squeeze', 'exercise', '{\"0\":\"        \",\"1\":\"    /  /   \",\"2\":\" /  /       \"}', '{\"0\":\"Lie down on your back keeping both your knees bent.\",\"1\":\"Place a pillow/roller/football between your knees.\",\"2\":\"Press and hold both your knees on to the pillow/roller/football.\"}', NULL, 1, '2022-07-01 05:38:57', '2022-07-01 05:38:57'),
(24, 'Adductor Squeeze with knee to chest', 'exercise', '{\"0\":\"         \",\"1\":\"    /  /   \",\"2\":\" /  /    ,        \"}', '{\"0\":\"Lie down on your back keeping both your knees bent.\",\"1\":\"Place a pillow/roller/football between your knees.\",\"2\":\"Press both your knees on to the pillow/roller/football and try to touch your knees to chest.\"}', NULL, 1, '2022-07-01 05:46:44', '2022-07-01 05:46:44'),
(25, 'Air Cyling', 'exercise', '{\"0\":\"           \",\"1\":\"           \",\"2\":\"              \"}', '{\"0\":\"Lie down on your back. Keep both arms by the side.\",\"1\":\"Lift both your legs up with your knees bent.\",\"2\":\"Imagine you are sitting on a cycle and move your legs in a circular motion as if pedaling.\",\"3\":\"To increase the difficulty, raise both arms up straight towards the sky and hold the position while pedaling.\"}', NULL, 1, '2022-07-01 05:50:39', '2022-07-01 05:50:39'),
(26, 'Alternating Shoulder taps', 'exercise', '{\"0\":\"                 \",\"1\":\"                                     \",\"2\":\"               \",\"3\":\"                  \"}', '{\"0\":\"Lie down on your stomach. Supporting your body on your hands and toes rise up.\",\"1\":\"Keep your hands two finger distance inwards from shoulder width and at chest level. Feet should be hip width apart and in line with shoulder. Keep a neutral lower back.\",\"2\":\"Tuck your tummy in by pulling your belly button in and upwards to tighten your core.\",\"3\":\"Lift one hand off the ground to touch the opposite shoulder. Perform the movement alternating between both the sides.\"}', NULL, 1, '2022-07-01 05:56:39', '2022-07-01 05:56:39'),
(27, 'Ankle Dorsiflexors strengthening', 'exercise', '{\"0\":\"         \",\"1\":\"                \",\"2\":\"                       \",\"3\":\"        \"}', '{\"0\":\"Lie down or be in a long sitting position.\",\"1\":\"Place a band just below your toes and tie/ ask someone to hold it in line with your leg away from the body.\",\"2\":\"Pull your ankle towards your body against the resistance.\"}', NULL, 1, '2022-07-01 06:00:48', '2022-07-01 06:00:48'),
(28, 'Ankle Dorsiflexion Mobilization', 'exercise', '{\"0\":\"                  \",\"1\":\"                     \"}', '{\"0\":\"Come in a kneeling position.\",\"1\":\"Keep the exercising leg ahead.\",\"2\":\"Lunge ahead reaching forward with your knee keeping the heel in contact with the ground.\"}', NULL, 1, '2022-07-01 06:02:35', '2022-07-01 06:02:35'),
(29, 'Ankle Dorsiflexion 3-way mobilization', 'exercise', '{\"0\":\"     (   ) \",\"1\":\"            \",\"2\":\"                 i)    ii)    iii)    \"}', '{\"0\":\"Come in a kneeling position.\",\"1\":\"Keep the exercising leg ahead.\",\"2\":\"Lunge ahead reaching i) forward ii) diagonally outwards iii) diagonally inwards with your knee, keeping the heel in contact with the ground.\"}', NULL, 1, '2022-07-01 06:06:45', '2022-07-01 06:06:45'),
(30, 'Ankle evertors strengthening', 'exercise', '{\"0\":\"        \",\"1\":\"                                             \",\"2\":\"              \"}', '{\"0\":\"Lie down or be in a long sitting position.\",\"1\":\"Place a band just below your toes and tie it around your other foot / ask someone to hold it. Keep it in direction of your other foot in line with your feet and parallel to the ground.\",\"2\":\"Move your ankle away from the other foot against resistance.\"}', NULL, 1, '2022-07-01 06:13:45', '2022-07-01 06:13:45'),
(31, 'Ankle invertors strengthening', 'exercise', '{\"0\":\"        \",\"1\":\"                                                          \",\"2\":\"             \"}', '{\"0\":\"Lie down or be in a long sitting position with one hand on the knee.\",\"1\":\"Place a band just below your toes and tie it around your other foot keeping your feet crossed with one thigh on top of the other / ask someone to hold it in opposite direction of your other foot in line with your feet and parallel to the ground.\",\"2\":\"Move your ankle towards the great toe against resistance.\"}', NULL, 1, '2022-07-01 06:16:48', '2022-07-01 06:16:48'),
(32, 'Ankle plantarflexors open chain strengthening', 'exercise', '{\"0\":\"        \",\"1\":\"                          \",\"2\":\"           \"}', '{\"0\":\"Lie down or be in a long sitting position.\",\"1\":\"Place a band just below your toes and hold it in line with your leg towards your waist.\",\"2\":\"Push your foot away from your body against the resistance.\"}', NULL, 1, '2022-07-01 06:19:29', '2022-07-01 06:19:29'),
(33, 'Ankle pumps', 'exercise', '{\"0\":\"  \",\"1\":\"                    \",\"2\":\"         \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Pull your ankles towards your body. Next push them away from the body squeezing your calves.\",\"2\":\"Alternate these movements.\"}', NULL, 1, '2022-07-01 06:24:26', '2022-07-01 06:24:26'),
(34, 'Ankle pumps elevated', 'exercise', '{\"0\":\"  \",\"1\":\"               \",\"2\":\"                    \",\"3\":\"         \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Keep your targeted leg elevated with support of pillows/wedge.\",\"2\":\"Pull your ankles towards your body. Next push them away from the body squeezing your calves.\",\"3\":\"Alternate between these movements.\"}', NULL, 1, '2022-07-01 06:27:06', '2022-07-01 06:27:06'),
(35, 'Anterior step down', 'exercise', '{\"0\":\"     ,        (   /  )  \",\"1\":\"     (       )                          \"}', '{\"0\":\"Keeping hands on your waist, stand on a raised platform (preferably in a corner/near wall).\",\"1\":\"Without dropping your waist (maintaining both the hands at same level) touch the ground with the heel of non-exercising leg in the front and come back up.\"}', NULL, 1, '2022-07-01 06:28:21', '2022-07-01 06:28:21'),
(36, 'Adductor Copenhagen- Level 3', 'exercise', '{\"0\":\"         ,           \",\"1\":\"                 ,                  \",\"2\":\"                              \"}', '{\"0\":\"Lie down on the exercising side. Keep your top leg on a chair supporting only your ankle in line with your body.\",\"1\":\"Lift up your body sideways by supporting it on your exercising side forearm (in line with your shoulder), non-exercising leg on the chair and exercising leg below the chair (side plank).\",\"2\":\"Transfer the weight to forearm and non-exercising leg and lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\"}', NULL, 1, '2022-07-02 07:24:23', '2022-07-02 07:24:23'),
(37, 'Adductor Copenhagen with knee drive', 'exercise', '{\"0\":\"         ,           \",\"1\":\"                 ,                  \",\"2\":\"                              \",\"3\":\"                  \"}', '{\"0\":\"Lie down on the exercising side. Keep your top leg on a chair supporting only your ankle in line with your body.\",\"1\":\"Lift up your body sideways by supporting it on your exercising side forearm (in line with your shoulder), non-exercising leg on the chair and exercising leg below the chair (side plank).\",\"2\":\"Transfer the weight to forearm and non-exercising leg and lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\",\"3\":\"Hold this position and bend the bottom leg trying to bring the knee towards the chest.\"}', NULL, 1, '2022-07-02 07:25:37', '2022-07-02 07:25:37'),
(38, 'Low back extension- Standing', 'exercise', '{\"0\":\"  ,              \",\"1\":\"               \"}', '{\"0\":\"Stand tall with both your hands on the waist and your feet hip width apart.\",\"1\":\"Bend your back behind keeping the knees straight till a comfortable position and in a position where you can keep your balance.\"}', NULL, 1, '2022-07-02 08:33:49', '2022-07-02 08:33:49'),
(39, 'Low back forward flexion- Standing', 'exercise', '{\"0\":\"  ,              \",\"1\":\"               \"}', '{\"0\":\"Stand tall with both your hands on the waist and your feet hip width apart.\",\"1\":\"Bend forward and down from the back keeping the knees straight till a comfortable position and in a position where you can keep your balance.\"}', NULL, 1, '2022-07-02 08:36:14', '2022-07-02 08:36:14'),
(40, 'Balance Training- Feet together', 'exercise', '{\"0\":\"                        \",\"1\":\"                \",\"2\":\"         \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your both feet as close to each other as possible pointing forwards.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:39:57', '2022-07-02 08:39:57'),
(41, 'Balance Training- Feet together (Eyes closed)', 'exercise', '{\"0\":\"                        \",\"1\":\"                \",\"2\":\"              \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your both feet as close to each other as possible pointing forwards.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:41:56', '2022-07-02 08:41:56'),
(42, 'Balance Training- Single leg standing', 'exercise', '{\"0\":\"                        \",\"1\":\"                    \",\"2\":\"         \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot pointing forwards.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:44:07', '2022-07-02 08:44:07'),
(43, 'Balance Training- Single leg standing (Eyes closed)', 'exercise', '{\"0\":\"                        \",\"1\":\"                    \",\"2\":\"              \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot pointing forwards.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:47:28', '2022-07-02 08:47:28'),
(44, 'Balance Training- Single leg standing Knee Unlocked', 'exercise', '{\"0\":\"                        \",\"1\":\"                         \",\"2\":\"         \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot.\",\"2\":\"Keep the knee slightly bent. Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:51:35', '2022-07-02 08:51:35'),
(45, 'Balance Training- Single leg Standing Knee Unlocked (Eyes closed)', 'exercise', '{\"0\":\"                        \",\"1\":\"                         \",\"2\":\"              \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot.\",\"2\":\"Keep the knee slightly bent. Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:52:55', '2022-07-02 08:52:55'),
(46, 'Balance Training- Tandem standing', 'exercise', '{\"0\":\"                        \",\"1\":\"                 \",\"2\":\"         \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your feet in line (one foot ahead of the other in a straight line) pointing forwards.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:56:00', '2022-07-02 08:56:00'),
(47, 'Balance Training- Tandem standing (Eyes closed)', 'exercise', '{\"0\":\"                        \",\"1\":\"                 \",\"2\":\"              \"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your feet in line (one foot ahead of the other in a straight line) pointing forwards.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:57:41', '2022-07-02 08:57:41'),
(48, 'Balance Training- Unstable surface Feet together', 'exercise', '{\"0\":\"                               \",\"1\":\"                \",\"2\":\"         \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your feet as close to each other as possible.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 09:03:00', '2022-07-02 09:03:00'),
(49, 'Balance Training- Unstable surface', 'exercise', '{\"0\":\"                               \",\"1\":\"                \",\"2\":\"         \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your feet as close to each other as possible.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 2, '2022-07-02 09:03:08', '2022-07-02 09:03:08'),
(50, 'Balance Training- Unstable surface  Feet together(Eyes closed)', 'exercise', '{\"0\":\"                               \",\"1\":\"                \",\"2\":\"              \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your feet as close to each other as possible.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 09:04:34', '2022-07-02 09:04:34'),
(51, 'Balance Training- SIngle leg Unstable surface Knee unlocked', 'exercise', '{\"0\":\"                               \",\"1\":\"                         \",\"2\":\"         \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot. Keep your knee slightly bent.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 09:47:35', '2022-07-02 09:47:35'),
(52, 'Balance Training- Single leg Unstable surface Knee unlocked (Eyes closed)', 'exercise', '{\"0\":\"                               \",\"1\":\"                         \",\"2\":\"              \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot. Keep your knee slightly bent.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 09:49:43', '2022-07-02 09:49:43'),
(53, 'Bear Plank', 'exercise', '{\"0\":\"          \",\"1\":\"          \",\"2\":\"      (      )         ,             \"}', '{\"0\":\"Come on all fours/crawling position.\",\"1\":\"Keep your shoulders on top of your palms and knees in line with the hip.\",\"2\":\"Maintaining a neutral lower-back (do not arch/hunch) lift your knees off the ground, supporting body weight on your hands and toes.\"}', NULL, 1, '2022-07-02 09:51:56', '2022-07-02 09:51:56'),
(54, 'Bent over Rows', 'exercise', '{\"0\":\"        /              \",\"1\":\"                           \",\"2\":\"                           \"}', '{\"0\":\"Stand with dumbells in both hands/ hold band in both hands passing under both feet.\",\"1\":\"Bend the knees, hinging forward from the hip and keep your back straight. Keep your elbows bent and in contact with your waist.\",\"2\":\"Pinch your shoulder blades together and pull behind the elbows while still maintaining contact between forearms and waist.\"}', NULL, 1, '2022-07-02 09:53:49', '2022-07-02 09:53:49'),
(55, 'Biceps Eccentric strengthening', 'exercise', '{\"0\":\"  /                \",\"1\":\"                                   \",\"2\":\"                  \"}', '{\"0\":\"Stand /sit and bend your elbow all the way with your palm facing up.\",\"1\":\"Transfer a weight from your non-exercising arm to the bent exercising arm and lower the weight down in a slow controlled manner.\",\"2\":\"Once the elbow is straight, take the weight back in non-exercising arm and return to starting position.\"}', NULL, 1, '2022-07-02 09:56:46', '2022-07-02 09:56:46'),
(56, 'Bicycle crunch', 'exercise', '{\"0\":\"              \",\"1\":\"           \",\"2\":\"                \"}', '{\"0\":\"Lie down on your back with keeping both your hands behind the head.\",\"1\":\"Lift both feet off the ground with knees straight.\",\"2\":\"Touch your alternate elbow and knee to each other in a slow controlled fashion.\"}', NULL, 1, '2022-07-02 09:58:14', '2022-07-02 09:58:14'),
(57, 'Bird Dog- Bilateral', 'exercise', '{\"0\":\"                     \",\"1\":\"                          \",\"2\":\"       \"}', '{\"0\":\"Come in a crawling position with your hands right below your shoulders and knees below your hips.\",\"1\":\"Pull your belly button inwards and upwards to brace your core and lift one hand simultaneously with the opposite leg.\",\"2\":\"Relax and perform on the other side.\"}', NULL, 1, '2022-07-02 09:59:47', '2022-07-02 09:59:47'),
(58, 'Bruegger exercise', 'exercise', '{\"0\":\"        (-   ) /          ,   ,               \",\"1\":\"           \",\"2\":\"             \",\"3\":\"             \",\"4\":\"          \"}', '{\"0\":\"Stand tall with/without a band in both of your hands (length- palm to shoulder). Take starting position of arms by your side, elbows bent and both wrists curled inwards and touching your chest.\",\"1\":\"Open your hands and curl out your wrist\",\"2\":\"straighten your elbows lifting your arms in front straight at eye level.\",\"3\":\"Take your arms away from each other sideways to open up your chest.\",\"4\":\"Come back to starting position reversing the steps.\"}', NULL, 1, '2022-07-02 10:03:07', '2022-07-02 10:03:07'),
(59, 'Balance Training- Unstable surface Single leg ', 'exercise', '{\"0\":\"                               \",\"1\":\"                    \",\"2\":\"         \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-03 10:58:18', '2022-07-03 10:58:18'),
(60, 'Balance Training- Unstable surface Single leg ', 'exercise', '{\"0\":\"                               \",\"1\":\"                    \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 2, '2022-07-03 10:58:32', '2022-07-03 10:58:32'),
(61, 'Balance Training- Unstable surface Single leg (Eyes closed)', 'exercise', '{\"0\":\"                               \",\"1\":\"                    \",\"2\":\"              \"}', '{\"0\":\"Stand tall on some unstable surface (pillow/balance board) near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-03 11:02:11', '2022-07-03 11:02:11'),
(62, 'Bird dog- Unilateral', 'exercise', '{\"0\":\"                     \",\"1\":\"                          \",\"2\":\"                       \"}', '{\"0\":\"Come in a crawling position with your hands right below your shoulders and knees below your hips.\",\"1\":\"Pull your belly button inwards and upwards to brace your core and lift one hand simultaneously with the opposite leg.\",\"2\":\"Touch the elbow and knee together alternating with hand and leg lifts.\"}', NULL, 1, '2022-07-04 10:10:36', '2022-07-04 10:10:36'),
(63, 'Bridging', 'exercise', '{\"0\":\"        \",\"1\":\"                \",\"2\":\"                   -    \"}', '{\"0\":\"- Lie down on your back with both knees bent.\",\"1\":\"Squeeze your buttocks and pull the belly button in and upwards tightening your core.\",\"2\":\"Lift off your buttocks towards the ceiling. Slowly lower the buttocks down to take them up again without touching the bed.\"}', NULL, 1, '2022-07-04 10:21:05', '2022-07-04 10:21:05'),
(64, 'Bridging- Unilateral', 'exercise', '{\"0\":\"        \",\"1\":\"                \",\"2\":\"                            \",\"3\":\"            -    \"}', '{\"0\":\"Lie down on your back with both knees bent.\",\"1\":\"Squeeze your buttocks and pull the belly button in and upwards tightening your core.\",\"2\":\"Keep the non-exercising side leg raised with knee straight till both thighs are aligned in line. Lift off your buttocks towards the ceiling.\",\"3\":\"Slowly lower the buttocks down to take them up again without touching the bed and losing the core contraction.\"}', NULL, 1, '2022-07-04 10:24:37', '2022-07-04 10:24:37'),
(65, 'Bridging with abduction', 'exercise', '{\"0\":\"               /  \",\"1\":\"        ,                        \",\"2\":\"                    -    \"}', '{\"0\":\"Lie down on your back with both knees bent. Place a resistance band/loop band around your knees.\",\"1\":\"Slightly pull apart your knees against resistance, squeeze your buttocks and pull the belly button in and upwards tightening your core. Lift off your buttocks towards the ceiling.\",\"2\":\"Slowly lower the buttocks down to take them up again without touching the bed without losing the core contraction and maintaining pressure on the band.\"}', NULL, 1, '2022-07-04 10:27:40', '2022-07-04 10:27:40'),
(66, 'Bridging with adductor squeeze', 'exercise', '{\"0\":\"              / \",\"1\":\"/                               \",\"2\":\"     /              -    \"}', '{\"0\":\"Lie down on your back with both knees bent. Keep a pillow/roller between your knees.\",\"1\":\"Squeeze your buttocks, squeeze the pillow/roller between your knees and pull the belly button in and upwards tightening your core. Lift off your buttocks towards the ceiling.\",\"2\":\"Slowly lower the buttocks down to take them up again without touching the bed without losing the core contraction & pressure on pillow/roller.\"}', NULL, 1, '2022-07-04 10:31:05', '2022-07-04 10:31:05'),
(67, 'Butt kicks', 'exercise', '{\"0\":\"   \",\"1\":\"                 \",\"2\":\"        \"}', '{\"0\":\"Stand straight.\",\"1\":\"Kick your knee behind trying to touch your ball of foot to your buttocks.\",\"2\":\"Alternate between both the legs.\"}', NULL, 1, '2022-07-04 10:37:04', '2022-07-04 10:37:04'),
(68, 'Buttock walk', 'exercise', '{\"0\":\"                \",\"1\":\"                      \",\"2\":\"           \",\"3\":\"      \"}', '{\"0\":\"Be in a long sitting position with both arms raised and held straight at shoulder level.\",\"1\":\"Lift up your leg and buttock by bending the trunk sideways to keep that leg ahead.\",\"2\":\"Alternate between both sides to walk ahead on your buttocks.\",\"3\":\"Come back in a similar manner.\"}', NULL, 1, '2022-07-04 10:39:54', '2022-07-04 10:39:54'),
(69, 'Calf eccentric strengthening', 'exercise', '{\"0\":\"                   \",\"1\":\"                                 \",\"2\":\"                 -            \"}', '{\"0\":\"Stand against a wall taking support for balance. Keep your feet below your hips.\",\"1\":\"Lift your heels upwards so that entire body moves up in a straight direction and not forwards. Squeeze and tighten the calves at the highest point.\",\"2\":\"Raise & bend the non-exercising leg from knee and slowly lower the body in a controlled manner.\"}', NULL, 1, '2022-07-05 09:24:53', '2022-07-05 09:24:53'),
(70, 'Calf Lateral Raises', 'exercise', '{\"0\":\"                           \",\"1\":\"       \",\"2\":\"                   \"}', '{\"0\":\"Stand with your exercising side facing the wall. Slightly lean on the wall and take support of the wall with same side arm.\",\"1\":\"Raise the opposite leg bending it from the knee.\",\"2\":\"Squeeze your calf lifting your heel up to raise your body upwards and towards the wall.\"}', NULL, 1, '2022-07-05 09:27:25', '2022-07-05 09:27:25'),
(71, 'Calf raises- Bilateral', 'exercise', '{\"0\":\"                  \",\"1\":\"                      \",\"2\":\"          \"}', '{\"0\":\"Stand against a wall taking support for balance. Keep your feet below your hips.\",\"1\":\"Lift your heels upwards so that entire body moves up in a straight direction and not forwards.\",\"2\":\"Squeeze and tighten the calves at the highest point.\"}', NULL, 1, '2022-07-05 09:29:45', '2022-07-05 09:29:45');
INSERT INTO `mst_instructions` (`instruction_id`, `instruction_name`, `instruction_type`, `instruction_description_hindi`, `instruction_description_english`, `instruction_description_marathi`, `isActive`, `created_at`, `updated_at`) VALUES
(72, 'Calf Raises- Unilateral', 'exercise', '{\"0\":\"                           \",\"1\":\"                      \",\"2\":\"          \"}', '{\"0\":\"Stand against a wall taking support for balance. Lift the opposite leg bending it from the knee and stand on your exercising leg.\",\"1\":\"Lift your heel upwards so that entire body moves up in a straight direction and not forwards.\",\"2\":\"Squeeze and tighten the calf at the highest point.\"}', NULL, 1, '2022-07-05 09:31:56', '2022-07-05 09:31:56'),
(73, 'Calf Raises- Triplanar', 'exercise', '{\"0\":\"                  \",\"1\":\"                      \",\"2\":\"          \",\"3\":\" 3    1)      2)        3)       \"}', '{\"0\":\"Stand against a wall taking support for balance. Keep your feet below your hips.\",\"1\":\"Lift your heel upwards so that entire body moves up in a straight direction and not forwards.\",\"2\":\"Squeeze and tighten the calves at the highest point.\",\"3\":\"Do the exercise 3 ways 1) Toes pointing straight 2) Toes pointing inwards 3) Toes pointing outwards.\"}', NULL, 1, '2022-07-05 09:34:47', '2022-07-05 09:34:47'),
(74, 'Calf raises- Bilateral Bent knee', 'exercise', '{\"0\":\"                         \",\"1\":\"                      \",\"2\":\"          \"}', '{\"0\":\"Stand against a wall taking support for balance. Keep your feet below your hips and knees slightly bent.\",\"1\":\"Lift your heels upwards so that entire body moves up in a straight direction and not forwards.\",\"2\":\"Squeeze and tighten the calves at the highest point.\"}', NULL, 1, '2022-07-05 09:36:59', '2022-07-05 09:36:59'),
(75, 'Calf Raises- Unilateral Bent knee', 'exercise', '{\"0\":\"                         \",\"1\":\"                \",\"2\":\"                                 \"}', '{\"0\":\"Stand against a wall taking support for balance. Keep your feet below your hips and knees slightly bent.\",\"1\":\"Lift the opposite leg bending it from the knee and stand on your exercising leg.\",\"2\":\"Lift your heel upward so that entire body moves up in a straight direction and not forwards. Squeeze and tighten the calf at the highest point.\"}', NULL, 1, '2022-07-05 09:38:50', '2022-07-05 09:38:50'),
(76, 'Calf Raises- Split Stance', 'exercise', '{\"0\":\"     (          ),          \",\"1\":\"                    \"}', '{\"0\":\"Stand with your legs apart (exercising leg in front & the other behind), knees bent and back straight.\",\"1\":\"Squeeze your calf muscle of the front leg and lift up the heel keeping your balance.\"}', NULL, 1, '2022-07-05 09:40:23', '2022-07-05 09:40:23'),
(77, 'Calf Raises- Seated', 'exercise', '{\"0\":\"   ,              \"}', '{\"0\":\"Sitting on a chair, keep a weight on your thighs and lift your heels up against resistance.\"}', NULL, 1, '2022-07-05 09:41:08', '2022-07-05 09:41:08'),
(78, 'Calf stretch', 'exercise', '{\"0\":\"                             \",\"1\":\"         \",\"2\":\"      ,          \"}', '{\"0\":\"Stand facing the wall. Keep the leg of which the calf you want to stretch behind with the knee straight.\",\"1\":\"Keep the other leg in a lunge position near the wall.\",\"2\":\"Without lifting heel of back leg, shift your weight ahead and reach forwards as much as you can. \"}', NULL, 1, '2022-07-05 09:42:57', '2022-07-05 09:42:57'),
(79, 'Calf stretch- Triplanar', 'exercise', '{\"0\":\"                             \",\"1\":\"         \",\"2\":\"      ,          \",\"3\":\"       )        )          )          \"}', '{\"0\":\"Stand facing the wall. Keep the leg of which the calf you want to stretch behind with the knee straight.\",\"1\":\"Keep the other leg in a lunge position near the wall.\",\"2\":\"Without lifting heel of back leg, shift your weight ahead and reach forwards as much as you can. \",\"3\":\"Do the stretch 3 ways 1) Leg behind in a straight line 2) Leg behind and away from the center of body 3) Leg behind and crossing the other leg.\"}', NULL, 1, '2022-07-05 09:43:52', '2022-07-05 09:43:52'),
(80, 'Calf stretch- Bent knee', 'exercise', '{\"0\":\"                               \",\"1\":\"         \",\"2\":\"      ,          \"}', '{\"0\":\"Stand facing the wall. Keep the leg of which the calf you want to stretch behind with a slightly bent knee.\",\"1\":\"Keep the other leg in a lunge position near the wall.\",\"2\":\"Without lifting heel of back leg, shift your weight ahead and reach forwards as much as you can.\"}', NULL, 1, '2022-07-05 09:45:44', '2022-07-05 09:45:44'),
(81, 'Cat camel exercise', 'exercise', '{\"0\":\"     (        ) \",\"1\":\"             \",\"2\":\"                   \",\"3\":\"                                          \"}', '{\"0\":\"Come in position on all fours (body supported on hands and knees).\",\"1\":\"Keep your hands under your shoulder and knees under the hip. \",\"2\":\"Curl in your chest and stomach to hunch the back as high as you can. \",\"3\":\"Afterwards arch your back pushing your stomach and chest towards the floor as low as you can.\"}', NULL, 1, '2022-07-05 09:47:27', '2022-07-05 09:47:27'),
(82, 'Cervical Isometrics', 'exercise', '{\"0\":\"        \",\"1\":\"i)                       \",\"2\":\"ii)   \",\"3\":\"iii)                  \",\"4\":\"iv)                   \"}', '{\"0\":\"Sit straight with your back and feet supported.\",\"1\":\"i-Keep your hand against your head above your ear. Press your head against your hand and hold the position.\",\"2\":\"ii) Repeat on other side.\",\"3\":\"iii) Keep your hands behind your head and press your head against them. Hold the position.\",\"4\":\"iv) Keep your hands on your forehead and press your head against your hands. Hold the position.\"}', NULL, 1, '2022-07-05 09:51:13', '2022-07-05 09:51:13'),
(83, 'Cervical ROM with overpressure', 'exercise', '{\"0\":\"        \",\"1\":\"i)                    \",\"2\":\"ii)     \",\"3\":\"iii)                            \",\"4\":\"iv)     \",\"5\":\"v)                     \",\"6\":\"vi)                    \"}', '{\"0\":\"Sit straight with your back and feet supported.\",\"1\":\"i) Turn your head sideways & at the end range apply slight overpressure with your opposite hand over the jaw.\",\"2\":\"ii) Repeat the same on other side.\",\"3\":\"iii) Bend the head sideways so that your ear touches the same side shoulder. At the end range apply slight overpressure with same side hand over the head. \",\"4\":\"iv) Repeat the same on other side.\",\"5\":\"v) Bend your neck behind to look upwards as high as you can. Do not give overpressure to this movement.\",\"6\":\"vi) Bend your neck forwards to look down. Apply slight overpressure with either of the hand.\"}', NULL, 1, '2022-07-05 09:55:58', '2022-07-05 09:55:58'),
(84, 'Chest ups', 'exercise', '{\"0\":\"              \",\"1\":\"                      (    ) \",\"2\":\"                            (    ) \"}', '{\"0\":\"Stand straight with both arms raised sideways to shoulder level.\",\"1\":\"Curl your chest inwards hunching the upper back while rotating the arms inwards (palms face down).\",\"2\":\"Afterwards arch your upper back and push your chest out and up towards the ceiling while rotating the arms outwards (palms face up).\"}', NULL, 1, '2022-07-05 09:58:06', '2022-07-05 09:58:06'),
(85, 'Chin tuck- Quadruped', 'exercise', '{\"0\":\"     (        ) \",\"1\":\"             \",\"2\":\"                  (  )  \"}', '{\"0\":\"Come on all fours (crawling position).\",\"1\":\"Keep Your hands below shoulder and knees below the hip.\",\"2\":\"Keep your head aligned with your back and tuck your chin inwards (towards the ceiling).\"}', NULL, 1, '2022-07-05 09:59:48', '2022-07-05 09:59:48'),
(86, 'Chin tucks- Quadruped resisted', 'exercise', '{\"0\":\"     (        ) \",\"1\":\"             \",\"2\":\"                \",\"3\":\"                     (  )  \"}', '{\"0\":\"Come on all fours (crawling position).\",\"1\":\"Keep Your hands below shoulder and knees below the hip.\",\"2\":\"Place a resistance band behind your head and hold both the ends under your hands.\",\"3\":\"Keep your head aligned with your back and tuck your chin inwards (towards the ceiling) against resistance.\"}', NULL, 1, '2022-07-05 10:03:27', '2022-07-05 10:03:27'),
(87, 'Chin tucks- Quadruped with arm lifts', 'exercise', '{\"0\":\"     (        ) \",\"1\":\"             \",\"2\":\"                  (  )  \",\"3\":\"             \"}', '{\"0\":\"Come on all fours (crawling position).\",\"1\":\"Keep Your hands below shoulder and knees below the hip.\",\"2\":\"Keep your head aligned with your back and tuck your chin inwards (towards the ceiling).\",\"3\":\"Maintaining this position lift your alternate hands straight forwards.\"}', NULL, 1, '2022-07-05 10:05:13', '2022-07-05 10:05:13'),
(88, 'Chin Tucks- Supine', 'exercise', '{\"0\":\"             \",\"1\":\"                        \"}', '{\"0\":\"Lie down on your back without a pillow. Keep both your knees bent.\",\"1\":\"Pull your chin inwards trying to flatten your neck on the bed.\"}', NULL, 1, '2022-07-05 10:07:10', '2022-07-05 10:07:10'),
(89, 'Chin tucks with nods', 'exercise', '{\"0\":\"      /   \",\"1\":\"                      - \"}', '{\"0\":\"Sit/stand straight against a wall.\",\"1\":\"Tuck your chin inwards trying to touch your neck to the wall and perform a nodding movement with your head.\"}', NULL, 1, '2022-07-05 10:08:19', '2022-07-05 10:08:19'),
(90, 'Clamshells', 'exercise', '{\"0\":\"           \",\"1\":\"         ,         \",\"2\":\"                     \"}', '{\"0\":\"Lie down on the non-exercising side.\",\"1\":\"Keep your hip and knee bent and align your ankle hip and shoulder in a straight line.\",\"2\":\"Lift the knee of exercising side upwards apart from the other knee while keeping the ankles in contact with each other.\"}', NULL, 1, '2022-07-05 10:09:47', '2022-07-05 10:09:47'),
(91, 'Clamshell- Standing', 'exercise', '{\"0\":\"            \",\"1\":\"                           \"}', '{\"0\":\"Stand with both your legs together. Keep your hip and knee slightly bent.\",\"1\":\"Tuck in the stomach and rotate your exercising side knee outwards away from the other without moving the ankle from its position.\"}', NULL, 1, '2022-07-05 10:11:29', '2022-07-05 10:11:29'),
(92, 'Core scissor kicks', 'exercise', '{\"0\":\"                        \",\"1\":\"                     \",\"2\":\"            \",\"3\":\"                  \"}', '{\"0\":\"Lie down on your back. Keep your hand under tail bone for support and maintaining a neutral lower back.\",\"1\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"2\":\"Lift up both legs straight from the hip without bending your knees.\",\"3\":\"Take your legs apart from each other and crossing each other alternatively.\"}', NULL, 2, '2022-07-05 10:13:38', '2022-07-05 10:13:38'),
(93, 'Core scissor kicks', 'exercise', '{\"0\":\"                        \",\"1\":\"                     \",\"2\":\"            \",\"3\":\"                  \"}', '{\"0\":\"Lie down on your back. Keep your hand under tail bone for support and maintaining a neutral lower back.\",\"1\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"2\":\"Lift up both legs straight from the hip without bending your knees.\",\"3\":\"Take your legs apart from each other and crossing each other alternatively.\"}', NULL, 1, '2022-07-05 10:13:39', '2022-07-05 10:13:39'),
(94, 'Deadbugs- Bilateral', 'exercise', '{\"0\":\"            -    \",\"1\":\"         \",\"2\":\"                     \",\"3\":\"                \"}', '{\"0\":\"Lie down on your back. Keep both your knees and hips bent in a 90-90 manner.\",\"1\":\"Raise both your arms up straight towards the ceiling.\",\"2\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"3\":\"Maintain this position and alternatively bring down one arm and opposite leg.\"}', NULL, 1, '2022-07-05 10:35:47', '2022-07-05 10:35:47'),
(95, 'Deadbugs- Unilateral', 'exercise', '{\"0\":\"            -    \",\"1\":\"         \",\"2\":\"        // \",\"3\":\"                      \",\"4\":\"//                  \"}', '{\"0\":\"Lie down on your back. Keep both your knees and hips bent in a 90-90 manner.\",\"1\":\"Raise both your arms up straight towards the ceiling.\",\"2\":\"Keep a roller/bolster/pillow between the arm and knee of one side.\",\"3\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"4\":\"Keep pressing the roller/bolster/pillow on one side and open up on other side to bring down the arm and leg towards the ground.\"}', NULL, 1, '2022-07-05 10:38:09', '2022-07-05 10:38:09'),
(96, 'Dhanurasana', 'exercise', '{\"0\":\"    \",\"1\":\"            \",\"2\":\"           \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Bend both your knees and hold your ankles with your hands.\",\"2\":\"Pull your legs towards your shoulder arching your back.\"}', NULL, 1, '2022-07-05 10:39:36', '2022-07-05 10:39:36'),
(97, 'Digital rolling flexion', 'exercise', '{\"0\":\"      \",\"1\":\"                    \",\"2\":\"           \",\"3\":\" ,                   \"}', '{\"0\":\"Keep your palm and fingers straight.\",\"1\":\"Start bending your fingers as if rolling them downwards starting from the top.\",\"2\":\"Continue with bending the knuckles and your wrist inwards.\",\"3\":\"Go back to the starting position starting from straightening your wrist, knuckles and then fingers down to up.\"}', NULL, 1, '2022-07-05 10:41:29', '2022-07-05 10:41:29'),
(98, 'DLF + LATS stretch (Balasana)', 'exercise', '{\"0\":\"        (             )\",\"1\":\"        \",\"2\":\"                        \"}', '{\"0\":\"Come in a kneel sitting position (kneel and sit behind resting your buttocks on the foot)\",\"1\":\"Keep your hands in front palms face upwards.\",\"2\":\"Slide your arms forwards bending from your back and reach forward as much as you can without lifting your buttocks.\"}', NULL, 1, '2022-07-05 10:43:14', '2022-07-05 10:43:14'),
(99, 'DLF + LATS stretch (Balasana) single side biased', 'exercise', '{\"0\":\"        (             )\",\"1\":\"        \",\"2\":\"             (   ) \",\"3\":\"                        \"}', '{\"0\":\"Come in a kneel sitting position (kneel and sit behind resting your buttocks on the foot)\",\"1\":\"Keep your hands in front palms face upwards.\",\"2\":\"Shift your palms (one on top of the other) to the side opposite to the one you want to stretch.\",\"3\":\"Slide your arms forwards diagonally bending from your back and reach forward as much as you can without lifting your buttocks.\"}', NULL, 1, '2022-07-05 10:44:50', '2022-07-05 10:44:50'),
(100, 'Donkey Kicks', 'exercise', '{\"0\":\"     (        )               \",\"1\":\"                     \",\"2\":\"                    \"}', '{\"0\":\"Come in position on all fours (body supported on hands and knees). Keep your hands under your shoulder and knees under the hip.\",\"1\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"2\":\"Keeping the knee in a bent position lift your leg behind to extend your hips and take your foot towards the ceiling.\"}', NULL, 1, '2022-07-05 10:46:46', '2022-07-05 10:46:46'),
(101, 'Bruegger exercise- High shoulders', 'exercise', '{\"0\":\"        (-   ) /    \",\"1\":\"     ,   ,               \",\"2\":\"           \",\"3\":\"             \",\"4\":\"             \",\"5\":\"          \"}', '{\"0\":\"Stand tall with/without a band in both of your hands (length- palm to shoulder).\",\"1\":\"Take starting position of arms by your side, elbows bent and both wrists curled inwards and touching your chest.\",\"2\":\"Open your hands and curl out your wrist.\",\"3\":\"straighten your elbows lifting your arms in front straight at eye level.\",\"4\":\"Take your arms away from each other sideways to open up your chest.\",\"5\":\"Come back to starting position reversing the steps.\"}', NULL, 1, '2022-07-06 02:27:10', '2022-07-06 02:27:10'),
(102, 'Elbow curls- Biceps', 'exercise', '{\"0\":\"                    \",\"1\":\"          \",\"2\":\"        \"}', '{\"0\":\"Stand straight and hold a weight in your hand or a resistance band attaching it under your leg.\",\"1\":\"Keep the forearm rotated so that palm faces up.\",\"2\":\"Bend your elbow against the resistance.\"}', NULL, 1, '2022-07-06 02:29:34', '2022-07-06 02:29:34'),
(103, 'Elbow curls- Hammer', 'exercise', '{\"0\":\"                    \",\"1\":\"              \",\"2\":\"        \"}', '{\"0\":\"Stand straight and hold a weight in your hand or a resistance band attaching it under your leg.\",\"1\":\"Keep the forearm in neutral rotation so that palm faces towards your thigh.\",\"2\":\"Bend your elbow against the resistance.\"}', NULL, 1, '2022-07-06 02:30:56', '2022-07-06 02:30:56'),
(104, 'Elbow curls- Reverse', 'exercise', '{\"0\":\"                    \",\"1\":\"          \",\"2\":\"        \"}', '{\"0\":\"Stand straight and hold a weight in your hand or a resistance band attaching it under your leg.\",\"1\":\"Keep the forearm rotated so that palm faces down.\",\"2\":\"Bend your elbow against the resistance.\"}', NULL, 1, '2022-07-06 02:32:50', '2022-07-06 02:32:50'),
(105, 'Face Pulls', 'exercise', '{\"0\":\"                  \",\"1\":\"           \",\"2\":\"              \"}', '{\"0\":\"Stand straight. Tie or ask someone to hold a resistance band at eye level.\",\"1\":\"Keep the shoulders elevated in front holding both ends of the band.\",\"2\":\"Pull the band towards your face keeping the elbows at shoulder level.\"}', NULL, 1, '2022-07-06 02:34:20', '2022-07-06 02:34:20'),
(106, 'Figure 4 stretch', 'exercise', '{\"0\":\"        \",\"1\":\"              (   4    ) \",\"2\":\"          \",\"3\":\"             \"}', '{\"0\":\"Lie down on your back with both knees bent.\",\"1\":\"Rotate and keep the targeted side leg on top of the other (like in a figure 4 position).\",\"2\":\"Hold your thigh of the other leg and pull it towards your chest.\",\"3\":\"While doing so push out the rotated leg more with your elbow.\"}', NULL, 1, '2022-07-06 02:36:12', '2022-07-06 02:36:12'),
(107, 'Fire hydrant exercise', 'exercise', '{\"0\":\"     (        ) \",\"1\":\"             \",\"2\":\"                      \",\"3\":\"                \"}', '{\"0\":\"Come in position of all 4s/crawling (Knees below hips and hands below shoulder).\",\"1\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"2\":\"Lift your leg sideways rotating it out keeping the knees bent.\"}', NULL, 1, '2022-07-06 02:48:07', '2022-07-06 02:48:07'),
(108, 'Fire hydrant exercise- Standing', 'exercise', '{\"0\":\"          \",\"1\":\"                    \",\"2\":\"                     \",\"3\":\"                \"}', '{\"0\":\"Stand straight with both feet under your hips. \",\"1\":\"Slightly bend down from the knees and for added resistance you can tie a resistance band around your thighs.\",\"2\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"3\":\"Lift and rotate your leg out sideways keeping the knee bent.\"}', NULL, 1, '2022-07-06 02:50:04', '2022-07-06 02:50:04'),
(109, 'Face pull to external rotation in prone', 'exercise', '{\"0\":\"                \",\"1\":\"              \",\"2\":\"         \",\"3\":\"                         \",\"4\":\"                      \"}', '{\"0\":\"Lie down on your stomach with your exercise arm sideways lifted till 90.\",\"1\":\"Keep hand hanging down the side of the bed from the elbow.\",\"2\":\"For added resistance hold a weight in your hand.\",\"3\":\"Pinch your shoulder blades together and lift the hand up in the same position so that elbow moves towards the ceiling.\",\"4\":\"Hold this position and rotate your arm outwards to bring your wrist at your head level.\"}', NULL, 1, '2022-07-06 03:11:10', '2022-07-06 03:11:10'),
(110, 'Finger abductors rubberband strengthening', 'exercise', '{\"0\":\"         (      /  )     \",\"1\":\"                          \"}', '{\"0\":\"Attach a rubberband around your exercising finger and the adjacent finger which is closer to or the middle finger itself.\",\"1\":\"Keeping the adjacent finger stable in position take your exercising finger away from adjacent finger against the resistance.\"}', NULL, 1, '2022-07-06 10:12:09', '2022-07-06 10:12:09'),
(111, 'Finger ducks', 'exercise', '{\"0\":\"              \",\"1\":\"             \",\"2\":\"                           \"}', '{\"0\":\"Keep your fingers straight in contact with each other and thumb apart.\",\"1\":\"Keeping the fingers straight at other joints bend them at the knuckle joint.\",\"2\":\"Simultaneously take your thumb towards the center and rotate it as if touching the tip of middle finger.\"}', NULL, 1, '2022-07-06 10:13:52', '2022-07-06 10:13:52'),
(112, 'Finger adductors rubberband strengthening', 'exercise', '{\"0\":\"                \",\"1\":\"                        \"}', '{\"0\":\"Attach a rubberband around your exercising finger and hold it with your other hand.\",\"1\":\"Keeping the adjacent fingers stable in position take your exercising finger towards the middle finger against the resistance.\"}', NULL, 1, '2022-07-06 10:22:21', '2022-07-06 10:22:21'),
(113, 'Finger extensors rubberband strengthening', 'exercise', '{\"0\":\"              \",\"1\":\"               \",\"2\":\"                  \"}', '{\"0\":\"Attach a rubberband around your exercising finger and the adjacent finger.\",\"1\":\"Keep your hand stable on an armrest or table with palm facing down.\",\"2\":\"Keeping the adjacent fingers stable in position raise your exercising finger upwards.\"}', NULL, 1, '2022-07-06 10:59:01', '2022-07-06 10:59:01'),
(114, 'Finger flexors rubberband strengthening', 'exercise', '{\"0\":\"              \",\"1\":\"               \",\"2\":\"                  \"}', '{\"0\":\"Attach a rubberband around your exercising finger and the adjacent finger.\",\"1\":\"Keep your hand stable on an armrest or table with palm facing up.\",\"2\":\"Keeping the adjacent fingers stable in position raise your exercising finger upwards.\"}', NULL, 1, '2022-07-06 11:12:25', '2022-07-06 11:12:25'),
(115, 'Finger slides (passive wrist extension)', 'exercise', '{\"0\":\"       (  )     \",\"1\":\"                  \"}', '{\"0\":\"Keep your palm (facing down) and forearm supported on a table.\",\"1\":\"Slide your fingers towards your wrist on the table to let the wrist rise up on its own.\"}', NULL, 1, '2022-07-06 11:14:20', '2022-07-06 11:14:20'),
(116, 'Finger Splays', 'exercise', '{\"0\":\"         (  ) \",\"1\":\"             \"}', '{\"0\":\"Keep your fingers and thumb straight and together (in close contact).\",\"1\":\"From this starting position take all your fingers apart and spread them.\"}', NULL, 1, '2022-07-06 11:15:33', '2022-07-06 11:15:33'),
(117, 'Foam rolling- Adductors', 'exercise', '{\"0\":\"    \",\"1\":\"   ,           4          \",\"2\":\"                ,          \",\"3\":\"            - \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Bent your knee, rotate it outwards and take it away from center of the body making a figure 4 with your leg.\",\"2\":\"Keep a roller on the inner aspect of your thigh while supporting the body on forearm, non-rolling toes and the roller.\",\"3\":\"Move your body sideways to and fro keeping pressure on the inner thigh.\"}', NULL, 1, '2022-07-06 11:44:54', '2022-07-06 11:44:54'),
(118, 'Foam rolling- Adductors', 'exercise', '{\"0\":\"    \",\"1\":\"   ,           4          \",\"2\":\"                ,          \",\"3\":\"            - \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Bent your knee, rotate it outwards and take it away from center of the body making a figure 4 with your leg.\",\"2\":\"Keep a roller on the inner aspect of your thigh while supporting the body on forearm, non-rolling toes and the roller.\",\"3\":\"Move your body sideways to and fro keeping pressure on the inner thigh.\"}', NULL, 2, '2022-07-06 11:46:16', '2022-07-06 11:46:16'),
(119, 'Foam Rolling- Calf', 'exercise', '{\"0\":\"          \",\"1\":\"     \",\"2\":\" ,             \",\"3\":\"   ,    -        \",\"4\":\"              \"}', '{\"0\":\"Long sit with foam roller under your calf.\",\"1\":\"Bend the non-rolling leg at the knees.\",\"2\":\"Support your body on hands the other foot and the calf on roller.\",\"3\":\"While maintaining pressure, roll on calf by moving your body up and down.\",\"4\":\"To increase pressure, keep one leg on top of other.\"}', NULL, 1, '2022-07-06 11:49:43', '2022-07-06 11:49:43'),
(120, 'Foam Rolling- Hamstring', 'exercise', '{\"0\":\"         \",\"1\":\"      \",\"2\":\" ,              \",\"3\":\"   ,    -       \",\"4\":\"              \"}', '{\"0\":\"Long sit with foam roller under your thigh.\",\"1\":\"Bend the non-rolling leg at the knees.\",\"2\":\"Support your body on hands the other foot and the thigh on roller.\",\"3\":\"While maintaining pressure, roll on back of thigh by moving your body up and down.\",\"4\":\"For increasing the pressure, keep one leg on top of other.\"}', NULL, 1, '2022-07-06 11:52:47', '2022-07-06 11:52:47'),
(121, 'Foam Rolling- Piriformis', 'exercise', '{\"0\":\"         \",\"1\":\"                  (    4    ) \",\"2\":\"                               \",\"3\":\"         - \"}', '{\"0\":\"Sit on the roller with both your hips and knees bent. \",\"1\":\"Rotate the targeted side leg outwards and cross it keeping it on top of the other (like in a figure 4 position).\",\"2\":\"Shift your weight on the targeted side buttock by slightly turning and keeping the opposite side arm on the bent and rotated knee.\",\"3\":\"Move your body up and down keeping pressure on the buttock.\"}', NULL, 1, '2022-07-06 11:55:41', '2022-07-06 11:55:41'),
(122, 'Foam Rolling- Quadriceps', 'exercise', '{\"0\":\"    \",\"1\":\"                ,            \",\"2\":\"         - \",\"3\":\"   ,     \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Keep a roller below the front aspect of your thigh while supporting the body on forearm, non-rolling toes and the roller.\",\"2\":\"Move your body up and down keeping pressure on the thigh.\",\"3\":\"To increase pressure, bend your knee while rolling.\"}', NULL, 1, '2022-07-06 11:58:46', '2022-07-06 11:58:46'),
(123, 'Foam rolling- TFL', 'exercise', '{\"0\":\"      ,     \",\"1\":\"        \",\"2\":\"            ,        \",\"3\":\"         - \"}', '{\"0\":\"Lie down sideways turning to the side you want to roll.\",\"1\":\"Keep a roller on the outer aspect of your thigh.\",\"2\":\"Keep your other leg bent and forwards, rotating from the hip and bending from the knee to maintain balance.\",\"3\":\"Move your body up and down keeping pressure on the thigh. \"}', NULL, 1, '2022-07-06 12:02:17', '2022-07-06 12:02:17'),
(124, 'Foam rolling- Shin', 'exercise', '{\"0\":\"              \",\"1\":\"                          \",\"2\":\"              \"}', '{\"0\":\"Come in a position supporting your body on hands and toes.\",\"1\":\"Keep the targeted leg bent from knee and hip and the outer aspect of shin supported on a roller.\",\"2\":\"Move your leg up and down from the hips keeping pressure on the shin.\"}', NULL, 1, '2022-07-06 12:04:05', '2022-07-06 12:04:05'),
(125, 'Foot intrinsic strengthening (towel crumpling)', 'exercise', '{\"0\":\"       \",\"1\":\"        /        \",\"2\":\"         /      \"}', '{\"0\":\"Keep your foot on a towel or a paper.\",\"1\":\"Use your toes to crumple the towel/paper trying to make a ball of it. \",\"2\":\"Afterwards use your toes to spread out the paper/towel back to its starting position.\"}', NULL, 1, '2022-07-06 12:07:27', '2022-07-06 12:07:27'),
(126, 'Forearm extensor compartment rolling', 'exercise', '{\"0\":\"  /                \",\"1\":\"/              \"}', '{\"0\":\"Keep a tennis ball/lacrosse ball under your outer aspect of forearm.\",\"1\":\"Move your hand front and back with constant pressure on the tennis/lacrosse ball.\"}', NULL, 1, '2022-07-06 12:09:20', '2022-07-06 12:09:20'),
(127, 'Front Rows', 'exercise', '{\"0\":\"                   \",\"1\":\"                      \",\"2\":\"                                   \"}', '{\"0\":\"Stand straight. Tie a resistance band in front of you at elbow level or have someone stand holding it.\",\"1\":\"Hold both ends of the band in your hands. Keep your elbows bent at 90 degrees and shoulder in neutral position.\",\"2\":\"Pull your shoulders downwards while pinching your shoulder blades and pull a resistance band towards you taking the elbows behind.\"}', NULL, 1, '2022-07-06 12:12:43', '2022-07-06 12:12:43'),
(128, 'Great toe abduction', 'exercise', '{\"0\":\"           \",\"1\":\"                            \"}', '{\"0\":\"Stand or sit favoring weight on exercising side.\",\"1\":\"Keeping all other toes on the ground try and take your great toe away from other toes maintaining contact with the floor.\"}', NULL, 1, '2022-07-06 14:40:55', '2022-07-06 14:40:55'),
(129, 'Great toe extension', 'exercise', '{\"0\":\"           \",\"1\":\"                     \"}', '{\"0\":\"Stand or sit favoring weight on exercising side.\",\"1\":\"Keeping all other toes on the ground try and lift off your great toe.\"}', NULL, 1, '2022-07-06 14:41:43', '2022-07-06 14:41:43'),
(130, 'Half split adductor rocks', 'exercise', '{\"0\":\"                 \",\"1\":\"                 \",\"2\":\"                             \"}', '{\"0\":\"Come on all fours (crawling position). Place your palms below the shoulders and knees below the hip.\",\"1\":\"Keep your exercising side leg taken out sideways with knee straight in a split position.\",\"2\":\"Push your body behind using your hands to sit back on your non-exercising leg till you feel a comfortable stretch.\"}', NULL, 1, '2022-07-06 14:44:23', '2022-07-06 14:44:23'),
(131, 'Hamstring curls- Prone', 'exercise', '{\"0\":\"    \",\"1\":\"              \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Bend your knee and try to touch your heel to your buttock.\"}', NULL, 1, '2022-07-06 14:45:36', '2022-07-06 14:45:36'),
(132, 'Hamstring curls- Triplanar', 'exercise', '{\"0\":\"    \",\"1\":\"              \",\"2\":\"    : i)      ii)    iii)     \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Bend your knee and try to touch your heel to your buttock.\",\"2\":\"Do the exercise three ways: i) foot rotated inwards. ii) foot straight. iii) foot rotated outwards.\"}', NULL, 1, '2022-07-06 14:48:19', '2022-07-06 14:48:19'),
(133, 'Hamstring curls- Standing', 'exercise', '{\"0\":\"          \",\"1\":\"                  \",\"2\":\"           \"}', '{\"0\":\"Stand facing the wall and take support.\",\"1\":\"Bend your knee behind and upwards, trying to touch your heel to the buttocks.\",\"2\":\"Do not let your knee travel in front of your hips.\"}', NULL, 1, '2022-07-06 14:50:28', '2022-07-06 14:50:28'),
(134, 'Hamstring curls with Bridging', 'exercise', '{\"0\":\"   ,   /       \",\"1\":\"                \",\"2\":\"      ,     /          /\"}', '{\"0\":\"Lie down on your back with both knees bent and heels on a roller/pillow.\",\"1\":\"Squeeze your buttocks and pull the belly button in and upwards tightening your core.\",\"2\":\"Lift off your buttocks towards the ceiling, bend the knees and roll/slide the roller/pillow towards and away from you respectively.\"}', NULL, 1, '2022-07-06 14:52:16', '2022-07-06 14:52:16'),
(135, 'Hamstring stretch with pelvic tilts in standing', 'exercise', '{\"0\":\" //          ,           \",\"1\":\"                              \"}', '{\"0\":\"Stand straight with your exercising leg supported on a step/stool/low chair in front with your knee straight and ankle relaxed.\",\"1\":\"Arch your lower back and push out your buttocks backwards till you feel a comfortable stretch in back of your thigh.\"}', NULL, 1, '2022-07-06 14:53:33', '2022-07-06 14:53:33'),
(136, 'Hamstring sweeps', 'exercise', '{\"0\":\"              \",\"1\":\"                \",\"2\":\"             -    \"}', '{\"0\":\"Stand straight and keep your exercising leg forwards.\",\"1\":\"Bend down from your back reaching behind the exercising leg with both your hands.\",\"2\":\"Sweep your hands forwards of the exercising leg while slowly raising up from the back.\"}', NULL, 1, '2022-07-06 14:54:47', '2022-07-06 14:54:47'),
(137, 'Hamstring 90-90 dynamic stretch', 'exercise', '{\"0\":\"    \",\"1\":\"                    \",\"2\":\"                            \",\"3\":\"          \"}', '{\"0\":\"Lie down on the back.\",\"1\":\"Bend your hip and knee holding the thigh with your hands so as the knee is facing straight up towards the ceiling.\",\"2\":\"Point the toes and straighten your knee till you feel a comfortable stretch in back of your thigh.\",\"3\":\"Bend the knee to straighten it again to the point of stretch.\"}', NULL, 1, '2022-07-06 14:57:06', '2022-07-06 14:57:06'),
(138, 'Hamstring long leg bridges with leg lifts', 'exercise', '{\"0\":\"         \",\"1\":\"    \",\"2\":\"                \"}', '{\"0\":\"Lie down on the back with your knees slightly bent.\",\"1\":\"Lift your buttocks up.\",\"2\":\"Maintain this position and straighten your alternate legs to lift them up.\"}', NULL, 1, '2022-07-06 15:15:28', '2022-07-06 15:15:28'),
(139, 'Heel slides', 'exercise', '{\"0\":\"     \",\"1\":\"           \",\"2\":\"                        \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Bend your knee by sliding the heel towards your body.\",\"2\":\"Slide it as close as you can and slide it back out to straighten the leg.\"}', NULL, 1, '2022-07-06 16:47:33', '2022-07-06 16:47:33'),
(140, 'Heel taps', 'exercise', '{\"0\":\"           \",\"1\":\"                        \",\"2\":\"           \"}', '{\"0\":\"Lie down on your back with your hips and knees bent.\",\"1\":\"Slightly curl up from your upper back keeping the head in a neutral position and avoiding strain to your neck.\",\"2\":\"Tap your heel with the same side hand alternatively.\"}', NULL, 1, '2022-07-06 16:51:51', '2022-07-06 16:51:51'),
(141, 'Heel Walking', 'exercise', '{\"0\":\"   \",\"1\":\"              \",\"2\":\"           \"}', '{\"0\":\"Stand straight.\",\"1\":\"Shift your bodyweight behind lifting up your toes. \",\"2\":\"Maintain this position and walk forward just touching your heels.\"}', NULL, 1, '2022-07-06 16:55:38', '2022-07-06 16:55:38'),
(142, 'Hip Lateral Kicks ( Standing)', 'exercise', '{\"0\":\"       \",\"1\":\"                  \"}', '{\"0\":\"Stand facing the wall.\",\"1\":\"Keeping the trunk stable kick your leg from the hip sideways.\"}', NULL, 1, '2022-07-07 05:10:13', '2022-07-07 05:10:13'),
(143, 'High Plank', 'exercise', '{\"0\":\"    \",\"1\":\"                 \",\"2\":\"                              \"}', '{\"0\":\"Come on all fours (crawling position).\",\"1\":\"Keep your hands right under your shoulders. Keep your feet shoulder width apart.\",\"2\":\"Straighten your knees and take them off the ground supporting your body on your foot and hands. Maintain the body in an inclined line.\"}', NULL, 1, '2022-07-07 05:11:47', '2022-07-07 05:11:47');
INSERT INTO `mst_instructions` (`instruction_id`, `instruction_name`, `instruction_type`, `instruction_description_hindi`, `instruction_description_english`, `instruction_description_marathi`, `isActive`, `created_at`, `updated_at`) VALUES
(144, 'Hip Extension Kicks', 'exercise', '{\"0\":\"       \",\"1\":\"                \"}', '{\"0\":\"Stand facing the wall as close as you can.\",\"1\":\"Without moving your back and keeping the knee straight, kick back from your hips as far as you can.\"}', NULL, 1, '2022-07-07 05:12:50', '2022-07-07 05:12:50'),
(145, 'Hip extension in prone', 'exercise', '{\"0\":\"    \",\"1\":\"                 \",\"2\":\"                         \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Bend the exercising leg from the knee till 90 degrees so that your foot is facing the ceiling.\",\"2\":\"Keeping the knee in this position squeeze your buttocks and lift your leg upwards from the hips trying to reach the ceiling.\"}', NULL, 1, '2022-07-07 05:14:23', '2022-07-07 05:14:23'),
(146, 'Hip flexor banded strengthening', 'exercise', '{\"0\":\"                       \",\"1\":\"            \"}', '{\"0\":\"Stand straight and tie a resistance band around your feet or attach a weight to your exercising leg.\",\"1\":\"Lift your knee forwards against the resistance trying to reach diagonally upwards with the knee.\"}', NULL, 1, '2022-07-07 05:15:31', '2022-07-07 05:15:31'),
(147, 'Hip flexor stretch- Half kneeling position', 'exercise', '{\"0\":\"              (   ) \",\"1\":\"                 \",\"2\":\"            (  )           \",\"3\":\"                         \",\"4\":\"              \"}', '{\"0\":\"Come in a kneeling position (on both your knees) preferably near a wall for support.\",\"1\":\"Keep the non-targeted leg forward supporting it on the foot.\",\"2\":\"Rotate your pelvis (waist bone) behind to flatten your lower back and maintain this position.\",\"3\":\"Bend the targeted sides knee and pull it with your hand trying to touch your foot to the buttock.\",\"4\":\"Pull till you feel a comfortable stretch in front of the thigh.\"}', NULL, 1, '2022-07-07 05:19:47', '2022-07-07 05:19:47'),
(148, 'Hip flexor stretch- Thomas position', 'exercise', '{\"0\":\"     \",\"1\":\"               /                       \",\"2\":\"                      \",\"3\":\"            \"}', '{\"0\":\"Sit on the edge of a bed.\",\"1\":\"Hook the targeted side leg at the foot of the bed with a bent knee/ask someone to hold the thigh firmly on the bed and knee bent as much as possible.\",\"2\":\"Lift the opposite leg bending it from hip and knee and hold it close to your stomach to flatten your lower back.\",\"3\":\"Lie down behind on the bed while maintaining this position.\"}', NULL, 1, '2022-07-07 05:21:50', '2022-07-07 05:21:50'),
(149, 'Hip Hiking exercise', 'exercise', '{\"0\":\"               /   \",\"1\":\"         \",\"2\":\"              ()       \",\"3\":\"              \",\"4\":\"  ()      \"}', '{\"0\":\"Stand on a stair/stepper supporting the body on the non-exercising side leg.\",\"1\":\"Keep the exercising leg hanging out sideways.\",\"2\":\"Drop your exercising side pelvis (waist) as low as you can without touching your foot to the ground.\",\"3\":\"Now tighten your core pulling the belly button inwards and upwards. Lift your pelvis(waist) up as high as you can.\"}', NULL, 1, '2022-07-07 05:32:01', '2022-07-07 05:32:01'),
(150, 'Hip Hinge', 'exercise', '{\"0\":\"           \",\"1\":\"                     \",\"2\":\"              ,     \"}', '{\"0\":\"Stand tall with a stick behind your back.\",\"1\":\"Hold the stick with one hand above your head behind the back and with your other hand below your buttocks.\",\"2\":\"Slightly bend down from your knees. Making sure the back is completely straight and in contact with the stick, bend forward from your hips.\"}', NULL, 1, '2022-07-07 05:33:30', '2022-07-07 05:33:30'),
(151, 'Hip thrust in kneeling', 'exercise', '{\"0\":\"       \",\"1\":\"       \",\"2\":\"   ,                   \"}', '{\"0\":\"Come in a kneeling position and sit behind on your heels.\",\"1\":\"Keep both your hands on your waist.\",\"2\":\"Squeezing the buttocks, push your body ahead driving from the hips and waist to again come in a kneeling position.\"}', NULL, 1, '2022-07-07 06:13:59', '2022-07-07 06:13:59'),
(152, 'I Y T', 'exercise', '{\"0\":\"          \",\"1\":\"   ,            \",\"2\":\"             \",\"3\":\"          )      I    )     Y    )         T      \"}', '{\"0\":\"Stand straight with feet shoulder width apart.\",\"1\":\"Hinge from your hips while bending your knees keeping the back straight.\",\"2\":\"Hold a pinch in between your shoulder blades.\",\"3\":\"Without bending from the elbows lift both your arms 1) Straight up making a I. 2) Diagonally upwards making a Y 3) From front of shoulder level take them apart making a T.\"}', NULL, 1, '2022-07-07 06:15:55', '2022-07-07 06:15:55'),
(153, 'Knee flexion ROM self-assisted (with towel)', 'exercise', '{\"0\":\"               /   \",\"1\":\"   //              \",\"2\":\"       \"}', '{\"0\":\"Lie down on your back and keep your affected leg on a towel/dupatta/tied with a rope.\",\"1\":\"Bend your knee actively while helping this movement by pulling the towel/dupatta/rope with your hands.\",\"2\":\"Bend till a comfortable and tolerable range.\"}', NULL, 1, '2022-07-07 06:17:27', '2022-07-07 06:17:27'),
(154, 'High knee kick', 'exercise', '{\"0\":\"       \",\"1\":\"                        \"}', '{\"0\":\"Stand facing the wall.\",\"1\":\"Keeping the trunk stable bend your knee and lift your leg driving the knee as high as you can.\"}', NULL, 1, '2022-07-07 06:25:28', '2022-07-07 06:25:28'),
(155, 'Hip Adduction kicks', 'exercise', '{\"0\":\"       \",\"1\":\"                \"}', '{\"0\":\"Stand facing the wall.\",\"1\":\"Keeping the trunk stable kick your leg from the hip inwards.\"}', NULL, 1, '2022-07-07 06:26:46', '2022-07-07 06:26:46'),
(156, 'Hip Flexion kick', 'exercise', '{\"0\":\"       \",\"1\":\"                \"}', '{\"0\":\"Stand facing the wall.\",\"1\":\"Keeping the trunk stable kick your leg from the hip straight towards the wall keeping the knee straight.\"}', NULL, 1, '2022-07-07 06:29:42', '2022-07-07 06:29:42'),
(157, 'Hip flexor banded running take offs', 'exercise', '{\"0\":\"                       \",\"1\":\"          \",\"2\":\"           \",\"3\":\"                                 \"}', '{\"0\":\"Tie a resistance band around both your feet or attach a weight to your exercising leg.\",\"1\":\"Stand with both knees bent and back straight.\",\"2\":\"Keep your non-exercising leg ahead. \",\"3\":\"Come up quickly from your knees and drive your exercising knee forward towards your chest against resistance. Give yourself momentum using the opposite hand.\"}', NULL, 1, '2022-07-07 06:31:57', '2022-07-07 06:31:57'),
(158, 'Hip open-close gate', 'exercise', '{\"0\":\"   \",\"1\":\"                  \",\"2\":\"              -         \",\"3\":\"          \"}', '{\"0\":\"Stand straight.\",\"1\":\"Rotate your exercising leg inwards and lift it high bending it from hip and knees.\",\"2\":\"Continue in a circular motion to rotate your leg outwards and slowly straightening and lowering your hip and knee.\",\"3\":\"Come back to starting position in a reverse manner.\"}', NULL, 1, '2022-07-07 10:49:39', '2022-07-07 10:49:39'),
(159, 'Hip isometrics', 'exercise', '{\"0\":\"           -     \",\"1\":\"     -\",\"2\":\"i)                      \",\"3\":\"ii)                        \",\"4\":\"iii)                              \",\"5\":\"iv) \'iii\'           \"}', '{\"0\":\"Lie down on your back. Keep your hip and knee bent 90-90 degrees.\",\"1\":\"Perform the exercise in 4 ways-\",\"2\":\"i) Keep both your fists between the knees and press your legs together on the fists and maintain the position.\",\"3\":\"ii) Keep your hands on the outside of respective knees. Press your legs outwards against the hands and maintain the position.\",\"4\":\"iii) Keep one hand on the front aspect of same side thigh and other hand clasping the knee from front. Push your legs against the hands and hold the position.\",\"5\":\"iv) Switch the hand position from exercise iii. Push against the hands and hold the position.\"}', NULL, 1, '2022-07-07 10:56:48', '2022-07-07 10:56:48'),
(160, 'Knee flexion ROM gravity assisted (on wall)', 'exercise', '{\"0\":\"                         \",\"1\":\"             \",\"2\":\"          \",\"3\":\"                    \"}', '{\"0\":\"Lie down on your back and keep your affected leg straight on the wall with the other leg supporting the affected leg from beneath.\",\"1\":\"Let the foot slide down on the wall bending your knee.\",\"2\":\"Control this movement using your other leg.\",\"3\":\"Allow the leg to bend to a comfortable and tolerable extent, then with the help of the other leg, straighten the painful leg.\"}', NULL, 1, '2022-07-07 10:58:56', '2022-07-07 10:58:56'),
(161, 'Knee flexion with Rotational mobilization', 'exercise', '{\"0\":\"     \",\"1\":\"            \",\"2\":\"                \",\"3\":\"                              \",\"4\":\"            \"}', '{\"0\":\"Come in a kneeling position.\",\"1\":\"Keep your exercising leg forward Supporting it on the foot.\",\"2\":\"Grab hold your leg one hand distance below your knee firmly with both the hands.\",\"3\":\"Try to slightly rotate the held bone outwards and shift your weight ahead on the exercising leg bending the knee. \",\"4\":\"Come back and repeat with rotating the held bone inwards.\"}', NULL, 1, '2022-07-07 11:01:13', '2022-07-07 11:01:13'),
(162, 'Knee passive flexion oscillations from full extension', 'exercise', '{\"0\":\"      \",\"1\":\"        \",\"2\":\"                \",\"3\":\"        -  \"}', '{\"0\":\"Come in a long sitting position. \",\"1\":\"Hold above your knee with both hands.\",\"2\":\"Slightly lift up the thigh using your hands with no help from the thigh muscles.\",\"3\":\"In a comfortable range and a slow tempo keep moving the knee up and down.\"}', NULL, 1, '2022-07-07 11:03:21', '2022-07-07 11:03:21'),
(163, 'Knee ROM bedside with end range overpressure', 'exercise', '{\"0\":\"  \",\"1\":\"                  \",\"2\":\"                 \",\"3\":\"          \",\"4\":\"              \"}', '{\"0\":\"Sit straight.\",\"1\":\"Tighten muscles in front of your thigh and straighten your exercising knee.\",\"2\":\"At the end range push the leg more upwards using your other leg.\",\"3\":\"Afterwards, bend the exercising leg as far as you can.\",\"4\":\"At the end range push the leg more inwards using the other leg.\"}', NULL, 1, '2022-07-07 11:05:26', '2022-07-07 11:05:26'),
(164, 'Knee rotations in transverse plane', 'exercise', '{\"0\":\"           \",\"1\":\"       \",\"2\":\"                 \",\"3\":\"         \"}', '{\"0\":\"Sit down with your hip and knee bent as much as you can.\",\"1\":\"Keep your feet supported on the ground.\",\"2\":\"Lift up your toes and take your feet left and right pivoting on the heels.\",\"3\":\"Try to maintain your ankles in a neutral position.\"}', NULL, 1, '2022-07-07 11:34:34', '2022-07-07 11:34:34'),
(165, 'Knee to chest Bilateral mobilization', 'exercise', '{\"0\":\"      \",\"1\":\"        \",\"2\":\"            \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Bring both of your knees towards the chest. \",\"2\":\"Try to touch the knees to the chest with help from your arms.\"}', NULL, 1, '2022-07-07 11:36:02', '2022-07-07 11:36:02'),
(166, 'Knee to chest unilateral mobilization', 'exercise', '{\"0\":\"      \",\"1\":\"         \",\"2\":\"            \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Bring your knee towards the chest.\",\"2\":\"Try to touch the knee to the chest with help from your arms.\"}', NULL, 1, '2022-07-07 11:37:44', '2022-07-07 11:37:44'),
(167, 'Kneeling adductor rocks ', 'exercise', '{\"0\":\"     \",\"1\":\"                   \",\"2\":\"                  \",\"3\":\"                 \",\"4\":\"                  \"}', '{\"0\":\"Come in a kneeling position (on both the knees). \",\"1\":\"Keep opposite leg to the targeted side rotated out sideways supporting it on the foot.\",\"2\":\"Keep the targeted side hand on the waist and opposite hand raised over the head. \",\"3\":\"Push your waist towards the non-targeted side thus shifting your weight on that leg. \",\"4\":\"Take it to a point of comfortable stretch at the inner aspect of your thigh.\"}', NULL, 1, '2022-07-07 11:39:43', '2022-07-07 11:39:43'),
(168, 'Knee Posterior capsule stretch', 'exercise', '{\"0\":\"    /     \",\"1\":\"             \",\"2\":\"                       \",\"3\":\"                      \"}', '{\"0\":\"Be in a long sitting/lying down position. \",\"1\":\"Keep your ankle supported without any support at the knee.\",\"2\":\"Press your knee down either by your hands or by tightening the muscles in front of your thigh. \",\"3\":\"Perform this till you feel a comfortable stretch in the back of the knee and hold this position.\"}', NULL, 1, '2022-07-07 12:17:24', '2022-07-07 12:17:24'),
(169, 'Lat pull downs', 'exercise', '{\"0\":\"           \",\"1\":\"         \",\"2\":\"                \"}', '{\"0\":\"Attach a resistance band above or at head level.\",\"1\":\"Hold two ends of the band in both your hands.\",\"2\":\"Keeping the elbows straight pull the band down and behind your back using both hands.\"}', NULL, 1, '2022-07-08 10:09:05', '2022-07-08 10:09:05'),
(170, 'Lateral Leg Raise- Triplanar ', 'exercise', '{\"0\":\"          \",\"1\":\"           \",\"2\":\"               \",\"3\":\")   )      )     \"}', '{\"0\":\"Lie down on non-exercising side. Keep the hip and knee of the lower leg bent for stability.\",\"1\":\"Lift your exercising leg away from the other, keeping the knee straight\",\"2\":\"i) sideways up ii) sideways & diagonally behind iii) sideways & diagonally ahead\"}', NULL, 1, '2022-07-08 10:14:20', '2022-07-08 10:14:20'),
(171, 'Lateral leg raises', 'exercise', '{\"0\":\"          \",\"1\":\"           \",\"2\":\"                 \"}', '{\"0\":\"Lie down on non-exercising side. Keep the hip and knee of the lower leg bent for stability.\",\"1\":\"Lift your exercising leg away from the other, keeping the knee straight\"}', NULL, 1, '2022-07-08 11:00:29', '2022-07-08 11:00:29'),
(172, 'Lateral step down', 'exercise', '{\"0\":\"             ,               \",\"1\":\"     \",\"2\":\"     (        )               \"}', '{\"0\":\"Stand on a raised platform on your exercising leg preferably near a wall or stairs with railing for support.\",\"1\":\"Keep both your hands on the waist.\",\"2\":\"Without dropping your waist (maintaining both the hands at same level) touch the ground with the heel of non-exercising leg sideways and come back up.\"}', NULL, 1, '2022-07-08 11:02:42', '2022-07-08 11:02:42'),
(173, 'Latissimus stretch', 'exercise', '{\"0\":\"      ,          \",\"1\":\"                       \",\"2\":\"                       \"}', '{\"0\":\"Stand with wall opposite to the side you want to stretch.\",\"1\":\"Raise same side arm over your head and take it bend towards the wall so that the hand touches the wall.\",\"2\":\"To increase the stretch, take the same side leg behind and inwards crossing the other leg. Hold this position.\"}', NULL, 1, '2022-07-08 11:05:20', '2022-07-08 11:05:20'),
(174, 'Levator stretch', 'exercise', '{\"0\":\"              \",\"1\":\"                                \",\"2\":\"   \"}', '{\"0\":\"Turn your head opposite to the side you want to stretch. \",\"1\":\"With your same side hand pull the same shoulder down and with opposite side hand pull your head to try and touch your nose to the shoulder.\",\"2\":\"Hold the position.\"}', NULL, 1, '2022-07-08 11:07:56', '2022-07-08 11:07:56'),
(175, 'Lower limb lateral fascia stretching', 'exercise', '{\"0\":\"    \",\"1\":\"        //           \",\"2\":\"                        \",\"3\":\"                  \"}', '{\"0\":\"Lie down on the back.\",\"1\":\"Hook a towel/belt/dupatta around your exercising side foot and hold both the ends in your non-exercising sides hand.\",\"2\":\"Rotate the foot inwards and pull both the ends towards you diagonally to the non-exercising side shoulder without bending the knee.\",\"3\":\"Take it up till you feel a comfortable stretch on the outer side of your leg.\"}', NULL, 1, '2022-07-08 11:10:43', '2022-07-08 11:10:43'),
(176, 'Lumbo pelvic rotations', 'exercise', '{\"0\":\"           \",\"1\":\"             \",\"2\":\"         \"}', '{\"0\":\"Lie down on your back with both your knees bent.\",\"1\":\"Let your legs fall down to one side so that the knees touch the bed.\",\"2\":\"Bring your leg back up and repeat on the other side.\"}', NULL, 1, '2022-07-08 11:13:55', '2022-07-08 11:13:55'),
(177, 'Lumbo pelvic rotations with overpressure', 'exercise', '{\"0\":\"           \",\"1\":\"             \",\"2\":\"        \",\"3\":\"         \"}', '{\"0\":\"Lie down on your back with both your knees bent.\",\"1\":\"Let your legs fall down to one side so that the knees touch the bed.\",\"2\":\"Use your hand to give slight pressure at the end range.\",\"3\":\"Bring your leg back up and repeat on the other side.\"}', NULL, 1, '2022-07-08 11:18:35', '2022-07-08 11:18:35'),
(178, 'Lunges- Inline', 'exercise', '{\"0\":\"          \",\"1\":\"            \",\"2\":\"     ,     \",\"3\":\"          \"}', '{\"0\":\"Stand straight. Keep both hands on your waist.\",\"1\":\"Put forward one of your legs in line with the other.\",\"2\":\"Without bending down from your back, bend down from your knees.\",\"3\":\"Try to get the knee of the leg behind to touch the ground.\"}', NULL, 1, '2022-07-08 11:23:36', '2022-07-08 11:23:36'),
(179, 'Lunges- Lateral', 'exercise', '{\"0\":\"          \",\"1\":\"         \",\"2\":\"             \"}', '{\"0\":\"Stand straight. Keep both hands on your waist.\",\"1\":\"Take your targeted side leg outwards sideways and bend down from your knees while planting it on ground while keeping your back and opposite leg straight. \",\"2\":\"Keep the knee facing forwards all the time. Return to starting position.\"}', NULL, 2, '2022-07-08 11:25:27', '2022-07-08 11:25:27'),
(180, 'Lunges- Lateral', 'exercise', '{\"0\":\"    \",\"1\":\"                       \",\"2\":\"         \",\"3\":\"             \"}', '{\"0\":\"Stand straight.\",\"1\":\"Take your targeted side leg outwards sideways and bend down from your knees while planting it on ground while keeping your back and opposite leg straight.\",\"2\":\"Keep the knee facing forwards all the time. Return to starting position.\"}', NULL, 1, '2022-07-08 11:30:24', '2022-07-08 11:30:24'),
(181, 'Lunges- Forward', 'exercise', '{\"0\":\"          \",\"1\":\"        \",\"2\":\"     ,               \"}', '{\"0\":\"Stand straight. Keep both hands on your waist.\",\"1\":\"Put forward one of your legs.\",\"2\":\"Try and touch the knee of the leg which is behind to the ground without bending from your back.\"}', NULL, 1, '2022-07-08 12:20:31', '2022-07-08 12:20:31'),
(182, 'Lunges- Reverse', 'exercise', '{\"0\":\"          \",\"1\":\"                   \",\"2\":\"             \"}', '{\"0\":\"Stand straight. Keep both hands on your waist.\",\"1\":\"Take your non-exercising leg behind and try to touch the knee of the same leg to the ground. \",\"2\":\"Do this keeping your back straight and bending from the knee of front leg.\"}', NULL, 1, '2022-07-08 12:22:53', '2022-07-08 12:22:53'),
(183, 'Lunge runners multidirectional', 'exercise', '{\"0\":\"     (  ) \",\"1\":\"               \",\"2\":\"i)       \",\"3\":\"ii)     ,    \",\"4\":\"iii)         \",\"5\":\"                       \"}', '{\"0\":\"Come in a kneeling position (on both the knees). \",\"1\":\"Perform the exercise by keeping the non-exercising leg in three directions one after other\",\"2\":\"i) Keep leg in front placing it on the foot.\",\"3\":\"ii) Keep the leg diagonally out placing it on the foot.\",\"4\":\"iii) Keep the leg out sideways placing it on the foot.\",\"5\":\"Lunge on the non-kneeling foot taking the knee in front of the toes. \"}', NULL, 1, '2022-07-08 12:36:38', '2022-07-08 12:36:38'),
(184, 'Mckenzie lateral opener', 'exercise', '{\"0\":\"           \",\"1\":\"            \",\"2\":\"                      \",\"3\":\"    \"}', '{\"0\":\"Stand with your affected side towards the wall.\",\"1\":\"Place your forearm sideways against the wall at shoulder level.\",\"2\":\"Keep your opposite hand on the waist and push your waist towards the wall, bending laterally to opposite side.\",\"3\":\"Relax to come in starting position and repeat.\"}', NULL, 1, '2022-07-08 12:39:45', '2022-07-08 12:39:45'),
(185, 'MNT1 double ended tensioner', 'exercise', '{\"0\":\"    \",\"1\":\"              \",\"2\":\"            \",\"3\":\"                              \",\"4\":\"                       \"}', '{\"0\":\"Stand straight.\",\"1\":\"Lift your arm sideways just above your shoulder level.\",\"2\":\"Keep your palm facing up and elbow straight. \",\"3\":\"Extend your wrist so that fingers point down and bend your head to opposite side to touch ear to the shoulder simultaneously.\",\"4\":\"Bring your head back to neutral and wrist up to relax. Repeat the stretch.\"}', NULL, 1, '2022-07-08 12:43:38', '2022-07-08 12:43:38'),
(186, 'MNT1 sliders', 'exercise', '{\"0\":\"    \",\"1\":\"              \",\"2\":\"            \",\"3\":\"                                \",\"4\":\"                       \"}', '{\"0\":\"Stand straight.\",\"1\":\"Lift your arm sideways just above your shoulder level.\",\"2\":\"Keep your palm facing up and elbow straight. \",\"3\":\"Extend your wrist so that fingers point down and bend your head to same side to touch ear to the shoulder simultaneously.\",\"4\":\"Bring your head back to neutral and wrist up to relax. Repeat the stretch.\"}', NULL, 1, '2022-07-08 12:46:07', '2022-07-08 12:46:07'),
(187, 'MNT1 stretch', 'exercise', '{\"0\":\"             \",\"1\":\"               \",\"2\":\"      ,        \",\"3\":\"                 \"}', '{\"0\":\"Stand straight with your stretching arm towards the wall.\",\"1\":\"Lift your arm sideways just above your shoulder level.\",\"2\":\"Keep your hand rotate outwards, palm facing up and elbow straight.\",\"3\":\"Place your palm against the wall fingers pointing downwards. Hold this position.\"}', NULL, 1, '2022-07-08 12:49:46', '2022-07-08 12:49:46'),
(188, 'Neck circle with neural tension', 'exercise', '{\"0\":\"               (    ) \",\"1\":\"          \",\"2\":\"         \"}', '{\"0\":\"Stand straight with both your arms lifted slightly away from the body sideways (palms facing in front).\",\"1\":\"Pull your wrist behind and maintain this position.\",\"2\":\"Rotate your neck in a circular manner and come back in reverse.\"}', NULL, 1, '2022-07-08 12:53:54', '2022-07-08 12:53:54'),
(189, 'Neck half circles', 'exercise', '{\"0\":\"    / \",\"1\":\"      \",\"2\":\"                         \",\"3\":\"    \"}', '{\"0\":\"Stand/sit straight.\",\"1\":\"Bend your neck forward.\",\"2\":\"Rotate your neck sideways in one direction in a circular motion till your ear is above the same side shoulder.\",\"3\":\"Now go in the opposite direction.\"}', NULL, 1, '2022-07-08 13:35:21', '2022-07-08 13:35:21'),
(190, 'Partial curl ups', 'exercise', '{\"0\":\"           \",\"1\":\"     /   /    (     )\",\"2\":\"              \"}', '{\"0\":\"Lie down on your back with both your knees bent.\",\"1\":\"Keep your arms by side/Crossed in front of your chest/ behind your head (according to increasing level of difficulty)\",\"2\":\"Curl only your upper back towards your knees keeping the head stable.\"}', NULL, 1, '2022-07-09 03:59:44', '2022-07-09 03:59:44'),
(191, 'Patellar inferior glide ', 'exercise', '{\"0\":\"    ,                    \"}', '{\"0\":\"In long sitting, relax your thigh muscles and push your knee cap towards the foot.\"}', NULL, 1, '2022-07-09 04:04:09', '2022-07-09 04:04:09'),
(192, 'Patella lateral retinaculum stretch', 'exercise', '{\"0\":\"    ,                     (    )     \"}', '{\"0\":\"In long sitting, relax your thigh muscles and push your knee cap inwards (in direction of other leg) and hold the position.\"}', NULL, 1, '2022-07-09 04:05:28', '2022-07-09 04:05:28'),
(193, 'Patellar mobility all directions ', 'exercise', '{\"0\":\"    ,                      -\",\"1\":\"i)    \",\"2\":\"ii)    \",\"3\":\"iii)     \",\"4\":\"iv)         \"}', '{\"0\":\"In long sitting, relax your thigh muscles and push your knee cap towards the following directions-\",\"1\":\"i) up & down \",\"2\":\"ii) inwards & outwards \",\"3\":\"iii) diagonally upwards & downwards \",\"4\":\"iv) hook under the knee cap and lift it up\"}', NULL, 1, '2022-07-09 04:09:10', '2022-07-09 04:09:10'),
(194, 'Pectoralis stretch- hand behind head', 'exercise', '{\"0\":\"       \",\"1\":\"                 \",\"2\":\"                                  \"}', '{\"0\":\"Stand near a wall.\",\"1\":\"Bend your arm and lift up your shoulder sideways to put your hand behind the head.\",\"2\":\"Support the folded arm against the wall and turn to the opposite side till you feel a comfortable stretch in front of your shoulder and chest.\"}', NULL, 1, '2022-07-09 04:11:22', '2022-07-09 04:11:22'),
(195, 'Pectoralis stretch- hand below shoulder', 'exercise', '{\"0\":\"       \",\"1\":\"              \",\"2\":\"           \",\"3\":\"                      \"}', '{\"0\":\"Stand near a wall.\",\"1\":\"Lift your arm up sideways midway between the waist and shoulder level.\",\"2\":\"Keep the elbow slightly bent and support the arm against the wall. \",\"3\":\"Turn to the opposite side till you feel a comfortable stretch in front of your shoulder and chest.\"}', NULL, 1, '2022-07-09 04:14:32', '2022-07-09 04:14:32'),
(196, 'Pectoralis stretch- supine (chest opener)', 'exercise', '{\"0\":\"         \",\"1\":\"            /   \",\"2\":\"                       ,                 \"}', '{\"0\":\"Lie down on your back with both knees bent.\",\"1\":\"Keep a roller/bolster vertical under your upper back and supporting the head.\",\"2\":\"Raise both your arms sideways till shoulder level with your elbows bent and let them fall down towards the ground till you feel a comfortable stretch in front of your chest. Hold this position.\"}', NULL, 1, '2022-07-09 04:16:56', '2022-07-09 04:16:56'),
(197, 'Pelvic tilts- Standing', 'exercise', '{\"0\":\"                \",\"1\":\"                          \",\"2\":\"                        \"}', '{\"0\":\"Stand straight. Keep both hands on the waist to appreciate the movement.\",\"1\":\"Keeping the upper back and knees straight, try and push your groin forwards to flatten the lower back.\",\"2\":\"Afterwards try and push your hips out and backwards to arch your lower back.\"}', NULL, 1, '2022-07-09 04:19:05', '2022-07-09 04:19:05'),
(198, 'Pelvic tilts- Supine ', 'exercise', '{\"0\":\"           \",\"1\":\"                \",\"2\":\"              ,                   \"}', '{\"0\":\"Lie down on your back with hip and knee bent.\",\"1\":\"Keep one hand below the lower back to appreciate the movement.\",\"2\":\"Flatten your lower and push it over your hand afterwards arch the lower back to take it away from your hand.\"}', NULL, 1, '2022-07-09 04:21:13', '2022-07-09 04:21:13'),
(199, 'Plank', 'exercise', '{\"0\":\"     \",\"1\":\"                          \",\"2\":\"                    \",\"3\":\"   \"}', '{\"0\":\"Lie down on your stomach. \",\"1\":\"Keep both your elbows below your shoulder and lift your body up with support of your forearms and toes.\",\"2\":\"Maintain a straight spine and do not let your hips rise or drop too much.\",\"3\":\"Hold this position.\"}', NULL, 1, '2022-07-09 05:51:16', '2022-07-09 05:51:16'),
(200, 'Plank on knees', 'exercise', '{\"0\":\"     \",\"1\":\"                          \",\"2\":\"                    \",\"3\":\"   \"}', '{\"0\":\"Lie down on your stomach. \",\"1\":\"Keep both your elbows below your shoulder and knees bent.\",\"2\":\"Lift your body up with support of your forearms and knees.\",\"3\":\"Maintain a straight spine and do not let your hips rise or drop too much. \",\"4\":\"Hold this position.\"}', NULL, 1, '2022-07-09 05:52:56', '2022-07-09 05:52:56'),
(201, 'Plank with Kickbacks', 'exercise', '{\"0\":\"     \",\"1\":\"                          \",\"2\":\"                    \",\"3\":\"                    \"}', '{\"0\":\"Lie down on your stomach. \",\"1\":\"Keep both your elbows below your shoulder and lift your body up with support of your forearms and toes.\",\"2\":\"Maintain a straight spine and do not let your hips rise or drop too much. \",\"3\":\"Holding this position raise your alternate legs towards the ceiling keeping the knee straight.\"}', NULL, 1, '2022-07-09 05:54:23', '2022-07-09 05:54:23'),
(202, 'Plantar fascia rolling ', 'exercise', '{\"0\":\"  /  ,      /  \",\"1\":\"            \"}', '{\"0\":\"Sitting on a chair/ standing, put a tennis/lacrosse ball under your foot.\",\"1\":\"Roll it on your sole from heel to great toe.\"}', NULL, 1, '2022-07-09 05:55:16', '2022-07-09 05:55:16'),
(203, 'Plantar fascia stretching ', 'exercise', '{\"0\":\"     ,                         \"}', '{\"0\":\"Lie down on your back keep your leg straight with your other leg pull your fingers towards you.\"}', NULL, 1, '2022-07-09 05:56:04', '2022-07-09 05:56:04'),
(204, 'Pogo Hops', 'exercise', '{\"0\":\"          \",\"1\":\"         \",\"2\":\"          \"}', '{\"0\":\"Stand tall with feet hip width apart.\",\"1\":\"Come on your toes raising the heels.\",\"2\":\"Jump bouncing up and down without letting your heel touch the ground.\"}', NULL, 1, '2022-07-09 05:58:02', '2022-07-09 05:58:02'),
(205, 'Pogo Hops single leg', 'exercise', '{\"0\":\"          \",\"1\":\"                \",\"2\":\"         \",\"3\":\"          \"}', '{\"0\":\"Stand tall with feet hip width apart.\",\"1\":\"Raise your non-exercising leg by bending it from the knee.\",\"2\":\"Come on your toes raising the heel. \",\"3\":\"Jump bouncing up and down without letting your heel touch the ground.\"}', NULL, 1, '2022-07-09 05:59:26', '2022-07-09 05:59:26'),
(206, 'Pronation ROM with overpressure', 'exercise', '{\"0\":\"      //        \",\"1\":\"               //              \"}', '{\"0\":\"Hold a stick/pipe/dumbbell pointing up in the hand of the targeted side. \",\"1\":\"Pull the stick/pipe/dumbbell with your other hand to make your palm face down and give slight over pressure at end range.\"}', NULL, 1, '2022-07-09 10:38:26', '2022-07-09 10:38:26'),
(207, 'Pronator Isometrics', 'exercise', '{\"0\":\"        \",\"1\":\"      (  ) \",\"2\":\"                      (   )     \",\"3\":\"        \"}', '{\"0\":\"Rest both forearms on top of a table.\",\"1\":\"Hold your fists together (prayer position).\",\"2\":\"Try to turn your exercising hand inwards (palms face down) while resisting with the non-exercising hand with equal force. \",\"3\":\"Adjust the forces so that no movement should happen.\"}', NULL, 1, '2022-07-09 10:40:26', '2022-07-09 10:40:26'),
(208, 'Prone back extensions', 'exercise', '{\"0\":\"     \",\"1\":\"        \",\"2\":\"              \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Keep your hands behind your head. \",\"2\":\"Lift up your head and chest off the bed without using your hands.\"}', NULL, 1, '2022-07-09 10:42:40', '2022-07-09 10:42:40'),
(209, 'Prone on elbows', 'exercise', '{\"0\":\"     \",\"1\":\"                            \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Come up from your back on both your elbows using primarily your back muscles and pushing minimally from the elbows and hands.\"}', NULL, 1, '2022-07-09 10:44:12', '2022-07-09 10:44:12'),
(210, 'Prone on hands', 'exercise', '{\"0\":\"     \",\"1\":\"                         \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Come up from your back on both your hands using primarily your back muscles and pushing minimally from the hands.\"}', NULL, 1, '2022-07-09 10:46:23', '2022-07-09 10:46:23'),
(211, 'Push up plus', 'exercise', '{\"0\":\"            \",\"1\":\"              \",\"2\":\"           \",\"3\":\"                        \",\"4\":\"              \",\"5\":\"               \"}', '{\"0\":\"Come in a position where your body is supported by your hands and toes.\",\"1\":\"Keep your hands slightly apart from shoulder width and at chest level.\",\"2\":\"Feet should be hip width apart and in line with shoulder. Keep a neutral lower back and neck.\",\"3\":\"Take your chest as near to the floor as possible bending the elbows.\",\"4\":\"Push away from the floor using your hands and maintaining the form.\",\"5\":\"At the highest point push your shoulders down more to reach higher.\"}', NULL, 1, '2022-07-09 10:50:03', '2022-07-09 10:50:03'),
(212, 'Push ups', 'exercise', '{\"0\":\"            \",\"1\":\"              \",\"2\":\"           \",\"3\":\"                        \",\"4\":\"              \"}', '{\"0\":\"Come in a position where your body is supported by your hands and toes.\",\"1\":\"Keep your hands slightly apart from shoulder width and at chest level.\",\"2\":\"Feet should be hip width apart and in line with shoulder. Keep a neutral lower back and neck.\",\"3\":\"Take your chest as near to the floor as possible bending the elbows.\",\"4\":\"Push away from the floor using your hands and maintaining the form.\"}', NULL, 1, '2022-07-09 10:52:27', '2022-07-09 10:52:27'),
(213, 'Push up plus on knees', 'exercise', '{\"0\":\"              \",\"1\":\"               \",\"2\":\"         \",\"3\":\"                         \",\"4\":\"              \",\"5\":\"               \"}', '{\"0\":\"Come on all fours (crawling position). \",\"1\":\"Keep your hands slightly ahead and outwards of your shoulder joints at the level of chest.\",\"2\":\"Take support on your knees by keeping the back and hips straight.\",\"3\":\"Keeping your back and neck flat try to touch your chest to the ground and back up.\",\"4\":\"Push down more at the highest position from your shoulders to reach higher.\"}', NULL, 1, '2022-07-09 10:58:24', '2022-07-09 10:58:24'),
(214, 'Push ups on knees', 'exercise', '{\"0\":\"              \",\"1\":\"               \",\"2\":\"         \",\"3\":\"                         \",\"4\":\"              \"}', '{\"0\":\"Come on all fours (crawling position). \",\"1\":\"Keep your hands slightly ahead and outwards of your shoulder joints at the level of chest.\",\"2\":\"Take support on your knees by keeping the back and hips straight.\",\"3\":\"Keeping your back and neck flat try to touch your chest to the ground and back up.\"}', NULL, 1, '2022-07-09 11:02:48', '2022-07-09 11:02:48'),
(215, 'Quadriceps Eccentric Falls', 'exercise', '{\"0\":\"    \",\"1\":\"      \",\"2\":\"      - -                    \",\"3\":\" -           \"}', '{\"0\":\"Come in a kneeling position.\",\"1\":\"Keep both your hands on the chest.\",\"2\":\"Without Moving from your back or hip, slowly and in a controlled manner take your body behind as much as you can hinging from your knee.\",\"3\":\"Then slowly sit on your heels and return to starting position.\"}', NULL, 1, '2022-07-10 10:22:06', '2022-07-10 10:22:06'),
(216, 'Quadriceps plus hip flexor stretch prone', 'exercise', '{\"0\":\"     \",\"1\":\"             \",\"2\":\"               \",\"3\":\"           \",\"4\":\"              \"}', '{\"0\":\"Lie down flat on your stomach.\",\"1\":\"Keep a pillow under your thigh to raise that leg higher.\",\"2\":\"Bend the knee to hold your leg above your ankle.\",\"3\":\"Using your hands try to press down your foot to the buttocks.\",\"4\":\"Pull till you feel a comfortable stretch in front of the thigh.\"}', NULL, 1, '2022-07-10 10:25:48', '2022-07-10 10:25:48');
