(217, 'Quadriceps stretch prone', 'exercise', '{\"0\":\"     \",\"1\":\"               \",\"2\":\"           \",\"3\":\"              \"}', '{\"0\":\"Lie down flat on your stomach.\",\"1\":\"Bend the knee to hold your leg above your ankle.\",\"2\":\"Using your hands try to press down your foot to the buttocks.\",\"3\":\"Pull till you feel a comfortable stretch in front of the thigh.\"}', NULL, 1, '2022-07-10 10:27:32', '2022-07-10 10:27:32'),
(218, 'Radial Nerve double ended tensioner', 'exercise', '{\"0\":\"    \",\"1\":\"               \",\"2\":\"         \",\"3\":\"       \",\"4\":\"                     \"}', '{\"0\":\"Stand straight.\",\"1\":\"Take your arm slightly away from your body sideways to keep it away from the trunk.\",\"2\":\"Rotate your arm inwards as much as you can.\",\"3\":\"Bend the fingers and wrist inwards.\",\"4\":\"Take your arm sideways and behind while simultaneously bending the head sideways on the opposite side.\"}', NULL, 1, '2022-07-10 10:33:33', '2022-07-10 10:33:33'),
(219, 'Radial Nerve Sliders', 'exercise', '{\"0\":\"    \",\"1\":\"               \",\"2\":\"         \",\"3\":\"       \",\"4\":\"                     \"}', '{\"0\":\"Stand straight.\",\"1\":\"Take your arm slightly away from your body sideways to keep it away from the trunk.\",\"2\":\"Rotate your arm inwards as much as you can.\",\"3\":\"Bend the fingers and wrist inwards.\",\"4\":\"Take your arm sideways and behind while simultaneously bending the head sideways on the same side.\"}', NULL, 1, '2022-07-10 10:35:01', '2022-07-10 10:35:01'),
(220, 'Radial nerve stretch', 'exercise', '{\"0\":\"    \",\"1\":\"               \",\"2\":\"         \",\"3\":\"       \",\"4\":\"              //            \"}', '{\"0\":\"Stand straight.\",\"1\":\"Take your arm slightly away from your body sideways to keep it away from the trunk.\",\"2\":\"Rotate your arm inwards as much as you can.\",\"3\":\"Bend the fingers and wrist inwards.\",\"4\":\"Take your arm sideways and behind till you feel a comfortable stretch in your arm/forearm/fingers and hold the position.\"}', NULL, 1, '2022-07-10 10:36:56', '2022-07-10 10:36:56'),
(221, 'Reverse Nordic falls (Quadriceps Eccentric strengthening)', 'exercise', '{\"0\":\"    \",\"1\":\"       \",\"2\":\"      - -                    \",\"3\":\"               \"}', '{\"0\":\"Come in a kneeling position.\",\"1\":\"Keep both your hands on the chest.\",\"2\":\"Without moving from your back or hip, slowly and in a controlled manner take your body behind as much as you can hinging from your knee.\",\"3\":\"Come back to starting position in the same arc using your thigh muscles only.\"}', NULL, 1, '2022-07-10 10:41:46', '2022-07-10 10:41:46'),
(222, 'Reverse plank', 'exercise', '{\"0\":\"        \",\"1\":\"      ,              \",\"2\":\"                        \"}', '{\"0\":\"Sit down with both your legs straight and supported.\",\"1\":\"Keep your hands behind shoulder width apart and fingers pointing towards the toes.\",\"2\":\"Lift your buttocks off the ground supporting your body on hands and heels to make a slope from your head towards the heel.\"}', NULL, 1, '2022-07-10 10:44:02', '2022-07-10 10:44:02'),
(223, 'Rotator cuff External rotators Eccentrics (0)', 'exercise', '{\"0\":\"          \",\"1\":\"             \",\"2\":\"                     \",\"3\":\"              \",\"4\":\"                ,   -   \",\"5\":\"          \"}', '{\"0\":\"Tie a resistance band somewhere at your elbow level.\",\"1\":\"Stand sideways with your exercising arm on opposite side of band.\",\"2\":\"Fix the elbow against the trunk while bending your elbow at 90 degrees and hold the band.\",\"3\":\"Maintaining the position walk sideways to generate tension in the band.\",\"4\":\"Hold this position and in a controlled fashion let your arm rotate inwards, thus reducing the tension slowly.\",\"5\":\"Walk back towards the band to start the next repetition.\"}', NULL, 1, '2022-07-10 10:51:15', '2022-07-10 10:51:15'),
(224, 'Rotator cuff External rotators isometrics walks (0)', 'exercise', '{\"0\":\"          \",\"1\":\"             \",\"2\":\"                     \",\"3\":\"              \",\"4\":\"       -         \"}', '{\"0\":\"Tie a resistance band somewhere at your elbow level.\",\"1\":\"Stand sideways with your exercising arm on opposite side of band.\",\"2\":\"Fix the elbow against the trunk while bending your elbow at 90 degrees and hold the band.\",\"3\":\"Maintaining the position walk sideways to generate tension in the band.\",\"4\":\"Hold this position and walk back to relieve the tension.\"}', NULL, 2, '2022-07-10 10:54:18', '2022-07-10 10:54:18'),
