(253, 'Scapula clicks', 'exercise', '{\"0\":\"          \",\"1\":\"                         -   )           )               )        \"}', '{\"0\":\"Stand or sit straight without back support.\",\"1\":\"Take your shoulder back and downwards to make a pinch in between your shoulder blades.\",\"2\":\"Perform the same at three levels-  i) Hands relaxed  ii) Hands by the side with bent elbows and pulling back them back  iii) Hands straight in front at shoulder level.\"}', NULL, 1, '2022-07-13 05:53:24', '2022-07-13 05:53:24'),
(254, 'Scapula protraction in supine', 'exercise', '{\"0\":\"            \",\"1\":\"        \",\"2\":\"            \"}', '{\"0\":\"Lie down on your back with both your hips and knees bent.\",\"1\":\"Raise your arm up at shoulder level.\",\"2\":\"Push your arm towards the ceiling from the shoulder as far as you can.\"}', NULL, 1, '2022-07-13 06:03:42', '2022-07-13 06:03:42'),
(255, 'Scapula retraction in prone', 'exercise', '{\"0\":\"               \",\"1\":\"      -             \",\"2\":\"     \"}', '{\"0\":\"Lie down on your stomach, turn and rest the head to any comfortable side.\",\"1\":\"Pull the shoulder blades towards each other and in a downward direction to create a pinch between them.\",\"2\":\"Hold the position for some time and relax.\"}', NULL, 1, '2022-07-13 06:06:16', '2022-07-13 06:06:16'),
(256, 'Sternocledomastoid stretch', 'exercise', '{\"0\":\"      ,                     \",\"1\":\"                          \"}', '{\"0\":\"Press your shoulder downwards with the same side hand of the side you want to stretch and turn your head to opposite side.\",\"1\":\"Press your head with the opposite side hand to stretch your neck behind and sideways. Hold the position.\"}', NULL, 1, '2022-07-13 08:40:55', '2022-07-13 08:40:55'),
(257, 'Sciatic nerve slider', 'exercise', '{\"0\":\"     \",\"1\":\"                  \",\"2\":\"                \",\"3\":\"       \"}', '{\"0\":\"Lie down on your back. \",\"1\":\"Bend your hip and knee of the targeted side and pull your ankle towards you.\",\"2\":\"From this position straighten the hip and knee while pointing your ankle down.\",\"3\":\"Alternate between these two positions.\"}', NULL, 1, '2022-07-13 08:45:07', '2022-07-13 08:45:07'),
(258, 'Sciatic nerve tensioner', 'exercise', '{\"0\":\"      \",\"1\":\"                    \",\"2\":\"                     \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Bend your hip and knee of the targeted side and hold the thigh with both your hands.\",\"2\":\"Pull your ankle towards you and straighten the Knee to point your heel towards the ceiling.\"}', NULL, 1, '2022-07-13 08:47:22', '2022-07-13 08:47:22'),
(259, 'Sciatic nerve tensioner standing', 'exercise', '{\"0\":\"                            \",\"1\":\"                ,               \"}', '{\"0\":\"Stand straight and keep the targeted side leg elevated on some surface with the knee straight and ankle pulled towards you. \",\"1\":\"Arch your lower back and push out your buttocks backwards still you feel a comfortable stretch in back of your leg.\"}', NULL, 1, '2022-07-13 08:48:43', '2022-07-13 08:48:43'),
(260, 'Seated Lift', 'exercise', '{\"0\":\"     \",\"1\":\"                    \"}', '{\"0\":\"Sit on a firm surface.\",\"1\":\"Keep both your hands beside the thigh on the surface and lift up your buttocks taking weight on the hands.\"}', NULL, 1, '2022-07-13 08:49:54', '2022-07-13 08:49:54'),
(261, 'Serratus Banded strengthening', 'exercise', '{\"0\":\"                      \",\"1\":\"                              \"}', '{\"0\":\"Stand with a resistance band in both the hands taken from behind at shoulder blades level.\",\"1\":\"With your palms facing down take your arms diagonally out and then reach forwards pushing out from the shoulders against the resistance.\"}', NULL, 1, '2022-07-13 08:51:18', '2022-07-13 08:51:18'),
(262, 'Serratus wall slides', 'exercise', '{\"0\":\"               \",\"1\":\"    ,  /     ,            \",\"2\":\"  ,        /             \"}', '{\"0\":\"Press a foam roller or a pillow against the wall using your forearms.\",\"1\":\"Maintaining the press, slide the foam roller/pillow upwards pushing out from the shoulders to reach as high as you can.\",\"2\":\"At the highest point, lift your hands away from the foam roller/pillow alternatively while pressing it against the wall with your other arm.\"}', NULL, 1, '2022-07-13 08:53:12', '2022-07-13 08:53:12'),
(263, 'Serratus wall slides with rc activation', 'exercise', '{\"0\":\"               \",\"1\":\"        \",\"2\":\"                  \",\"3\":\" /     ,            \"}', '{\"0\":\"Press a foam roller or a pillow against the wall using your forearms.\",\"1\":\"Tie a resistance band around your wrists.\",\"2\":\"Maintaining the press take your arms slightly apart from each other against the resistance.\",\"3\":\"Slide the foam roller/pillow upwards pushing out from the shoulders to reach as high as you can.\"}', NULL, 1, '2022-07-13 08:56:06', '2022-07-13 08:56:06'),
(264, 'Shoulder abductor eccentrics with front raises', 'exercise', '{\"0\":\"   /                   \",\"1\":\"               \",\"2\":\"         \",\"3\":\"           \"}', '{\"0\":\"Stand straight with a dumbbell/weight in your hand and squeeze your shoulder blades together.\",\"1\":\"Keep your elbow straight and raise your hand straight over head from the front.\",\"2\":\"Turn to the opposite side keeping the arm stable.\",\"3\":\"Lower your arm sideways in a controlled manner.\"}', NULL, 1, '2022-07-13 11:53:45', '2022-07-13 11:53:45'),
(265, 'Shoulder Backward raises with dumbbell', 'exercise', '{\"0\":\"   /                   \",\"1\":\"                   \",\"2\":\"          \"}', '{\"0\":\"Stand straight with a dumbbell/weight in your hand and squeeze your shoulder blades together.\",\"1\":\"Keep your elbow straight and raise your hand behind as high as you can without bending forward from the trunk.\",\"2\":\"Lower the arm down in the same arc in a controlled manner.\"}', NULL, 1, '2022-07-13 11:55:48', '2022-07-13 11:55:48'),
(266, 'Shoulder Banded L', 'exercise', '{\"0\":\"                     \",\"1\":\"           \",\"2\":\"                               \"}', '{\"0\":\"Fix both elbows against the trunk while bending your elbow at 90 degrees and hold a resistance band.\",\"1\":\"Keep the band length of your wrist to elbow distance.\",\"2\":\"Pinching both your shoulder blades together and rotate your arms outwards maintaining the elbow and trunk contact.\"}', NULL, 1, '2022-07-13 11:57:06', '2022-07-13 11:57:06'),
(267, 'Shoulder Banded W', 'exercise', '{\"0\":\"                 \",\"1\":\"              \",\"2\":\"                         \"}', '{\"0\":\"Hold a band in hands with the band length of your wrist to elbow distance.\",\"1\":\"Bend your elbows and shoulders so that your wrists are at your eye level.\",\"2\":\"While pinching your shoulder blades, rotate both arms outwards opening up your chest.\"}', NULL, 1, '2022-07-13 11:59:00', '2022-07-13 11:59:00'),
(268, 'Shoulder Banded T', 'exercise', '{\"0\":\"                   \",\"1\":\"           \",\"2\":\"                                \"}', '{\"0\":\"Hold a band in your hands keeping the band length just short of your arm length.\",\"1\":\"Raise your shoulders in front at your chest level.\",\"2\":\"While pinching your shoulder blades, take your shoulders apart from each other maintaining the level against the resistance from the band.\"}', NULL, 1, '2022-07-13 12:00:16', '2022-07-13 12:00:16'),
(269, 'Shoulder banded L', 'exercise', '{\"0\":\"                     \",\"1\":\"           \",\"2\":\"                               \"}', '{\"0\":\"Fix both elbows against the trunk while bending your elbow at 90 degrees and hold a resistance band.\",\"1\":\"Keep the band length of your wrist to elbow distance.\",\"2\":\"Pinching both your shoulder blades together and rotate your arms outwards maintaining the elbow and trunk contact.\"}', NULL, 2, '2022-07-13 16:02:09', '2022-07-13 16:02:09'),
(270, 'Shoulder circumduction', 'exercise', '{\"0\":\"                \",\"1\":\"                                 \",\"2\":\"              \"}', '{\"0\":\"Stand sideways with your exercising arm in contact with the wall.\",\"1\":\"Lift your arm straight up overhead and continue to take it behind till you reach the starting position again in a circular motion.\",\"2\":\"Take the arm back in reverse maintaining the arc motion.\"}', NULL, 1, '2022-07-13 16:04:28', '2022-07-13 16:04:28'),
(271, 'Shoulder clocks (weight bearing)', 'exercise', '{\"0\":\"     \",\"1\":\"                  (      )   \",\"2\":\"         \",\"3\":\"       \",\"4\":\"       ,   ,  ,          \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Come up in a pushup position (on your hands and toes) with both your hands under the shoulder and feet hip width apart.\",\"2\":\"Tie a resistance band around your wrists. Keeping the non-exercising hand stable.\",\"3\":\"Take your exercising hand straight forward, diagonally forward, sideways, diagonally behind and straight behind.\"}', NULL, 1, '2022-07-13 16:06:37', '2022-07-13 16:06:37'),
(272, 'Shoulder diagonal pull apart', 'exercise', '{\"0\":\"         \",\"1\":\"       \",\"2\":\"              \",\"3\":\"-        \"}', '{\"0\":\"Keep your arms raised straight till shoulder level.\",\"1\":\"Hold a resistance band in your hands.\",\"2\":\"Pull the band apart taking the arms away from each other diagonally.\",\"3\":\"Alternatingly keep your one arm higher.\"}', NULL, 1, '2022-07-13 16:08:15', '2022-07-13 16:08:15'),
(273, 'Shoulder diagonal pull apart', 'exercise', '{\"0\":\"         \",\"1\":\"       \",\"2\":\"              \",\"3\":\"-        \"}', '{\"0\":\"Keep your arms raised straight till shoulder level.\",\"1\":\"Hold a resistance band in your hands.\",\"2\":\"Pull the band apart taking the arms away from each other diagonally.\",\"3\":\"Alternatingly keep your one arm higher.\"}', NULL, 2, '2022-07-13 16:10:16', '2022-07-13 16:10:16'),
(274, 'Shoulder diagonal pull down', 'exercise', '{\"0\":\"                   \",\"1\":\"              \",\"2\":\"                            \"}', '{\"0\":\"Hold a band in your hands keeping the band length just short of your arm length.\",\"1\":\"Raise the non-exercising arm sideways above the shoulder level.\",\"2\":\"Take your exercising arm in front of the opposite shoulder and from this position pull the band towards your exercising side foot. \"}', NULL, 1, '2022-07-13 16:11:51', '2022-07-13 16:11:51'),
(275, 'Shoulder drivers', 'exercise', '{\"0\":\"          \",\"1\":\"              \",\"2\":\"                             \"}', '{\"0\":\"Tie a resistance band around your wrist.\",\"1\":\"Keeping the elbows straight, raise both your hands up to shoulder level.\",\"2\":\"Slightly push apart both your arms against resistance and rotate in a circular manner as if turning a wheel.\"}', NULL, 1, '2022-07-13 16:12:58', '2022-07-13 16:12:58');
INSERT INTO `mst_instructions` (`instruction_id`, `instruction_name`, `instruction_type`, `instruction_description_hindi`, `instruction_description_english`, `instruction_description_marathi`, `isActive`, `created_at`, `updated_at`) VALUES
(276, 'Shoulder flutters', 'exercise', '{\"0\":\"          \",\"1\":\"         \",\"2\":\"                 \",\"3\":\"     \"}', '{\"0\":\"Tie a resistance band around your wrist.\",\"1\":\"Lift both your hands up straight overhead.\",\"2\":\"Do this while pushing your arms apart against resistance and relaxing making an oscillating movement.\",\"3\":\"Lower them in the same manner.\"}', NULL, 1, '2022-07-13 16:14:29', '2022-07-13 16:14:29'),
(277, 'Shoulder Clocks- On wall', 'exercise', '{\"0\":\"           \",\"1\":\"             \",\"2\":\"         \",\"3\":\"       \",\"4\":\"         ,    ,  ,         \"}', '{\"0\":\"Stand with both your hands on the wall at shoulder level.\",\"1\":\"Slightly lean ahead on the wall.\",\"2\":\"Tie a resistance band around your wrists.\",\"3\":\"Keeping the non-exercising hand stable.\",\"4\":\"Take your exercising hand straight upward, diagonally upward, sideways, diagonally down and straight downward.\"}', NULL, 1, '2022-07-14 04:13:09', '2022-07-14 04:13:09'),
(278, 'Shoulder Forward elevation with rotator cuff activation', 'exercise', '{\"0\":\"    \",\"1\":\"         \",\"2\":\"               \",\"3\":\"           ,                     \",\"4\":\"             \"}', '{\"0\":\"Stand straight.\",\"1\":\"Tie a resistance band around the exercising arms wrist.\",\"2\":\"Pull the band towards the opposite side creating inward resistance to shoulder.\",\"3\":\"Squeeze your shoulder blades together, keep your elbow straight and raise your hand straight over head from the front while maintaining the resistance throughout the movement.\",\"4\":\"Lower the arm down in the same arc in a controlled manner without reducing the resistance.\"}', NULL, 1, '2022-07-14 04:15:19', '2022-07-14 04:15:19'),
(279, 'Shoulder Front raises- Dumbbell', 'exercise', '{\"0\":\"   /                  \",\"1\":\"              \",\"2\":\"         \"}', '{\"0\":\"Stand straight holding a dumbbell/weight in your hand and squeeze your shoulder blades together.\",\"1\":\"Keep your elbow straight and raise your hand straight over head from the front.\",\"2\":\"Lower the arm down in the same arc in a controlled manner.\"}', NULL, 1, '2022-07-14 04:16:47', '2022-07-14 04:16:47'),
(280, 'Shoulder Lateral raise- Banded', 'exercise', '{\"0\":\"                     \",\"1\":\"            \",\"2\":\"         \",\"3\":\"         \"}', '{\"0\":\"Stand straight with a resistance band in your hand and keep the length of the band of an arm distance.\",\"1\":\"Squeeze your shoulder blades together.\",\"2\":\"Raise your arm sideways above your shoulder level.\",\"3\":\"Lower the arm down in the same arc in a controlled manner.\"}', NULL, 1, '2022-07-14 04:18:43', '2022-07-14 04:18:43'),
(281, 'Shoulder lateral raises- Dumbbell', 'exercise', '{\"0\":\"   /                  \",\"1\":\"               \",\"2\":\"         \"}', '{\"0\":\"Stand straight and squeeze your shoulder blades together.\",\"1\":\"Keep your elbow straight and raise your hand sideways over your head straight up.\",\"2\":\"Lower the arm down in the same arc in a controlled manner.\"}', NULL, 1, '2022-07-14 04:20:17', '2022-07-14 04:20:17'),
(282, 'Shoulder PNF(D2)- banded', 'exercise', '{\"0\":\"           \",\"1\":\"                     \",\"2\":\"          ,                            \"}', '{\"0\":\"Stand straight. Hold a resistance band in both your hands.\",\"1\":\"Take the exercising arm towards your opposite arm by rotating it inwards and bending elbow.\",\"2\":\"Now take your exercising arm above your head by straightening the elbow, lifting the arm sideways upwards and rotating the arm outwards against the resistance.\"}', NULL, 1, '2022-07-14 04:22:05', '2022-07-14 04:22:05'),
(283, 'Shrugs', 'exercise', '{\"0\":\"     \",\"1\":\"                   \"}', '{\"0\":\"Stand/sit straight.\",\"1\":\"Bring both your shoulders back and lift the shoulder blades up without lifting your arm.\"}', NULL, 2, '2022-07-14 04:23:30', '2022-07-14 04:23:30'),
(284, 'Shrugs', 'exercise', '{\"0\":\"     \",\"1\":\"                   \"}', '{\"0\":\"Stand/sit straight.\",\"1\":\"Bring both your shoulders back and lift the shoulder blades up without lifting your arm.\"}', NULL, 1, '2022-07-14 04:24:21', '2022-07-14 04:24:21'),
(285, 'Shrugs- Overhead', 'exercise', '{\"0\":\"     \",\"1\":\"      \",\"2\":\"               \"}', '{\"0\":\"Stand/sit straight.\",\"1\":\"Raise both your arms straight up.\",\"2\":\"While lifting your shoulders upwards try to reach towards the ceiling with your hands.\"}', NULL, 1, '2022-07-14 04:27:58', '2022-07-14 04:27:58'),
(286, 'Serratus punches', 'exercise', '{\"0\":\"           (             ) \",\"1\":\"                  \",\"2\":\"              \",\"3\":\"       ,           \"}', '{\"0\":\"Stand in a split stance position (opposite foot ahead of the arm you are exercising).\",\"1\":\"Hold a resistance band in your hand and attach the other end somewhere behind at the chest level.\",\"2\":\"Push your arm ahead from the shoulder as if punching ahead.\",\"3\":\"When your arm is completely straight, try and reach ahead more pushing out from the shoulder.\"}', NULL, 1, '2022-07-14 05:12:16', '2022-07-14 05:12:16'),
(287, 'Side plank', 'exercise', '{\"0\":\"          \",\"1\":\"              \",\"2\":\"          (     )                                \"}', '{\"0\":\"Lie down sideways on the exercising side.\",\"1\":\"Tuck your belly button in and upwards tightening your core.\",\"2\":\"Lift your body up sideways with support on the exercising side forearm (elbow below the shoulder) and the outer side foot of the exercising side.\",\"3\":\"Keep the body in an inclined line and maintain the position.\"}', NULL, 1, '2022-07-15 03:17:59', '2022-07-15 03:17:59'),
(288, 'Side plank- Dynamic', 'exercise', '{\"0\":\"          \",\"1\":\"              \",\"2\":\"          (     )                                \",\"3\":\"      - \"}', '{\"0\":\"Lie down sideways on the exercising side.\",\"1\":\"Tuck your belly button in and upwards tightening your core.\",\"2\":\"Lift your body up sideways with support on the exercising side forearm (elbow below the shoulder) and the outer side foot of the exercising side.\",\"3\":\"Keep the body in an inclined line and maintain the position.\",\"4\":\"Take your waist up and down in a controlled manner.\"}', NULL, 1, '2022-07-15 03:18:47', '2022-07-15 03:18:47'),
(289, 'Side plank on knees', 'exercise', '{\"0\":\"          \",\"1\":\"              \",\"2\":\"          (     )                      \",\"3\":\"             \"}', '{\"0\":\"Lie down sideways on the exercising side.\",\"1\":\"Tuck your belly button in and upwards tightening your core.\",\"2\":\"Lift your body up sideways with support on the exercising side forearm (elbow below the shoulder) and the outer side of the knee and foot of the exercising side.\",\"3\":\"Keep the body in an inclined line and maintain the position.\"}', NULL, 1, '2022-07-15 03:20:05', '2022-07-15 03:20:05'),
(290, 'Side plank on knees dynamic', 'exercise', '{\"0\":\"          \",\"1\":\"              \",\"2\":\"          (     )                      \",\"3\":\"             \",\"4\":\"      - \"}', '{\"0\":\"Lie down sideways on the exercising side.\",\"1\":\"Tuck your belly button in and upwards tightening your core.\",\"2\":\"Lift your body up sideways with support on the exercising side forearm (elbow below the shoulder) and the outer side of the knee and foot of the exercising side.\",\"3\":\"Keep the body in an inclined line and maintain the position.\",\"4\":\"Take your waist up and down in a controlled manner.\"}', NULL, 1, '2022-07-15 03:20:52', '2022-07-15 03:20:52'),
(291, 'Sleepers stretch', 'exercise', '{\"0\":\"          \",\"1\":\"    -     \",\"2\":\"                          \",\"3\":\"          \"}', '{\"0\":\"Lie down on the shoulder you want to stretch.\",\"1\":\"Keep the shoulder and elbow in a 90-90 position.\",\"2\":\"Gently press down your arm with other hand to rotate the shoulder inwards till you feel a comfortable stretch.\",\"3\":\"Do not let the shoulder lift off the bed. Hold the stretch.\"}', NULL, 1, '2022-07-15 05:01:38', '2022-07-15 05:01:38'),
(292, 'Slump double ended tensioners', 'exercise', '{\"0\":\" /              -\",\"1\":\"i)            \",\"2\":\"ii)        \",\"3\":\"iii)        \",\"4\":\"iv)               \"}', '{\"0\":\"Sit on a chair/edge of the bed. Come in starting position by following this order-\",\"1\":\"i) Hold both your hands behind the lower back.\",\"2\":\"ii) Slouch your back.\",\"3\":\"iii) Pull up your ankle towards the ceiling.\",\"4\":\"iv) Now simultaneously press your chin on your chest and straighten your knee.\"}', NULL, 1, '2022-07-15 05:04:47', '2022-07-15 05:04:47'),
(293, 'Slump sliders', 'exercise', '{\"0\":\" /              -\",\"1\":\"i)            \",\"2\":\"ii)        \",\"3\":\"iii)      \",\"4\":\"iv)       \",\"5\":\"v)          \"}', '{\"0\":\"Sit on a chair/edge of the bed. Come in starting position by following this order-\",\"1\":\"i) Hold both your hands behind the lower back.\",\"2\":\"ii) Slouch your back.\",\"3\":\"iii) Touch your chin to chest.\",\"4\":\"iv) Pull up your ankle towards the ceiling.\",\"5\":\"v) Now simultaneously lift your head to look up and straighten your knee.\"}', NULL, 1, '2022-07-15 05:07:33', '2022-07-15 05:07:33'),
(294, 'Slump stretch', 'exercise', '{\"0\":\" /              -\",\"1\":\"i)            \",\"2\":\"ii)        \",\"3\":\"iii)      \",\"4\":\"iv)       \",\"5\":\"v)     \"}', '{\"0\":\"Sit on a chair/edge of the bed. Come in starting position by following this order-\",\"1\":\"i) Hold both your hands behind the lower back.\",\"2\":\"ii) Slouch your back.\",\"3\":\"iii) Touch your chin to chest.\",\"4\":\"iv) Pull up your ankle towards the ceiling.\",\"5\":\"v) Straighten your knee.\"}', NULL, 1, '2022-07-15 05:08:55', '2022-07-15 05:08:55'),
(295, 'Snap downs', 'exercise', '{\"0\":\"    \",\"1\":\"        \",\"2\":\"                 (        ) \"}', '{\"0\":\"Stand straight.\",\"1\":\"Lift both your hands towards the ceiling.\",\"2\":\"Quickly swing down your arms and bend down from the knees keeping the back straight (as if you are winding up for a jump).\"}', NULL, 1, '2022-07-15 05:11:37', '2022-07-15 05:11:37'),
(296, 'Spot Jogging', 'exercise', '{\"0\":\"             \",\"1\":\"             \",\"2\":\"                            \"}', '{\"0\":\"Stand straight with your feet hip width apart.\",\"1\":\"Tighten your core by pulling your belly button in and upwards, lift your feet only an inch or two off the ground, hopping on alternate foot.\",\"2\":\"Move your arms as you jog on one spot.\"}', NULL, 1, '2022-07-16 05:48:39', '2022-07-16 05:48:39'),
(297, 'Spot Marching- High knees', 'exercise', '{\"0\":\"    \",\"1\":\"             \",\"2\":\"           \"}', '{\"0\":\"Stand straight.\",\"1\":\"Keep your arms bent halfway at the elbow, palms facing down.\",\"2\":\"Lift up alternate knees high to touch respective palms.\"}', NULL, 1, '2022-07-16 05:50:09', '2022-07-16 05:50:09'),
(298, 'Squat- lateral walk', 'exercise', '{\"0\":\"             \",\"1\":\"   /      \",\"2\":\"                      \",\"3\":\"          ,   \"}', '{\"0\":\"Stand straight with feet shoulder width apart.\",\"1\":\"Wear a loop band/tie a resistance band around the thighs.\",\"2\":\"Push outwards the resistance opening up the knees. \",\"3\":\"Bend down hinging your hips and knees keeping the back straight.\",\"4\":\"Maintaining the tension in the band and holding the squat position, walk sideways.\"}', NULL, 1, '2022-07-16 06:00:58', '2022-07-16 06:00:58'),
(299, 'Squat- Bulgarian split', 'exercise', '{\"0\":\"  //     \",\"1\":\"              //    \",\"2\":\"              \",\"3\":\"     /           \"}', '{\"0\":\"Stand straight with a table/bench/chair behind.\",\"1\":\"Bend your non-exercising leg from the knee and place its foot behind on the table/bench/chair.\",\"2\":\"Bend down hinging your hip and knee keeping the back straight.\",\"3\":\"Avoid letting your knee moving too much inward/outward or too much ahead of the toes.\"}', NULL, 1, '2022-07-16 06:05:44', '2022-07-16 06:05:44'),
(300, 'Squats', 'exercise', '{\"0\":\"          \",\"1\":\"           \",\"2\":\"   -              \"}', '{\"0\":\"Stand with feet shoulder width apart.\",\"1\":\"Bend down hinging your hips and knees keeping the back straight.\",\"2\":\"Avoid letting your knees moving towards each other or going too ahead of the toes.\"}', NULL, 1, '2022-07-16 06:09:32', '2022-07-16 06:09:32'),
(301, 'Squats- sumo', 'exercise', '{\"0\":\"            \",\"1\":\"            \",\"2\":\"          \"}', '{\"0\":\"Stand with your feet wide apart and legs rotated outwards.\",\"1\":\"Bend down with a hinge in your hips and knees keeping the back straight.\",\"2\":\"Do not let the knees cross the feet while squatting.\"}', NULL, 1, '2022-07-16 06:11:32', '2022-07-16 06:11:32'),
(302, 'Squat with abductor activation', 'exercise', '{\"0\":\"           \",\"1\":\"   /      \",\"2\":\"           \",\"3\":\"            \",\"4\":\"          \"}', '{\"0\":\"Stand tall with feet shoulder width apart. \",\"1\":\"Wear a loop band/tie a resistance band around the thighs.\",\"2\":\"Push outwards the resistance band opening up the knees.\",\"3\":\"Bend down hinging your hips and knees keeping the back straight.\",\"4\":\"Do not let the knees cross the feet while squatting.\"}', NULL, 1, '2022-07-16 06:14:14', '2022-07-16 06:14:14'),
(303, 'Squat with adductor activation', 'exercise', '{\"0\":\"   //   \",\"1\":\"           \",\"2\":\"     //             \"}', '{\"0\":\"Stand straight holding a roller/ball/pillows between your thighs.\",\"1\":\"Bend down with a hinge in your hips and knees keeping the back straight.\",\"2\":\"Keep pressing on the roller/ball/pillows throughout the exercise and do not let the knees cross the feet.\"}', NULL, 1, '2022-07-16 06:15:28', '2022-07-16 06:15:28'),
(304, 'Squat with heel lift', 'exercise', '{\"0\":\"                       \",\"1\":\"     2-5       \",\"2\":\"       ,    -              \"}', '{\"0\":\"Stand straight with feet shoulder width apart.\",\"1\":\"Keep some flat object of about 2-5cms under your heel.\",\"2\":\"Keeping your hands straight ahead or on waist, bend down hinging your hips and knees keeping the back straight. \",\"3\":\"Avoid letting your knees moving towards each other or going too ahead of the toes.\"}', NULL, 1, '2022-07-16 06:16:38', '2022-07-16 06:16:38'),
(305, 'Star excursion balance training', 'exercise', '{\"0\":\"                 (       )  \",\"1\":\"                 - \",\"2\":\")  )    )     )    )  )    )   \"}', '{\"0\":\"Stand on one leg maintaining a good balance with your knee straight (avoid straightening knee more than normal). \",\"1\":\"Try to reach as far as you can with your other leg without losing balance in the following direction-\",\"2\":\"i) Front ii) Forward and out iii) Out sideways iv) Backward and outward v) Backward vi) Backward and inward vii) Forward and inward. \"}', NULL, 1, '2022-07-16 06:19:03', '2022-07-16 06:19:03'),
(306, 'Star excursion balance knee unlocked', 'exercise', '{\"0\":\"                   \",\"1\":\"                 -\",\"2\":\")  )    )     )    )  )    )   \"}', '{\"0\":\"Stand on one leg with your knee slightly bent maintaining a good balance.\",\"1\":\"Try to reach as far as you can with your other leg without losing balance in the following direction- \",\"2\":\"i) Front ii) Forward and out iii) Out sideways iv) Backward and outward v) Backward vi) Backward and inward vii) Forward and inward.\"}', NULL, 1, '2022-07-16 06:20:58', '2022-07-16 06:20:58'),
(307, 'Static Hamstrings', 'exercise', '{\"0\":\"             \",\"1\":\"            \",\"2\":\"                  \"}', '{\"0\":\"Lie down on your back and keep your feet out of the bed. \",\"1\":\"Bend your knees and hook the heel at the edge of the bed.\",\"2\":\"Tighten your muscle behind the thigh and press your heel on the edge and hold the position.\"}', NULL, 1, '2022-07-16 06:22:37', '2022-07-16 06:22:37'),
(308, 'Static Quadriceps', 'exercise', '{\"0\":\"     /    \",\"1\":\"                   \",\"2\":\"    \"}', '{\"0\":\"Lie down on your back/ Sit with your knees straight and supported.\",\"1\":\"Tighten the muscles in front of your thigh and press your knee down on the bed. \",\"2\":\"Hold this position.\"}', NULL, 1, '2022-07-16 06:23:52', '2022-07-16 06:23:52'),
(309, 'Static posterior chain', 'exercise', '{\"0\":\"      \",\"1\":\"    ,                \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Simultaneously press the back of your head, both buttocks and both the heels on the ground and hold the position.\"}', NULL, 1, '2022-07-16 06:39:07', '2022-07-16 06:39:07'),
(310, 'Straight leg raises- Bilateral', 'exercise', '{\"0\":\"     \",\"1\":\"                     \",\"2\":\"                      \",\"3\":\"            \",\"4\":\"     \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Keep your hand under tail bone for support and maintaining a neutral lower back.\",\"2\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise. \",\"3\":\"Lift up both legs straight from the hip without bending your knees.\",\"4\":\"Lower them down in a controlled manner.\"}', NULL, 1, '2022-07-16 10:51:20', '2022-07-16 10:51:20'),
(311, 'Straight leg raise stretch', 'exercise', '{\"0\":\"     \",\"1\":\"       //           \",\"2\":\"                \",\"3\":\"                 \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Hook a towel/belt/dupatta around your exercising side foot and hold the ends in your hands.\",\"2\":\"Pull both the ends towards you to pull the foot towards yourself and lift the leg straight up without bending the knee.\",\"3\":\"Take it up till you feel a comfortable stretch in the back of your leg.\"}', NULL, 1, '2022-07-16 10:52:47', '2022-07-16 10:52:47'),
(312, 'Straight leg raises- Triplanar', 'exercise', '{\"0\":\"     \",\"1\":\"                  \",\"2\":\"        ,       : i)   , ii)       iii)     \",\"3\":\"      \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Bend your non exercising knee to relax the back.\",\"2\":\"Lift the exercising leg up from the hip without bending from the knee in three directions: i) foot straight, ii) foot rotated outwards and iii) foot rotated inwards.\",\"3\":\"Lower it down in a controlled manner.\"}', NULL, 1, '2022-07-16 10:55:32', '2022-07-16 10:55:32'),
(313, 'Straight leg raises- Unilateral', 'exercise', '{\"0\":\"     \",\"1\":\"                  \",\"2\":\"                   \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Bend your non exercising knee to relax the back.\",\"2\":\"Lift the exercising leg straight up from the hip without bending from the knee and lower it down in a controlled manner.\"}', NULL, 1, '2022-07-16 10:56:15', '2022-07-16 10:56:15'),
(314, 'Sub occipital muscle rolling', 'exercise', '{\"0\":\"                /         \",\"1\":\"                       \",\"2\":\"                      \"}', '{\"0\":\"Lie down on your back with a tennis/lacrosse ball under your head at hairline level at the targeted side.\",\"1\":\"Tuck your chin inwards to flatten the neck and relax alternatively while applying pressure. \",\"2\":\"Next tuck your chin inwards and slightly rotate your neck to the sides alternatively.\"}', NULL, 1, '2022-07-16 10:57:31', '2022-07-16 10:57:31'),
(315, 'Sub occipital muscles stretch', 'exercise', '{\"0\":\"     (     )                              \",\"1\":\"    \"}', '{\"0\":\"Use your hand to tuck your chin inwards and with the other hand, bend the head down to stretch the small muscles at the base of your head (hairline).\",\"1\":\"Hold the position.\"}', NULL, 1, '2022-07-16 10:58:38', '2022-07-16 10:58:38'),
(316, 'Supination ROM with overpressure', 'exercise', '{\"0\":\"      //       \",\"1\":\"               //              \"}', '{\"0\":\"Hold a stick/pipe/dumbbell pointing up in the hand of the targeted side.\",\"1\":\"Pull the stick/pipe/dumbbell with your other hand to make your palm face up and give slight over pressure at end range.\"}', NULL, 1, '2022-07-16 10:59:59', '2022-07-16 10:59:59'),
(317, 'Supinators isometrics', 'exercise', '{\"0\":\"       \",\"1\":\"      (  ) \",\"2\":\"                      (   )     \",\"3\":\"        \"}', '{\"0\":\"Rest both forearms on top of a table.\",\"1\":\"Hold your fists together (prayer position).\",\"2\":\"Try to turn your exercising hand outwards (palms face up) while resisting with the non-exercising hand with equal force.\",\"3\":\"Adjust the forces so that no movement should happen.\"}', NULL, 1, '2022-07-16 11:01:37', '2022-07-16 11:01:37'),
(318, 'Supraspinatus strengthening (Empty Can)', 'exercise', '{\"0\":\"    \",\"1\":\"             (      )          \",\"2\":\"          ()       \",\"3\":\"                        \"}', '{\"0\":\"Stand straight.\",\"1\":\"Hold a resistance band in your exercising hand and keep the other end (shoulder to palm length distance) under your same side foot.\",\"2\":\"Rotate the hand inwards so that your little finger faces diagonally outwards.\",\"3\":\"Squeeze your shoulder blades together and lift your hand diagonally forward away from the body.\"}', NULL, 1, '2022-07-16 11:02:35', '2022-07-16 11:02:35'),
(319, 'Wrist supinators eccentric strengthening', 'exercise', '{\"0\":\" /         ,     ,            \",\"1\":\"              \",\"2\":\"       -     /    \",\"3\":\"    /             \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported, thumb facing up and palm facing inward.\",\"1\":\"Hold one end of a dumbbell or neck end of a bottle in your hand. \",\"2\":\"Slowly and in a controlled manner lower the dumbbell/bottle rotating the wrist outward. \",\"3\":\"Use your non-exercising hand to again make the dumbbell/bottle straight for the next repetition.\"}', NULL, 1, '2022-07-16 11:27:32', '2022-07-16 11:27:32'),
(320, 'Wrist pronators eccentric strengthening', 'exercise', '{\"0\":\" /         ,     ,            \",\"1\":\"              \",\"2\":\"       -     /    \",\"3\":\"    /             \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported, thumb facing up and palm facing inward.\",\"1\":\"Hold one end of a dumbbell or neck end of a bottle in your hand. \",\"2\":\"Slowly and in a controlled manner lower the dumbbell/bottle rotating the wrist inward. \",\"3\":\"Use your non-exercising hand to again make the dumbbell/bottle straight for the next repetition.\"}', NULL, 1, '2022-07-16 11:28:56', '2022-07-16 11:28:56'),
(321, 'Swimmers exercise', 'exercise', '{\"0\":\"     \",\"1\":\"          \",\"2\":\"                    \",\"3\":\"                 \",\"4\":\"                  \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Keep both your hands on the tail bone.\",\"2\":\"Pinch your shoulder blades together and lift up your arms towards the ceiling. \",\"3\":\"Maintaining the arm level bring them over your head without bending the elbows in an arc. \",\"4\":\"Return to starting position without leaving the pinch.\"}', NULL, 1, '2022-07-16 11:43:40', '2022-07-16 11:43:40'),
(322, 'Thoracic extension mobilization', 'exercise', '{\"0\":\"        \",\"1\":\"        / \",\"2\":\"         /         \",\"3\":\"    /      \"}', '{\"0\":\"Lie down on your back with both knees bent. \",\"1\":\"Keep a roller/bolster under your upper back.\",\"2\":\"Arch your back on it to touch your head to the ground.\",\"3\":\"Keep changing the level of your upper back on the roller/bolster.\"}', NULL, 1, '2022-07-16 13:19:09', '2022-07-16 13:19:09'),
(323, 'Thumb- Finkelstein stretch', 'exercise', '{\"0\":\"             \",\"1\":\"            \",\"2\":\"            \",\"3\":\"                          \"}', '{\"0\":\"Keep your hand pointing forward with thumb facing up.\",\"1\":\"Bring your thumb in such that it touches the palm.\",\"2\":\"Bend your fingers to make a fist over it.\",\"3\":\"Bend your wrist downwards towards the little finger till a comfortable stretch over your thumb and wrist.\"}', NULL, 1, '2022-07-16 13:20:57', '2022-07-16 13:20:57'),
(324, 'Thumb wipers', 'exercise', '{\"0\":\"       \",\"1\":\"           \",\"2\":\"           \"}', '{\"0\":\"Keep your wrist and fingers straight.\",\"1\":\"Bring your thumb in such that it touches the palm.\",\"2\":\"Take your thumb in and out in an arc.\"}', NULL, 1, '2022-07-16 13:21:54', '2022-07-16 13:21:54'),
(325, 'Tibialis posterior activation- Single leg standing', 'exercise', '{\"0\":\"                         \",\"1\":\"                   \",\"2\":\"              \",\"3\":\"            \"}', '{\"0\":\"Put one end of resistance band under your great toe and pull the other end diagonally upwards with the other hand. \",\"1\":\"Lift up the arch of the foot by attempting to rotating the foot inwards against the ground. \",\"2\":\"Lift your opposite leg up and stand on your exercising leg.\",\"3\":\"Do not let the resistance band slip from under your great toe.\"}', NULL, 1, '2022-07-16 13:25:55', '2022-07-16 13:25:55'),
(326, 'Tibialis posterior activation with reach downs', 'exercise', '{\"0\":\"          \",\"1\":\"                        \",\"2\":\"                  \",\"3\":\"               \",\"4\":\"            \",\"5\":\"                     \"}', '{\"0\":\"Keep some object of about knee height in front of you.\",\"1\":\"Put one end of resistance band under your great toe and pull the other end diagonally upwards with the other hand. \",\"2\":\"Lift up the arch of the foot by attempting to rotating the foot inwards against the ground. \",\"3\":\"Lift your opposite leg up and stand on your exercising leg.\",\"4\":\"Do not let the resistance band slip from under your great toe.\",\"5\":\"Try and touch the object by reaching down from your exercising hand while maintaining the arch.\"}', NULL, 1, '2022-07-16 13:33:33', '2022-07-16 13:33:33'),
(327, 'Tibialis posterior banded strengthening', 'exercise', '{\"0\":\"          \",\"1\":\"        /   \",\"2\":\"            \"}', '{\"0\":\"Sit with the exercising leg crossed on other leg.\",\"1\":\"Attach a weight cuff/band just below your toes.\",\"2\":\"Try to Rotate and move your foot upwards as to try and look at your sole. \"}', NULL, 1, '2022-07-16 13:34:34', '2022-07-16 13:34:34'),
(328, 'Toe walking', 'exercise', '{\"0\":\"    \",\"1\":\"      \",\"2\":\"            \"}', '{\"0\":\"Stand Straight.\",\"1\":\". Rise up lifting your heels. \",\"2\":\"Maintain this position and walk forwards only on your toes.\"}', NULL, 1, '2022-07-16 13:47:27', '2022-07-16 13:47:27'),
(329, 'Trapezius stretch', 'exercise', '{\"0\":\"                      \",\"1\":\"                          \",\"2\":\"   \"}', '{\"0\":\"Press the same side shoulder down with your hand and neck sideways with the opposite hand. \",\"1\":\"Try to press your head down trying to touch opposite side ear to the shoulder till you feel a comfortable stretch in your neck.\",\"2\":\"Hold the position.\"}', NULL, 1, '2022-07-16 13:49:58', '2022-07-16 13:49:58'),
(330, 'Pacing', 'adjunct', '{\"0\":\"              \",\"1\":\"        \",\"2\":\"      \",\"3\":\"          \"}', '{\"0\":\"Plan your activities in advanced and take breaks during activities.\",\"1\":\"Break down longer tasks into shorter ones.\",\"2\":\"Take rest when you feel tired.\",\"3\":\"Work at a slower pace and do not rush through the activities.\"}', NULL, 1, '2022-07-17 05:57:03', '2022-07-17 05:57:03'),
(331, 'Pacing', 'adjunct', '{\"0\":\"              \",\"1\":\"        \",\"2\":\"      \",\"3\":\"          \"}', '{\"0\":\"Plan your activities in advanced and take breaks during activities.\",\"1\":\"Break down longer tasks into shorter ones.\",\"2\":\"Take rest when you feel tired.\",\"3\":\"Work at a slower pace and do not rush through the activities.\"}', NULL, 2, '2022-07-17 05:58:12', '2022-07-17 05:58:12'),
(332, 'Triceps- Kickbacks', 'exercise', '{\"0\":\"     (          )  \",\"1\":\"         /           \",\"2\":\"                  \"}', '{\"0\":\"Come in on all fours position (hands below shoulder and knees below hips).\",\"1\":\"Tie/hold a weight or tie a resistance band in front and hold it in the targeted hand. \",\"2\":\"Slightly extend your arm from the shoulder and straighten your elbow against the resistance.\"}', NULL, 1, '2022-07-18 02:57:02', '2022-07-18 02:57:02'),
(333, 'Triceps strengthening- Overhead', 'exercise', '{\"0\":\"        \",\"1\":\"      \",\"2\":\"               \",\"3\":\"     ,        \"}', '{\"0\":\"Stand straight or sit unsupported. \",\"1\":\"Raise your arm overhead. \",\"2\":\"Keep your elbow bent as much as you can so that the elbow points the ceiling. \",\"3\":\"With a weight in your hand, straighten your elbow against the resistance.\"}', NULL, 1, '2022-07-18 03:02:26', '2022-07-18 03:02:26'),
(334, 'Triceps strengthening- Supine', 'exercise', '{\"0\":\"             \",\"1\":\"              \",\"2\":\"              \"}', '{\"0\":\"Lie down on the back and bend your elbow as much as you can.\",\"1\":\"Lift your arm straight up from the shoulder so that the elbow points towards the ceiling.\",\"2\":\"Hold a weight in your hand and straighten your elbow against the resistance.\"}', NULL, 1, '2022-07-18 03:05:56', '2022-07-18 03:05:56'),
(335, 'Thoracic Rotations- Sitting', 'exercise', '{\"0\":\"          \",\"1\":\"                   \",\"2\":\"    \"}', '{\"0\":\"Sit straight with both hands behind your head. \",\"1\":\"Turn to one side using your trunk and try to look behind without rotating your neck.\",\"2\":\"Repeat the same on the other side.\"}', NULL, 1, '2022-07-18 03:25:59', '2022-07-18 03:25:59'),
(336, 'Trunk lateral flexors activation', 'exercise', '{\"0\":\"             \",\"1\":\"                 \",\"2\":\"                     \",\"3\":\"      \"}', '{\"0\":\"- Stand straight. Keep your feet hip width apart.\",\"1\":\"Keep both your hands by the side with palms touching the outer aspect of thighs.\",\"2\":\"Sliding down your palm on the outer thigh, bend sideways from your trunk towards the non-exercising side in a controlled manner and hold the position. \",\"3\":\"Come back up in the same way.\"}', NULL, 1, '2022-07-18 04:26:51', '2022-07-18 04:26:51'),
(337, 'Trunk lateral flexors dynamic stretch', 'exercise', '{\"0\":\"             \",\"1\":\"                        \",\"2\":\"     ,              ,                   \",\"3\":\"  \"}', '{\"0\":\"- Stand straight. Keep your feet hip width apart.\",\"1\":\"Raise the arm of the same side you want to stretch sideways above and beyond your head reaching the opposite side. \",\"2\":\"Maintain this position, bend sideways to the opposite side till you feel a comfortable stretch in your trunk and come back up.\",\"3\":\"Repeat on the other side.\"}', NULL, 1, '2022-07-18 04:31:17', '2022-07-18 04:31:17'),
(338, 'Trunk twists', 'exercise', '{\"0\":\"            \",\"1\":\"        \",\"2\":\" ,                  ( ) \",\"3\":\"   \"}', '{\"0\":\"Stand straight with feet shoulder width apart.\",\"1\":\"Keep both your hands at chest level.\",\"2\":\"Rotate your trunk sideways (turn behind) to look back without moving your neck, hips and legs.\",\"3\":\"Repeat on the other side.\"}', NULL, 1, '2022-07-18 05:03:39', '2022-07-18 05:03:39'),
(339, 'Trunk lateral bending Mobility- Bilateral', 'exercise', '{\"0\":\"             \",\"1\":\"                 \",\"2\":\"                       \",\"3\":\"   \"}', '{\"0\":\"Stand straight. Keep your feet hip width apart.\",\"1\":\"Keep both your hands by the side with palms touching the outer aspect of thighs.\",\"2\":\"Sliding down your palm on the outer thigh, bend sideways from your trunk towards the non-exercising side in a controlled manner and come back up in the same way.\",\"3\":\"Repeat on the other side.\"}', NULL, 1, '2022-07-18 05:08:49', '2022-07-18 05:08:49'),
(340, 'Static pects- Clavicular fibers', 'exercise', '{\"0\":\"        \",\"1\":\"          ,      \",\"2\":\"               \"}', '{\"0\":\"Stand facing the wall. \",\"1\":\"Lift your arm sideways till shoulder level palm facing in towards the wall. \",\"2\":\"Press your palm inwards against the wall and hold this position.\"}', NULL, 1, '2022-07-18 09:46:57', '2022-07-18 09:46:57'),
(341, 'Static pects- Sternal fibers', 'exercise', '{\"0\":\"        \",\"1\":\"           ,     \",\"2\":\"        ,           \"}', '{\"0\":\"Stand facing the wall. \",\"1\":\"Lift your arm sideways above shoulder level palm facing in towards the wall. \",\"2\":\"Press your palm diagonally downward towards your opposite leg against the wall and hold this position.\"}', NULL, 1, '2022-07-18 09:48:00', '2022-07-18 09:48:00'),
(342, 'Toe raises', 'exercise', '{\"0\":\"        \",\"1\":\"                  \"}', '{\"0\":\"Stand with your back against the wall.\",\"1\":\"Raise your toes upwards towards the ceiling pushing yourself diagonally back and up.\"}', NULL, 1, '2022-07-18 09:53:03', '2022-07-18 09:53:03'),
(343, 'Trunk lateral flexors banded strengthening', 'exercise', '{\"0\":\"    \",\"1\":\"                                 \",\"2\":\"                      \"}', '{\"0\":\"Stand straight.\",\"1\":\"Keep your feet shoulder width apart and put a band under your non-exercising side leg and hold it in the non-exercising hand.\",\"2\":\"Bend sideways from your trunk towards the exercising side in a controlled manner against resistance and come back up.\"}', NULL, 1, '2022-07-18 12:11:17', '2022-07-18 12:11:17'),
(344, 'Ulnar nerve double ended tensioner', 'exercise', '{\"0\":\"    \",\"1\":\"               \",\"2\":\"             ,                       \",\"3\":\"                 \"}', '{\"0\":\"Stand straight.\",\"1\":\"Lift your arms sideways slightly to take it away from your trunk. \",\"2\":\"Rotate the arm inward, bend your elbow completely and pull your wrist towards your body with the other hand to touch the back side of your fingers to the shoulder.\",\"3\":\"Raise your arm sideways while simultaneously bending your head sideways towards the opposite side.\"}', NULL, 1, '2022-07-18 12:13:10', '2022-07-18 12:13:10'),
(345, 'Ulnar nerve sliders', 'exercise', '{\"0\":\"    \",\"1\":\"               \",\"2\":\"             ,                       \",\"3\":\"                    \"}', '{\"0\":\"Stand straight. \",\"1\":\"Lift your arms sideways slightly to take it away from your trunk.\",\"2\":\"Rotate the arm inward, bend your elbow completely and pull your wrist towards your body with the other hand to touch the back side of your fingers to the shoulder. \",\"3\":\"Raise your arm sideways while simultaneously bending your head sideways towards the same side.\"}', NULL, 1, '2022-07-18 12:14:36', '2022-07-18 12:14:36'),
(346, 'Upper back segmental extension', 'exercise', '{\"0\":\"                          \",\"1\":\" ,                \",\"2\":\"                \"}', '{\"0\":\"Lie down on your stomach with head facing down.\",\"1\":\"Tuck your chin inwards lifting your head off the ground. Next, pinch both your shoulder blades together.\",\"2\":\"Extend your upper back lifting your chest off the ground.\"}', NULL, 1, '2022-07-18 12:17:03', '2022-07-18 12:17:03');
INSERT INTO `mst_instructions` (`instruction_id`, `instruction_name`, `instruction_type`, `instruction_description_hindi`, `instruction_description_english`, `instruction_description_marathi`, `isActive`, `created_at`, `updated_at`) VALUES
(347, 'VMO strengthening- Supine', 'exercise', '{\"0\":\"             /    \",\"1\":\"        ,              (  ) \"}', '{\"0\":\"Lie down with a folded pillow/roller under your exercising sides knee.\",\"1\":\"Tighten the muscles in front of your thigh to press your knee down while simultaneously raising the heel off the ground (straightening the knee).\"}', NULL, 1, '2022-07-19 09:13:32', '2022-07-19 09:13:32'),
(348, 'VMO strengthening- Single leg standing', 'exercise', '{\"0\":\"                \",\"1\":\"       \",\"2\":\"            \",\"3\":\"       \"}', '{\"0\":\"Stand on your exercising leg. Keep the knee slightly bent.\",\"1\":\"Attach a resistance band around the knee.\",\"2\":\"Attach/ask someone to hold the band in the front of the knee.\",\"3\":\"Straighten your knee against the resistance.\"}', NULL, 1, '2022-07-19 09:16:03', '2022-07-19 09:16:03'),
(349, 'VMO strengthening- Standing', 'exercise', '{\"0\":\"              \",\"1\":\"    \",\"2\":\"       \",\"3\":\"            \",\"4\":\"       \"}', '{\"0\":\"Stand in split stance with your exercising leg in front of the other.\",\"1\":\"Keep the knee slightly bent.\",\"2\":\"Attach a band around the knee. \",\"3\":\"Attach/ask someone to hold the band in the front.\",\"4\":\"Straighten your knee against the resistance.\"}', NULL, 1, '2022-07-19 09:18:02', '2022-07-19 09:18:02'),
(350, 'Wall Angels', 'exercise', '{\"0\":\"               \",\"1\":\"                 \",\"2\":\"       \",\"3\":\"                                \",\"4\":\"                      \"}', '{\"0\":\"Squat down with support from the wall till your hips are slightly higher than the knees. \",\"1\":\"Keep your back straight against the wall. \",\"2\":\"Lift your arms sideways and rotate your hand outwards so that your elbow and back of the hand touch the wall. \",\"3\":\"Slide your hands upwards and try to reach to the ceiling maintaining the squat position.\"}', NULL, 1, '2022-07-19 09:21:39', '2022-07-19 09:21:39'),
(351, 'Wall sits', 'exercise', '{\"0\":\"          \",\"1\":\"                      \",\"2\":\"          -      \",\"3\":\"       \"}', '{\"0\":\"Stand with your back supporting the wall.\",\"1\":\"Keep both your feet forward shoulder width apart and slide down the wall so that your hip and knee are at 90-90 degrees.\",\"2\":\"Keep your entire back in contact with the wall throughout the motion.\"}', NULL, 1, '2022-07-19 09:23:47', '2022-07-19 09:23:47'),
(352, 'Wall sits with abductor activation', 'exercise', '{\"0\":\"          \",\"1\":\"            \",\"2\":\"              \",\"3\":\"                  -     \",\"4\":\"          \",\"5\":\"       \"}', '{\"0\":\"Stand with your back supporting the wall.\",\"1\":\"Tie a resistance band above your knee. \",\"2\":\"Keep both your feet forward shoulder width apart and slide down the wall so that your hip and knee are at 90-90 degrees.\",\"3\":\"Slightly push out both your knees against the resistance. \",\"4\":\"Keep your entire back in contact with the wall throughout the motion.\"}', NULL, 1, '2022-07-19 09:26:14', '2022-07-19 09:26:14'),
(353, 'Wall sits with adductor activation', 'exercise', '{\"0\":\"          \",\"1\":\"     //    \",\"2\":\"    \",\"3\":\"//                 \",\"4\":\"          -      \",\"5\":\"       \"}', '{\"0\":\"Stand with your back supporting the wall and a ball/roller/folded pillow between your knees. \",\"1\":\"Keep both your feet forward.\",\"2\":\"Slide down the wall so that your hip and knee are at 90-90 degrees while pressing into the ball/roller/pillow. \",\"3\":\"Keep your entire back in contact with the wall throughout the motion.\"}', NULL, 1, '2022-07-19 09:28:56', '2022-07-19 09:28:56'),
(354, 'Wall Push up plus', 'exercise', '{\"0\":\"          \",\"1\":\"                 \",\"2\":\"    \",\"3\":\"           \",\"4\":\"                    \",\"5\":\"                   \"}', '{\"0\":\"Keep your hands on the wall in line with the shoulders. \",\"1\":\"Take both your hands outwards one palm distance from the starting position. \",\"2\":\"Lean towards the wall. \",\"3\":\"Bend the elbows to touch your chest to the wall. \",\"4\":\"Push yourself away from the wall, with minimum to no activation of your back and leg muscles. \",\"5\":\"At the farthest position when the arms are completely straight, try and push more by pushing forward the shoulders.\"}', NULL, 1, '2022-07-19 09:47:41', '2022-07-19 09:47:41'),
(355, 'Wall push ups', 'exercise', '{\"0\":\"          \",\"1\":\"                 \",\"2\":\"    \",\"3\":\"           \",\"4\":\"                    \"}', '{\"0\":\"Keep your hands on the wall in line with the shoulders. \",\"1\":\"Take both your hands outwards one palm distance from the starting position. \",\"2\":\"Lean towards the wall. \",\"3\":\"Bend the elbows to touch your chest to the wall. \",\"4\":\"Push yourself away from the wall, with minimum to no activation of your back and leg muscles. \"}', NULL, 1, '2022-07-19 09:51:38', '2022-07-19 09:51:38'),
(356, 'Workplace Ergonomics', 'adjunct', '{\"0\":\"    -                            //                             (  ) \",\"1\":\"    -                  (          ) \",\"2\":\"     -                   \",\"3\":\"      -          -             (    )           --     (       ,              ) \",\"4\":\"- -                \",\"5\":\"-                    \",\"6\":\" -             \"}', '{\"0\":\"Get a good chair- Head supported. Back support. Arm rest so elbows are at 90. Chair lumbar support/pillow/towel roll kept at lower back. Feet supported on ground at 90. Seat tilt to keep the pelvis is a neutral position.\",\"1\":\"Change the task pattern- Avoid repetitive motions. Keep changing the the way you perform a certain task (turning completely with legs instead of twisting back).\",\"2\":\"Practice lifting techniques- Lift heavy loads from your knees and not from your back. Avoid excessively straining your back.\",\"3\":\"Rest your neck and eyes- Reduce screen glare. Put blue light filters in breaks. Keep screens clean and 25 inches (arms length) away. Keep your screen at eye level. Follow 20-20-20 rule (every 20 min after screen time, look away for 20 seconds at something that is 20 feet away).\",\"4\":\"Breaks- Take frequent breaks. Do not sit idle at one spot or in one posture for more than 30 mins.\",\"5\":\"Posture- Avoid awkward working postures. Sit erect and keep changing posture to avoid muscle fatigue.\",\"6\":\"Lighting- Work in a well-lit environment. Incorporate natural light as much as you can.\"}', NULL, 1, '2022-07-19 12:06:10', '2022-07-19 12:06:10'),
(357, 'Wrist Extensors Eccentric strengthening', 'exercise', '{\"0\":\" /          ,          \",\"1\":\"        \",\"2\":\"            /              \",\"3\":\"        /               \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported and palm facing down.\",\"1\":\"Take your wrist as high up as you can.\",\"2\":\"Transfer a weight/one end of a dumbbell to the exercising hand and lower it down in a controlled fashion. \",\"3\":\"Take the weight/one end of a dumbbell back in the non-exercising hand at the lowest point and again raise the exercising wrist up for the next repetition.\"}', NULL, 1, '2022-07-20 06:35:58', '2022-07-20 06:35:58'),
(358, 'Wrist Extensors isometric strengthening', 'exercise', '{\"0\":\"                \",\"1\":\"                    \",\"2\":\"        \"}', '{\"0\":\"Rest your forearm on top of a table with your palm facing down. \",\"1\":\"Try to lift your hand up from the wrist while resisting with the non-exercising hand with equal force.\",\"2\":\"Adjust the forces so that no movement should happen.\"}', NULL, 1, '2022-07-20 06:38:54', '2022-07-20 06:38:54'),
(359, 'Wrist Extensors strengthening', 'exercise', '{\"0\":\" /          ,          \",\"1\":\"    /                    \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported and palm facing down. \",\"1\":\"Hold a weight/one end of a dumbbell in your hand and bend your wrist upward starting from bottom most position.\"}', NULL, 1, '2022-07-20 06:39:56', '2022-07-20 06:39:56'),
(360, 'Wrist Extensor stretch', 'exercise', '{\"0\":\"                  / \",\"1\":\"                \",\"2\":\"                    \"}', '{\"0\":\"Stand/sit with your arms raised in front at shoulder levels with palms facing down.\",\"1\":\"Let the wrist of targeted side drop down towards the ground.\",\"2\":\"Pull it further towards you with the other hand till you feel a comfortable stretch in your forearm.\"}', NULL, 1, '2022-07-20 06:41:24', '2022-07-20 06:41:24'),
(361, 'Wrist supinators eccentric strengthening', 'exercise', '{\"0\":\" /         ,     ,           \",\"1\":\"              \",\"2\":\"       -     /    \",\"3\":\"    /             \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported, thumb facing up and palm facing inward.\",\"1\":\"Hold one end of a dumbbell or neck end of a bottle in your hand. \",\"2\":\"Slowly and in a controlled manner lower the dumbbell/bottle rotating the wrist outward. \",\"3\":\"Use your non-exercising hand to again make the dumbbell/bottle straight for the next repetition.\"}', NULL, 2, '2022-07-20 06:43:22', '2022-07-20 06:43:22'),
(362, 'Wrist Flexors Eccentric strengthening', 'exercise', '{\"0\":\" /          ,         \",\"1\":\"        \",\"2\":\"            /              \",\"3\":\"        /               \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported and palm facing up. \",\"1\":\"Take your wrist as high up as you can. \",\"2\":\"Transfer a weight/one end of a dumbbell to the exercising hand and lower it down in a controlled fashion. \",\"3\":\"Take the weight/one end of a dumbbell back in the non-exercising hand at the lowest point and again raise the exercising wrist up for the next repetition.\"}', NULL, 1, '2022-07-20 06:45:47', '2022-07-20 06:45:47'),
(363, 'Wrist flexors isometric strengthening', 'exercise', '{\"0\":\"               \",\"1\":\"                    \",\"2\":\"        \"}', '{\"0\":\"Rest your forearm on top of a table with your palm facing up.\",\"1\":\"Try to lift your hand up from the wrist while resisting with the non-exercising hand with equal force.\",\"2\":\"Adjust the forces so that no movement should happen.\"}', NULL, 1, '2022-07-20 06:47:26', '2022-07-20 06:47:26'),
(364, 'Wrist Flexor strengthening', 'exercise', '{\"0\":\" /          ,          \",\"1\":\"    /                    \"}', '{\"0\":\"Support your forearm on a table/armrest with wrist unsupported and your palm facing up. \",\"1\":\"Hold a weight/one end of a dumbbell in your hand and bend your wrist upwards starting from bottommost position.\"}', NULL, 1, '2022-07-20 06:48:54', '2022-07-20 06:48:54'),
(365, 'Wrist Flexor stretch', 'exercise', '{\"0\":\"                  /  \",\"1\":\"                \",\"2\":\"                    \"}', '{\"0\":\"Stand/sit with your arms raised in front at shoulder levels with palms facing up.\",\"1\":\"Let the wrist of targeted side drop down towards the ground. \",\"2\":\"Pull it further towards you with the other hand till you feel a comfortable stretch in your forearm.\"}', NULL, 1, '2022-07-20 06:50:11', '2022-07-20 06:50:11'),
(366, 'Wrist radial deviators Isometric strengthening', 'exercise', '{\"0\":\"                \",\"1\":\"                      \",\"2\":\"        \"}', '{\"0\":\"Rest your forearm on top of a table with your palm facing up.\",\"1\":\"Try to bend your wrist away from the body while resisting with the non-exercising hand with equal force.\",\"2\":\"Adjust the forces so that no movement should happen.\"}', NULL, 1, '2022-07-20 06:51:47', '2022-07-20 06:51:47'),
(367, 'Wrist ulnar deviators isometric strengthening', 'exercise', '{\"0\":\"                \",\"1\":\"                     \",\"2\":\"        \"}', '{\"0\":\"Rest your forearm on top of a table with your palm facing up.\",\"1\":\"Try to bend your hand towards your body from the wrist while resisting with the non-exercising hand with equal force.\",\"2\":\"Adjust the forces so that no movement should happen.\"}', NULL, 1, '2022-07-20 06:52:41', '2022-07-20 06:52:41'),
(368, 'Wrist radial deviators strengthening', 'exercise', '{\"0\":\" /          ,          \",\"1\":\"    /                 \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported and thumb facing up. \",\"1\":\"Hold a weight in your hand and move your wrist towards the ceiling starting from the bottommost position.\"}', NULL, 1, '2022-07-20 06:53:43', '2022-07-20 06:53:43'),
(369, 'Wrist ulnar deviators strengthening', 'exercise', '{\"0\":\" /          ,          \",\"1\":\"    /                 \"}', '{\"0\":\"Support your forearm on a table/armrest with your wrist unsupported and thumb facing down. \",\"1\":\"Hold a weight in your hand and move your wrist towards the ceiling starting from the bottommost position.\"}', NULL, 1, '2022-07-20 06:54:43', '2022-07-20 06:54:43'),
(370, 'Hip abduction ROM supine', 'exercise', '{\"0\":\"     \",\"1\":\"             \"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Slide your leg sideways without bending or rotating your leg and bring it back.\"}', NULL, 1, '2022-07-26 08:46:47', '2022-07-26 08:46:47'),
(371, 'Single arm push press', 'exercise', '{\"0\":\"      \",\"1\":\"                 \",\"2\":\"                \",\"3\":\"            \"}', '{\"0\":\"Come in kneeling position (on both your knees).\",\"1\":\"Keep the leg opposite to your exercising arm forward on the sole of foot.\",\"2\":\"Bend your elbow and hold a weight in your exercising arm in front of the shoulder.\",\"3\":\"Raise the arm straight up towards the ceiling straightening your elbow.\"}', NULL, 1, '2022-07-26 09:13:01', '2022-07-26 09:13:01'),
(372, 'Scooting on bed (Side to Side)', 'exercise', '{\"0\":\"        \",\"1\":\"        \",\"2\":\"              \",\"3\":\"     \"}', '{\"0\":\"Sit on a bed with your feet unsupported.\",\"1\":\"Keep both your hands to the side.\",\"2\":\"Lift up your body with support of your hands and shift sideways.\",\"3\":\"Alternate between both the sides.\"}', NULL, 1, '2022-07-26 09:35:35', '2022-07-26 09:35:35'),
(373, 'Diaphragmatic breathing supine- resisted', 'exercise', '{\"0\":\"          \",\"1\":\"       \",\"2\":\"               (          ) \",\"3\":\"       -  \"}', '{\"0\":\"Lie down on the back keeping both your knees bent.\",\"1\":\"Keep some light weight on your stomach.\",\"2\":\"Breathe in through your nose and try to fill air under your belly (Make the weight on your stomach rise up).\",\"3\":\"Breathe out slowly through you mouth relaxing the stomach.\"}', NULL, 1, '2022-07-26 09:45:52', '2022-07-26 09:45:52'),
(374, 'Asthma Care', 'adjunct', '{\"0\":\"              \",\"1\":\" - \",\"2\":\"      \",\"3\":\"          \",\"4\":\"    ,     (        )\",\"5\":\"        ,    \",\"6\":\"                    \",\"7\":\"  ,       \"}', '{\"0\":\"Identify and try to avoid the triggers.\",\"1\":\"Keep the surrounding clean.\",\"2\":\"Wear clean masks where required.\",\"3\":\"Do not perform high intensity exercise in cold and dry weather.\",\"4\":\"Breathe through your nose and not through your mouth (it helps warm the air)\",\"5\":\"Always keep your reliever inhaler near you even while exercising.\",\"6\":\"Stop smoking and always check your food for trigger ingredients.\",\"7\":\"Have a healthy diet, and regular safe exercise.\"}', NULL, 1, '2022-07-28 06:49:25', '2022-07-28 06:49:25'),
(375, 'Neck strengthening gravity resisted- Extensors', 'exercise', '{\"0\":\"                \",\"1\":\"       \"}', '{\"0\":\"Lie down on your stomach upon a bed with your head unsupported out of the bed.\",\"1\":\"Lift your head up towards the ceiling.\"}', NULL, 1, '2022-07-28 09:49:31', '2022-07-28 09:49:31'),
(376, 'Neck strengthening gravity resisted- Flexors', 'exercise', '{\"0\":\"                \",\"1\":\"       \"}', '{\"0\":\"Lie down on your back upon a bed with your head unsupported out of the bed.\",\"1\":\"Lift your head up towards the ceiling.\"}', NULL, 1, '2022-07-28 09:51:09', '2022-07-28 09:51:09'),
(377, 'Neck strengthening gravity resisted- Lateral flexors', 'exercise', '{\"0\":\"                     \",\"1\":\"       \"}', '{\"0\":\"Lie down on the side opposite to your exercising side upon a bed with your head unsupported out of the bed.\",\"1\":\"Lift your head up towards the ceiling.\"}', NULL, 1, '2022-07-28 09:53:41', '2022-07-28 09:53:41'),
(378, 'Neck strengthening gravity resisted- Rotators', 'exercise', '{\"0\":\"                     \",\"1\":\"               \",\"2\":\"          \"}', '{\"0\":\"Lie down on the side opposite to your exercising side upon a bed with your head unsupported out of the bed.\",\"1\":\"Keep your head rotated towards your non exercising side looking towards the ground.\",\"2\":\"Now rotate your head to look towards the ceiling.\"}', NULL, 1, '2022-07-28 09:54:52', '2022-07-28 09:54:52'),
(379, 'Diabetic foot care', 'adjunct', '{\"0\":\"   /    /          \",\"1\":\"             \",\"2\":\"         (        ) \",\"3\":\"        \",\"4\":\"   \",\"5\":\"    (   )  \",\"6\":\"         \"}', '{\"0\":\"Inspect your foot daily for any wounds/cuts/blisters.\",\"1\":\"Use lukewarm water on foot instead of hot water for bathing.\",\"2\":\"Moisturize your foot daily (avoid space between toes).\",\"3\":\"Take care while cutting nails.\",\"4\":\"Do not walk barefoot.\",\"5\":\"Wear clean dry socks.\",\"6\":\"Wear cushioned and flat (evenly distributed) shoes.\",\"7\":\"Inspect your shoes from inside before wearing. \"}', NULL, 1, '2022-07-29 05:29:30', '2022-07-29 05:29:30'),
(380, 'Diabetic foot care', 'adjunct', '{\"0\":\"   /    /          \",\"1\":\"             \",\"2\":\"         (        ) \",\"3\":\"        \",\"4\":\"   \",\"5\":\"    (   )  \",\"6\":\"         \"}', '{\"0\":\"Inspect your foot daily for any wounds/cuts/blisters.\",\"1\":\"Use lukewarm water on foot instead of hot water for bathing.\",\"2\":\"Moisturize your foot daily (avoid space between toes).\",\"3\":\"Take care while cutting nails.\",\"4\":\"Do not walk barefoot.\",\"5\":\"Wear clean dry socks.\",\"6\":\"Wear cushioned and flat (evenly distributed) shoes.\",\"7\":\"Inspect your shoes from inside before wearing. \"}', NULL, 2, '2022-07-29 05:29:48', '2022-07-29 05:29:48'),
(381, 'Dyspnea relieving position', 'adjunct', '{\"0\":\"  :                             \",\"1\":\"  :                                \",\"2\":\"  : -                              \"}', '{\"0\":\"In sitting: Slouch in front with hands on knees and continue inhaling from nose and exhaling from mouth till breathlessness settles.\",\"1\":\"In Standing: Take support of wall or hold on to any surface and continue inhaling from nose and exhaling from mouth till breathlessness gets settled.\",\"2\":\"In lying down: Take two-three pillows and lie on one side and continue inhaling from nose and exhaling from mouth.\"}', NULL, 1, '2022-07-30 10:13:47', '2022-07-30 10:13:47'),
(382, 'Thoracic expansion exercise- Abduction', 'exercise', '{\"0\":\"               \",\"1\":\"           \"}', '{\"0\":\"Take both the hands up sideways while inhaling from nose.\",\"1\":\"Then bring your hands down while exhaling through the mouth.\"}', NULL, 1, '2022-07-30 10:18:35', '2022-07-30 10:18:35'),
(383, 'Thoracic expansion exercise- Flexion', 'exercise', '{\"0\":\"                            \"}', '{\"0\":\"Take both hands up from front while inhaling from nose and bring them down while exhaling from mouth.\"}', NULL, 1, '2022-07-30 10:20:09', '2022-07-30 10:20:09'),
(384, 'Breathing exercise resisted- Apical', 'exercise', '{\"0\":\"  \",\"1\":\"                   \",\"2\":\"          ,          \",\"3\":\"      \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Wrap a resistance band around the area between neck and shoulders on both the sides holding its end in both the hands.\",\"2\":\"Inhale from your nose against the bands resistance trying to fill air under the area of the band.\",\"3\":\"Relax and exhale through the mouth.\"}', NULL, 1, '2022-07-30 11:00:57', '2022-07-30 11:00:57'),
(385, 'Breathing exercise resisted- Lateral costal', 'exercise', '{\"0\":\"  \",\"1\":\"               \",\"2\":\"          ,             \",\"3\":\"      \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Wrap a resistance band around chest holding its end in both the hands. \",\"2\":\"Inhale from your nose trying to fill the outer side of chest with air against the bands resistance.\",\"3\":\"Relax and exhale through the mouth.\"}', NULL, 1, '2022-07-30 11:02:35', '2022-07-30 11:02:35'),
(386, 'Multi-stage inspiratory breathing', 'exercise', '{\"0\":\"  \",\"1\":\"          \",\"2\":\"         \",\"3\":\"            \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Breathe in through your nose and hold for a second.\",\"2\":\"Breathe in more through your nose and again hold.\",\"3\":\"Take another breath in and finally breathe out through your mouth.\"}', NULL, 1, '2022-07-30 13:06:00', '2022-07-30 13:06:00'),
(387, 'End inspiratory sniff', 'exercise', '{\"0\":\"  \",\"1\":\"     \",\"2\":\"                       \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Take a deep breath in through your nose.\",\"2\":\"At the end hold for a second and take another sniff in before you breathe out completely through your mouth.\"}', NULL, 1, '2022-07-30 13:12:47', '2022-07-30 13:12:47'),
(388, 'Rotator cuff External rotators isometric walk- 0', 'exercise', '{\"0\":\"         \",\"1\":\"             \",\"2\":\"                     \",\"3\":\"              \",\"4\":\"       -         \"}', '{\"0\":\"Tie a resistance band somewhere at your elbow level.\",\"1\":\"Stand sideways with your exercising arm on opposite side of band.\",\"2\":\"Fix the elbow against the trunk while bending your elbow at 90 degrees and hold the band.\",\"3\":\"Maintaining the position walk sideways to generate tension in the band.\",\"4\":\"Hold this position and walk back to relieve the tension.\"}', NULL, 1, '2022-08-04 17:06:25', '2022-08-04 17:06:25'),
(389, 'Wand Shoulder ROM exercise- Flexion', 'exercise', '{\"0\":\"          \",\"1\":\"      \",\"2\":\"                 \",\"3\":\"                \"}', '{\"0\":\"Stand with your back against the wall.\",\"1\":\"Hold a stick in both your hands.\",\"2\":\"Perform the following movement using active efforts from both your arms keeping your trunk steady.\",\"3\":\"Lift the Stick straight upwards above your head to try and touch the wall.\"}', NULL, 1, '2022-08-05 05:31:43', '2022-08-05 05:31:43'),
(390, 'Wand Shoulder ROM exercise- Abduction', 'exercise', '{\"0\":\"      \",\"1\":\"                        \"}', '{\"0\":\"Hold a stick in both your hands.\",\"1\":\"Lift the exercising arm sideways above head and assist with the non-exercising arm by pushing the stick.\"}', NULL, 1, '2022-08-05 05:35:02', '2022-08-05 05:35:02'),
(391, 'Wand Shoulder ROM exercise- External rotation 90-90', 'exercise', '{\"0\":\"          ,                 \",\"1\":\"                    \",\"2\":\"                        \"}', '{\"0\":\"Lift your affected arm sideways till shoulder level with elbow bent midway, fingers pointing forwards and palm facing down.\",\"1\":\"Hook the stick around your forearm so that stick passes in front of your palms and rests on top of your elbow.\",\"2\":\"Hold the elbow end of the stick with other hand and pull it forwards to make the stick straight.\"}', NULL, 1, '2022-08-05 05:38:29', '2022-08-05 05:38:29'),
(392, 'Wand Shoulder ROM exercise- Internal rotation Functional', 'exercise', '{\"0\":\"                  \",\"1\":\"            \",\"2\":\"        \"}', '{\"0\":\"Hold the stick behind your lower back with the affected side hand.\",\"1\":\"Hold the stick behind near your head with the other hand.\",\"2\":\"Pull the stick towards the ceiling.\"}', NULL, 1, '2022-08-05 05:42:42', '2022-08-05 05:42:42'),
(393, 'Supraspinatus Strengthening (Full can)', 'exercise', '{\"0\":\"    \",\"1\":\"             (      )          \",\"2\":\"              \",\"3\":\"                        \"}', '{\"0\":\"Stand straight.\",\"1\":\"Hold a resistance band in your exercising hand and keep the other end (shoulder to palm length distance) under your same side foot.\",\"2\":\"Rotate the hand outwards so that your thumb faces diagonally outwards.\",\"3\":\"Squeeze your shoulder blades together and lift your hand diagonally forward and away from the body.\"}', NULL, 1, '2022-08-07 05:16:54', '2022-08-07 05:16:54'),
(394, 'Backwards walking', 'exercise', '{\"0\":\"          \",\"1\":\"    \",\"2\":\"                         \"}', '{\"0\":\"Choose an open space with no obstacles for this exercise.\",\"1\":\"Keep your hands on the waist.\",\"2\":\"Look straight and walk backwards focusing on balance and straightening the knee completely during every step.\"}', NULL, 1, '2022-08-08 07:53:02', '2022-08-08 07:53:02'),
(395, 'Prone shoulder As', 'exercise', '{\"0\":\"      \",\"1\":\"       \",\"2\":\"          \",\"3\":\"      -                 \",\"4\":\"          \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Rest your head in a comfortable position.\",\"2\":\"Keep your arms by the side and elbows straight. \",\"3\":\"Pull the shoulder blades towards each other and in a downward direction to create a pinch between them.\",\"4\":\"Hold this position and lift up both the arms.\"}', NULL, 1, '2022-08-08 09:09:27', '2022-08-08 09:09:27'),
(396, 'Prone shoulder Ls', 'exercise', '{\"0\":\"      \",\"1\":\"       \",\"2\":\"     ,          \'L\'    \",\"3\":\"      -                 \",\"4\":\"          \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Rest your head in a comfortable position.\",\"2\":\"Keep your arms by the side, rotated out and elbows bent halfway to make a L. \",\"3\":\"Pull the shoulder blades towards each other and in a downward direction to create a pinch between them.\",\"4\":\"Hold this position and lift up both the arms.\"}', NULL, 1, '2022-08-08 09:12:30', '2022-08-08 09:12:30'),
(397, 'Prone shoulder Ws', 'exercise', '{\"0\":\"      \",\"1\":\"       \",\"2\":\"            \'W\'        \",\"3\":\"      -                \",\"4\":\"          \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Rest your head in a comfortable position.\",\"2\":\"Keep your arms sideways below your shoulder level and elbows bent halfway to make a W.\",\"3\":\"Pull the shoulder blades towards each other and in a downward direction to create a pinch between them.\",\"4\":\"Hold this position and lift up both the arms.\"}', NULL, 1, '2022-08-08 09:14:26', '2022-08-08 09:14:26'),
(398, 'Prone shoulder Is', 'exercise', '{\"0\":\"      \",\"1\":\"       \",\"2\":\"      ,        \",\"3\":\"      -                \",\"4\":\"          \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Rest your head in a comfortable position.\",\"2\":\"Keep your arms above your head, close to the ears and elbows straight.\",\"3\":\"Pull the shoulder blades towards each other and in a downward direction to create a pinch between them.\",\"4\":\"Hold this position and lift up both the arms.\"}', NULL, 1, '2022-08-08 09:15:49', '2022-08-08 09:15:49'),
(399, 'Prone shoulder Ys', 'exercise', '{\"0\":\"      \",\"1\":\"       \",\"2\":\"\'Y\'                  \",\"3\":\"      -                \",\"4\":\"          \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Rest your head in a comfortable position.\",\"2\":\"Keep your arms above your shoulder level sideways, making a Y and elbows straight.\",\"3\":\"Pull the shoulder blades towards each other and in a downward direction to create a pinch between them.\",\"4\":\"Hold this position and lift up both the arms.\"}', NULL, 1, '2022-08-08 09:19:05', '2022-08-08 09:19:05'),
(400, 'Posture correction', 'adjunct', '{\"0\":\"       \",\"1\":\"                 \",\"2\":\"     \",\"3\":\"        \",\"4\":\"                                                                     \",\"5\":\"                  \",\"6\":\"            \",\"7\":\"             \",\"8\":\"     \"}', '{\"0\":\"Stand against a wall.\",\"1\":\"Keep your feet slightly forward to avoid any contact between heels and wall.\",\"2\":\"Lean behind on the wall.\",\"3\":\"Keep your buttocks in contact with the wall.\",\"4\":\"Slide your hand between the lower back and wall. The palm should be in full contact with the wall and the opposite side with the lower back. If space is too much then tuck your tummy in and press against the wall or if the space is too less arch your lower back to achieve this.\",\"5\":\"Straighten your upper back to touch both your shoulder blades to the wall.\",\"6\":\"Look straight ahead and touch the back of your head against the wall.\",\"7\":\"Try to flatten your neck on the wall while looking straight ahead.\",\"8\":\"Perform this several times a day.\"}', NULL, 1, '2022-08-09 06:46:09', '2022-08-09 06:46:09'),
(401, 'Use Vaporizer', 'adjunct', '{\"0\":\"/     \",\"1\":\"                 \",\"2\":\"                \",\"3\":\"   -                    \",\"4\":\"              \",\"5\":\"          \",\"6\":\"        \",\"7\":\"        /        \",\"8\":\"                \"}', '{\"0\":\"Fill clean water in the container/utensil.\",\"1\":\"Add additional relievers in water or apply on nose and head only if advised by the doctor.\",\"2\":\"Place a towel on the backside of your head and neck to trap the vapor from escaping.\",\"3\":\"Keep your distance to around 8-10cms away from the vaporizer or more if you are using utensil and gas.\",\"4\":\"Plug in the vaporizer or start the heat and wait for the vapors to rise.\",\"5\":\"Breathe in and out normally through nose and mouth.\",\"6\":\"Do so till directed by your therapist.\",\"7\":\"Throw the water away once it cools down and wash the container/utensil with warm water and soap.\",\"8\":\"Dry it completely after washing before storing it for next use.\"}', NULL, 1, '2022-08-09 07:24:35', '2022-08-09 07:24:35'),
(402, 'Prone knee hangs', 'exercise', '{\"0\":\"                 \",\"1\":\"         \",\"2\":\"                \"}', '{\"0\":\"Lie down on a bed on your stomach with your leg unsupported below the thigh.\",\"1\":\"Attach a weight on the ankle of the exercising leg.\",\"2\":\"Keep the knee straight and out of the bed so as to feel a stretch in the back of the knee.\"}', NULL, 1, '2022-08-14 09:58:07', '2022-08-14 09:58:07'),
(403, 'Balloon blowing (Expiratory muscle training)', 'exercise', '{\"0\":\"     \",\"1\":\"     \",\"2\":\"                   \",\"3\":\"     \",\"4\":\" ,                    \",\"5\":\"          \",\"6\":\"           \"}', '{\"0\":\"Take a deep breath in through your nose.\",\"1\":\"Blow air out in the balloon.\",\"2\":\"Hold your breath and keep the balloon firmly attached on the lips and take a break from blowing.\",\"3\":\"Blow in the balloon again.\",\"4\":\"Again, hold your breath and keep the balloon firmly attached on the lips and take a break from blowing.\",\"5\":\"Repeat the same if possible or then relax.\",\"6\":\"Breathe normally for a few times and repeat the exercise.\"}', NULL, 1, '2022-08-14 10:13:22', '2022-08-14 10:13:22'),
(404, 'Cat camel exercise- Upper back focused', 'exercise', '{\"0\":\"     (        ) \",\"1\":\"       \",\"2\":\"                  \",\"3\":\"                    \"}', '{\"0\":\"Come in position on all fours (body supported on hands and knees).\",\"1\":\"Sit behind resting the buttocks on your heels. \",\"2\":\"Curl in your chest to hunch the upper back as high as you can.\",\"3\":\"Afterwards arch your upper back pushing your chest towards the floor as low as you can.\"}', NULL, 1, '2022-09-02 05:14:29', '2022-09-02 05:14:29'),
(405, 'Femoral nerve slider', 'exercise', '{\"0\":\"     \",\"1\":\"                  \",\"2\":\"                   \",\"3\":\"    \",\"4\":\"                           \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Come up from your lower back on both your forearms.\",\"2\":\"Hunch your upper back by curling in the chest.\",\"3\":\"Bend down your neck.\",\"4\":\"Now at the same time, extend your neck to look upwards while bending the knee to try and touch the heel to the buttock.\"}', NULL, 1, '2022-09-02 06:23:08', '2022-09-02 06:23:08'),
(406, 'Femoral nerve tensioner', 'exercise', '{\"0\":\"     \",\"1\":\"                  \",\"2\":\"                   \",\"3\":\"           \",\"4\":\"                     \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Come up from your lower back on both your forearms.\",\"2\":\"Hunch your upper back by curling in the chest.\",\"3\":\"Bend your neck behind to look up.\",\"4\":\"Now at the same time, bend down your neck while bending the knee to try and touch the heel to the buttock.\"}', NULL, 1, '2022-09-02 06:24:09', '2022-09-02 06:24:09'),
(407, 'Curl ups- oblique', 'exercise', '{\"0\":\"           \",\"1\":\"                          \",\"2\":\"       \"}', '{\"0\":\"Lie down on your back with both your knees bent.\",\"1\":\"Curl only your upper back towards your knees keeping the head stable trying to reach outside and away from your knees with both your hands.\",\"2\":\"Perform on alternate sides.\"}', NULL, 1, '2022-09-02 06:37:58', '2022-09-02 06:37:58'),
(408, 'Pelvic rocking side to side', 'exercise', '{\"0\":\"           \",\"1\":\"                  \"}', '{\"0\":\"Lie down on your back with both your knees bent.\",\"1\":\"In a controlled manner and in a short arc rotate both your legs to alternate sides.\"}', NULL, 1, '2022-09-02 07:10:34', '2022-09-02 07:10:34'),
(409, 'Squats- Mini', 'exercise', '{\"0\":\"           \",\"1\":\"           \",\"2\":\"   -               \",\"3\":\"          \"}', '{\"0\":\"Stand with feet shoulder width apart.\",\"1\":\"Bend down hinging your hips and knees keeping the back straight.\",\"2\":\"Avoid letting your knees moving towards each other or going too ahead of the toes.\",\"3\":\"Stop before reaching 90 with the hip or knees.\"}', NULL, 1, '2022-09-03 03:53:27', '2022-09-03 03:53:27'),
(410, 'Ulnar nerve stretch', 'exercise', '{\"0\":\"    \",\"1\":\"              \",\"2\":\"             ,                      \",\"3\":\"                       \"}', '{\"0\":\"Stand straight.\",\"1\":\"Lift your arms sideways slightly to take it away from your trunk.\",\"2\":\"Rotate the arm inward, bend your elbow completely and pull your wrist towards your body with the other hand to touch the back side of your fingers to the shoulder.\",\"3\":\"Raise your arm sideways till you feel a comfortable stretch through your arm/in your fingers.\"}', NULL, 1, '2022-09-03 04:01:35', '2022-09-03 04:01:35');
INSERT INTO `mst_instructions` (`instruction_id`, `instruction_name`, `instruction_type`, `instruction_description_hindi`, `instruction_description_english`, `instruction_description_marathi`, `isActive`, `created_at`, `updated_at`) VALUES
(411, 'Dolphin press', 'exercise', '{\"0\":\"                         \",\"1\":\"                    \",\"2\":\"           \",\"3\":\"                     \",\"4\":\"   \"}', '{\"0\":\"Keep both your elbows below your shoulder and support the body on your forearms and toes.\",\"1\":\"Maintain a straight spine and do not let your hips rise or drop too much.\",\"2\":\"Push your body away from the ground using your shoulders.\",\"3\":\"Now push into the ground upwards using your shoulders so as to lift the low back towards the ceiling.\",\"4\":\"Return to starting position.\"}', NULL, 1, '2022-09-24 05:44:27', '2022-09-24 05:44:27'),
(412, 'Prone leg external rotation lift offs', 'exercise', '{\"0\":\"     \",\"1\":\"                   \",\"2\":\"              \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Rotate the exercising knee outwards and rest its foot on the other thigh by folding the knee.\",\"2\":\"Keeping the contact between the heel and thigh, raise the knee off the ground.\"}', NULL, 1, '2022-09-24 05:52:06', '2022-09-24 05:52:06'),
(413, 'Shoulder Terminal flexion on wall', 'exercise', '{\"0\":\"        \",\"1\":\"             \",\"2\":\"     ,                \"}', '{\"0\":\"Stand facing a wall.\",\"1\":\"Keep both your hands up straight on the wall at a level above the chest.\",\"2\":\"Keeping your back straight, bend forwards from your hip so as the arms to be lifted above the head automatically.\"}', NULL, 1, '2022-09-24 06:00:12', '2022-09-24 06:00:12'),
(414, 'Latissimus and triceps mobilization in childs pose', 'exercise', '{\"0\":\"        (             )\",\"1\":\"       \",\"2\":\"                 \",\"3\":\"   \",\"4\":\"               \"}', '{\"0\":\"Come in a kneel sitting position (kneel and sit behind resting your buttocks on the foot).\",\"1\":\"Keep a small table in front of you.\",\"2\":\"Support both your elbows on the table and bend forwards from the back as low as you can.\",\"3\":\"Hold the position.\",\"4\":\"Bend and straighten your elbows to feel a stretch in the shoulders and back.\"}', NULL, 1, '2022-09-24 06:25:17', '2022-09-24 06:25:17'),
(415, 'Superman exercise', 'exercise', '{\"0\":\"     \",\"1\":\"          \",\"2\":\"                   \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Keep both your hands up near your head.\",\"2\":\"At the same time lift off both your hands and legs up towards the ceiling keeping your knees and elbows straight.\"}', NULL, 1, '2022-09-24 06:37:25', '2022-09-24 06:37:25'),
(416, 'Triceps stretch', 'exercise', '{\"0\":\"          \",\"1\":\"          \",\"2\":\"                        \"}', '{\"0\":\"Stand or sit keeping the back straight.\",\"1\":\"Raise your arm overhead and bend the elbow.\",\"2\":\"With the non-exercising arm pull the elbow behind till you feel a comfortable stretch behind your elbow.\"}', NULL, 1, '2022-09-24 06:47:29', '2022-09-24 06:47:29'),
(417, 'Scapular push ups on hands', 'exercise', '{\"0\":\"             \",\"1\":\"              \",\"2\":\"           \",\"3\":\"        \",\"4\":\"    ,                \"}', '{\"0\":\"Come in a position where your body is supported by your hands and toes.\",\"1\":\"Keep your hands slightly apart from shoulder width and at chest level.\",\"2\":\"Feet should be hip width apart and in line with shoulder.\",\"3\":\"Keep a neutral lower back and neck.\",\"4\":\"Keeping the arms straight, push away from the floor through your shoulders lifting the body as high as you can.\"}', NULL, 1, '2022-09-24 07:11:22', '2022-09-24 07:11:22'),
(418, 'Static deltoid', 'exercise', '{\"0\":\"            \",\"1\":\"       \",\"2\":\"      :-\",\"3\":\"i)              \",\"4\":\"ii)              \",\"5\":\"iii)              \"}', '{\"0\":\"Stand close to the wall so that the arm is in contact with the wall.\",\"1\":\"Keep your elbow bent completely. \",\"2\":\"Alternate between three positions: -\",\"3\":\"i) Push the arm outwards against the wall and hold the position.\",\"4\":\"ii) Push the arm forwards against the wall and hold the position.\",\"5\":\"iii) Push the arm backwards against the wall and hold the position.\"}', NULL, 1, '2022-09-29 06:04:48', '2022-09-29 06:04:48'),
(419, 'Static triceps', 'exercise', '{\"0\":\"        \",\"1\":\"      ,                 \",\"2\":\"             \",\"3\":\"              \"}', '{\"0\":\"Stand facing the wall.\",\"1\":\"Keeping your elbow bent midway, raise your arm up till shoulder level and support the wrist on the wall.\",\"2\":\"Push your wrist out on the wall trying to straighten the elbow.\",\"3\":\"Push with a force that no movement actually occurs at the elbow.\"}', NULL, 1, '2022-09-29 06:06:25', '2022-09-29 06:06:25'),
(420, 'Bubble blowing in water', 'exercise', '{\"0\":\"           \",\"1\":\"             \",\"2\":\"     \"}', '{\"0\":\"Take a straw and place it in a cup half filled with water.\",\"1\":\"Inhale from nose and exhale out from straw in jar making bubbles in water.\",\"2\":\"Make as much bubbles as possible.\"}', NULL, 1, '2022-09-30 08:49:33', '2022-09-30 08:49:33'),
(421, 'Thoracic expansion- Lateral bending', 'exercise', '{\"0\":\"  \",\"1\":\"               \",\"2\":\"              \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Raise the exercising side arm and place it behind your head.\",\"2\":\"Bend sideways on the opposite side while inhaling and come up while exhaling from mouth.\"}', NULL, 1, '2022-09-30 08:58:58', '2022-09-30 08:58:58'),
(422, 'Quadruped hip circles', 'exercise', '{\"0\":\"     (        ) \",\"1\":\"             \",\"2\":\"                     \",\"3\":\"                    \",\"4\":\"            \",\"5\":\"                 \",\"6\":\"        \"}', '{\"0\":\"Come in position on all fours (body supported on hands and knees).\",\"1\":\"Keep your hands under your shoulder and knees under the hip.\",\"2\":\"Pull your belly button in and upwards tightening your core and maintain it throughout the exercise.\",\"3\":\"Keeping the knee in a bent position lift your leg behind to extend your hips and take your foot towards the ceiling.\",\"4\":\"Rotate the knee outwards so that your leg is lifted sideways.\",\"5\":\"Continue the motion and bring your knee in towards your chest.\",\"6\":\"Take your knee back to the starting position.\"}', NULL, 1, '2022-09-30 09:16:44', '2022-09-30 09:16:44'),
(423, 'Toe extensions', 'exercise', '{\"0\":\"           \",\"1\":\"                   \"}', '{\"0\":\"Stand or sit favoring weight on exercising side.\",\"1\":\"Keeping the great toe on the ground try and lift off all your other toes.\"}', NULL, 1, '2022-10-25 05:40:18', '2022-10-25 05:40:18'),
(424, 'Swimmer\'s exercise (Half range)', 'exercise', '{\"0\":\"     \",\"1\":\"         \",\"2\":\"      -                \",\"3\":\"          \",\"4\":\"   ,                  \",\"5\":\"                  \"}', '{\"0\":\"Lie down on your stomach.\",\"1\":\"Keep your arms by the side and elbows straight.\",\"2\":\"Pull the shoulder blades towards each other and in a downward direction to create a pinch between them.\",\"3\":\"Hold this position and lift up both the arms.\",\"4\":\"Maintaining the arm height bring them till the shoulder level without bending the elbows in an arc.\",\"5\":\"Return to starting position without leaving the pinch.\"}', NULL, 1, '2022-10-25 05:57:29', '2022-10-25 05:57:29'),
(425, 'Great toe mobility with calf raises', 'exercise', '{\"0\":\"           \",\"1\":\"            \",\"2\":\"                        \"}', '{\"0\":\"Stand against a wall taking support for balance.\",\"1\":\"Keep a towel roll below your great toe.\",\"2\":\"Lift your heels upwards so that entire body moves up in a straight direction thus bending your great toe.\"}', NULL, 1, '2022-10-25 06:19:45', '2022-10-25 06:19:45'),
(426, 'Prone chin lifts', 'exercise', '{\"0\":\"           \",\"1\":\"              \"}', '{\"0\":\"Lie down on your stomach with head facing down.\",\"1\":\"Tuck your chin inwards lifting your head off the ground.\"}', NULL, 1, '2022-10-25 06:26:14', '2022-10-25 06:26:14'),
(427, 'Breathing exercise- Lateral costal', 'exercise', '{\"0\":\"  \",\"1\":\"         \",\"2\":\"                 \",\"3\":\"     \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Place your hands on the outer sides of your chest.\",\"2\":\"Breathe in trying to fill air under your hands expanding your chest out sideways.\",\"3\":\"Breathe out through your mouth.\"}', NULL, 1, '2022-10-25 06:46:28', '2022-10-25 06:46:28'),
(428, 'Finger tracing', 'exercise', '{\"0\":\"             \"}', '{\"0\":\"Slide down along all the fingers from tip to the base using the tip of your thumb.\"}', NULL, 1, '2022-10-28 05:08:19', '2022-10-28 05:08:19'),
(429, 'Supine Angels', 'exercise', '{\"0\":\"         \",\"1\":\"       \",\"2\":\"                                \",\"3\":\"     ,              \"}', '{\"0\":\"Lie down on your back and keep your knees folded.\",\"1\":\"Keep your back flat on the floor.\",\"2\":\"Lift your arms sideways and rotate your hand outwards so that your elbow and back of the hand touch the floor.\",\"3\":\"Maintaining contact with the floor, slide your hands above your head to straighten the arms.\"}', NULL, 1, '2022-10-28 05:18:52', '2022-10-28 05:18:52'),
(430, 'Pallof Press', 'exercise', '{\"0\":\"    \",\"1\":\"                 \",\"2\":\"            \",\"3\":\"             \",\"4\":\"-         \"}', '{\"0\":\"Stand straight. \",\"1\":\"Attach a resistance band at your chest level on the opposite side you want to exercise.\",\"2\":\"Hold the resistance band with both the hands right in front of your chest.\",\"3\":\"Without rotating your torso straighten your arms out at the chest level.\",\"4\":\"Slowly return your hands towards the chest and repeat.\"}', NULL, 1, '2022-10-28 05:30:53', '2022-10-28 05:30:53'),
(431, 'Figure 4 stretch- Dynamic seated', 'exercise', '{\"0\":\"  \",\"1\":\"              (   4    )  \",\"2\":\"                 \",\"3\":\"    \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Rotate and keep the targeted side leg on top of the other (like in a figure 4 position).\",\"2\":\"Push down the knee of the folded leg towards the ground and bend forwards from your back. \",\"3\":\"Come back up to repeat.\"}', NULL, 1, '2022-10-29 06:59:46', '2022-10-29 06:59:46'),
(432, 'Breathing exercise- Diaphragmatic', 'exercise', '{\"0\":\"  \",\"1\":\"            \",\"2\":\"                   \",\"3\":\"     \"}', '{\"0\":\"Sit comfortably.\",\"1\":\"Place your hands on top of your stomach just below your chest.\",\"2\":\"Breathe in trying to fill air under your hands expanding the stomach outwards.\",\"3\":\"Breathe out through your mouth.\"}', NULL, 1, '2022-10-29 07:06:13', '2022-10-29 07:06:13'),
(433, 'Rolling- Interscapular region', 'exercise', '{\"0\":\"         \",\"1\":\"                  /   \",\"2\":\"             \",\"3\":\"/            \"}', '{\"0\":\"Stand with your back against a wall.\",\"1\":\"Keep a lacrosse/tennis ball between your spine and shoulder blade bone of the targeted side.\",\"2\":\"Cross your arms to rest the hands on the opposite shoulders.\",\"3\":\"Move up and down from the knees maintaining pressure on the lacrosse/tennis ball.\"}', NULL, 1, '2022-10-29 07:16:51', '2022-10-29 07:16:51'),
(434, 'Rolling- Trapezius', 'exercise', '{\"0\":\"        \",\"1\":\"       \",\"2\":\"          /  \",\"3\":\"/              \",\"4\":\"               \"}', '{\"0\":\"Stand near the edge of a wall.\",\"1\":\"Bend down forwards from your hips.\",\"2\":\"Keep a lacrosse/tennis ball on the area between your neck and shoulder.\",\"3\":\"Keeping pressure on the lacrosse/tennis ball move your upper body side to side.\",\"4\":\"You can even move up and down at the sore spot for added effect.\"}', NULL, 1, '2022-10-29 07:24:54', '2022-10-29 07:24:54');
