(1, 'invoice_template', 'invoice_template', '<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n    <meta charset=\"UTF-8\">\n    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <!-- <link rel=\"stylesheet\" href=\"invoice.css\"> -->\n    <title>Invoice - WorkFitt</title>\n    <style>\n        .flexClass {\n            display: flex;\n            display: -webkit-flex;\n            -webkit-flex-direction: row;\n            -webkit-justify-content: space-between;\n        }\n\n\n        .patientInfo {\n            margin: 15px 31px;\n        }\n\n        #poweredText {\n            margin: 0px;\n            font-size: 10px;\n            letter-spacing: 1px;\n            font-weight: 500;\n        }\n\n        .compName,\n        .compHead {\n            margin: 5px;\n            font-size: 8px;\n        }\n\n        .bodyFont {\n            font-size: 13px;\n        }\n\n        #billTime {\n            margin-left: 11px;\n        }\n\n        div#dateContainer {\n            margin-top: 25px;\n        }\n\n        table {\n            border-collapse: collapse;\n        }\n\n        #billTable {\n            width: 90%;\n            text-align: center;\n            margin: auto;\n        }\n\n        .tableContainer {\n            text-align: center;\n        }\n\n        td.columnleft,\n        .columnRight {\n            text-transform: capitalize;\n            padding: 6px;\n            font-size: 13px;\n        }\n\n        .tdBorderBottom {\n            border-bottom: 1px solid black;\n        }\n\n        .tdBorderTop {\n            border-top: 1px solid black;\n        }\n\n        #sign {\n            width: 10vw;\n            height: 6vh;\n        }\n\n        .discCol {\n            text-align: right;\n        }\n\n        .tdBorderRightLeft {\n            border-right: 1px solid black;\n            border-left: 1px solid black;\n        }\n\n        p#doctorName {\n            margin: 6px;\n            font-size: 12px;\n        }\n        #footerText{\nfont-size: 12px;\n}\n\n        #doctor_logo {\n            max-width: 260px;\n            max-height: 100px;\n        }\n\n        .seprator {\n            border-right: 1px solid #665e5e;\n            margin-left: 10px;\n        }\n\n        .doctor_name_top {\n            margin:0;\n            color: #665e5e;\n            font-size: 18px;\n        }\n\n.degree_color{\n color: #665e5e;\n}\n\n\n        .doctor_details p {\n            margin: 6px 0px\n        }\n\n        .doctor_details {\n            margin-left: 15px;\n        }\n\n        .flexClass.tophead {\n            border-bottom: 1px solid black;\n        }\n    </style>\n\n</head>\n\n<body>\n    <div>\n        <div class=\"flexClass \">\n            <div class=\"flexClass templateHead\">\n                <img \n                \n                src={{doctor_logo}}\n                 id=\"doctor_logo\" />\n                <div class=\"seprator\"></div>\n                <div class=\"doctor_details\">\n                    <p class=\"doctor_name_top\">Dr. {{doctor_name}} (PT)</p>\n                    {{doctor_degree}}                    \n                    {{doctor_address}}\n                    {{doctor_mobile}}\n                    {{doctor_email}}\n                </div>\n            </div>\n            <h5 id=\"poweredText\">Powered By WorkFitt</h5>\n        </div>\n        <hr />\n\n        {{bill_invoice_to}}\n        <div class=\"patientInfo\">\n            <p>\n                <span class=\"bodyFont\"><b>Patient Name</b> - </span>\n                <span class=\"bodyFont\">{{bill_patient_name}}</span>\n            </p>\n            <p>\n                <span class=\"bodyFont\"><b>Age</b> - {{bill_patient_age}}</span>\n                <span class=\"bodyFont\"><b>Gender</b> - {{bill_patient_gender}}</span>\n            </p>\n            {{bill_patient_address}}\n            <div id=\"dateContainer\">\n                <p>\n                    <span class=\"bodyFont\"><b>Date</b> - {{bill_date_of_evaluation}}</span>\n                    <span class=\"bodyFont \" id=\"billTime\"><b>Time</b> - {{bill_time_evaluation}}</span>\n                </p>\n                <p>\n                    <span class=\"bodyFont\"><b>Bill No.</b> - {{bill_no}}</span>\n                </p>\n            </div>\n        </div>\n        <div class=\"tableContainer\">\n            <table id=\"billTable\">\n                <tbody>\n                    <tr class=\"tdBorderBottom tdBorderTop \">\n                        <td class=\"columnleft tdBorderRightLeft\" colspan=\"2\"><b>Item No.</b></td>\n                        <td class=\"columnRight tdBorderRightLeft\"> <b>Rates </b></td>\n                    </tr>\n                    <tr>\n                        <td class=\"columnleft tdBorderRightLeft\" colspan=\"2\"> Consultation Charges </td>\n                        <td class=\"columnRight tdBorderRightLeft\"> {{consultation_charge}} </td>\n                    </tr>\n                    <tr>\n                        <td class=\"columnleft tdBorderRightLeft\" colspan=\"2\"> Treatment Charges </td>\n                        <td class=\"columnRight tdBorderRightLeft\"> {{treatment_charge}} </td>\n                    </tr>\n                    <tr class=\"tdBorderBottom \">\n                        <td class=\"columnleft tdBorderRightLeft\" colspan=\"2\"> Specific Modality Charges </td>\n                        <td class=\"columnRight tdBorderRightLeft\"> {{bill_modality_charge}} </td>\n                    </tr>\n                    <tr>\n                        <td class=\"columnleft\" colspan=\"2\"></td>\n                        <td class=\"columnRight\"> {{total}} </td>\n                    </tr>\n                    {{bill_discount}}\n                    <tr>\n                        <td class=\"columnleft discCol\" colspan=\"2\"> <b>Total Amount</b> </td>\n                        <td class=\"columnRight\"> {{total_amount}} /- </td>\n                    </tr>\n                </tbody>\n            </table>\n        </div>\n\n        <div class=\"alignBottom\">\n            <hr />\n            <div class=\"flexClass\">\n                <div></div>\n                <div style=\"text-align:center\">\n<p id=\"footerText\"><b>Verified and electronically signed by</b></p>\n                    <img src={{doctor_sign}} id=\"sign\" height=\"100%\" width=\"100%\" />\n                    <p id=\"doctorName\">Dr. {{doctor_name}} (PT)</p>\n                    {{doctor_registration_number}}\n                </div>\n            </div>\n        </div>\n    </div>\n</body>\n\n</html>', 1, '2022-07-07 15:22:44', '2022-07-07 15:22:44'),
