INSERT INTO `mst_instructions` (`instruction_id`, `instruction_name`, `instruction_type`, `instruction_description_hindi`, `instruction_description_english`, `instruction_description_marathi`, `isActive`, `created_at`, `updated_at`) VALUES
(1, 'Compression', 'adjunct', '{\"0\":\"अपने थेरेपिस्ट द्वारा सुझाए गए दबाव देने वाले वस्त्र या एक पट्टी का उपयोग करके पीड़ादायी क्षेत्र पर दबाव बनाए रखें।\",\"1\":\"यदि आप कोई झुनझुनी/सुन्नता महसूस करते है या फिर दबाव के निचे का क्षेत्र पीला/अतिरिक्त लाल या स्पर्श करने के लिए ठंडा हो तो दबाव को ढीला/कम करें।\"}', '{\"0\":\"Apply compression to the affected area using a compression garment suggested by your therapist or using a bandage.\",\"1\":\"Loosen up the compression if you find any tingling/numbness or if the area distal to the compression is pale/bright red or cold to touch.\"}', NULL, 1, '2022-06-30 05:22:56', '2022-06-30 05:22:56'),
(2, 'Contrast bath', 'adjunct', '{\"0\":\"पीड़ादायी हिस्से को गुनगुने (४ मिनट) और ठंडे (१ मिनट) पानी में एक के बाद एक निर्धारित समय के लिए डालें। \",\"1\":\"प्रक्रिया को गुनगुने पानी से शुरू करें और ठंडे पानी से खत्म करें।\"}', '{\"0\":\"Insert the affected part in lukewarm (4 mins) and cold water (1 min) alternatively for specified time. \",\"1\":\"Start the procedure with lukewarm water and end with cold water.\"}', NULL, 1, '2022-06-30 05:24:33', '2022-06-30 05:24:33'),
(3, 'Crutches', 'adjunct', '{\"0\":\"पीड़ादायी हिस्से को आधार देने के लिए बैसाखी का उपयोग करे और थेरेपिस्ट ने बताये हुए ढंग से उतना ही जोर दे या बिना जोर दिए चले।\",\"1\":\"बैसाखी की ऊंचाई अपने थेरेपिस्ट द्वारा किये हुए निर्दिष्ट की रखें।\"}', '{\"0\":\"Use Crutches for support and move around with or without weight bearing on the affected part to a percentage as taught by the therapist.\",\"1\":\"Adjust the height of the crutches as specified by your therapist.\"}', NULL, 1, '2022-06-30 05:32:00', '2022-06-30 05:32:00'),
(4, 'Elevation', 'adjunct', '{\"0\":\"लेटते समय तकियों/वेज पर सहारा देकर पीड़ादायी भाग को सीने के स्तर से ऊंचा रखें।\",\"1\":\"बैठते समय भाग को बिना सहारे के लटकाए न रखें।\"}', '{\"0\":\"Keep the affected part elevated while lying down above the heart level by supporting it on pillows/wedge.\",\"1\":\"Do not keep the part hanging unsupported while sitting.\"}', NULL, 1, '2022-06-30 05:33:53', '2022-06-30 05:33:53'),
(5, 'Icing', 'adjunct', '{\"0\":\"पीड़ादायी क्षेत्र पर बर्फ लगाएं। बर्फ का पैक/बैग को घुमाते रहें ।\",\"1\":\"अगर जलन हो/भाग अत्यधिक लाल होने लगे तो कुछ देर रुकें।\"}', '{\"0\":\"Apply ice over the affected area. Keep moving the ice pack/bag.\",\"1\":\"Stop for some time if it starts burning/excessive redness.\"}', NULL, 1, '2022-06-30 05:36:05', '2022-06-30 05:36:05'),
(6, 'Heat Therapy', 'adjunct', '{\"0\":\"पीड़ादायी क्षेत्र पर गर्म पानी की थैली/हीटिंग पैड से सेंकिये। बीच बीच में थोड़ी देर ठहरना।\",\"1\":\"गर्म पानी की थैली/हीटिंग पैड के ऊपर न लेटें।\",\"2\":\"अगर जलन हो/भाग अत्यधिक लाल होने लगे तो कुछ देर रुकें।\"}', '{\"0\":\"Apply a hot water bag/heating pad over the affected area. Keep giving yourself small breaks.\",\"1\":\"Do not lie over the hot water bag/heating pad.\",\"2\":\"Stop for some time if it starts burning/excessive redness.\"}', NULL, 1, '2022-06-30 05:38:31', '2022-06-30 05:38:31'),
(7, 'Jogging', 'adjunct', '{\"0\":\"कम तीव्रता की जॉगिंग के लिए जाएं।\",\"1\":\"व्यायाम से पहले और बाद में उचित वार्म-अप और कूल-डाउन करें।\",\"2\":\"आदर्श रूप से वॉकिंग/जॉगिंग ट्रैक पर जॉगिंग करें।\",\"3\":\"थकान महसूस होने पर छोटे-छोटे ब्रेक लें।\"}', '{\"0\":\"Go for light jogging.\",\"1\":\"Perform adequate warm-up and cool-down before and after the activity.\",\"2\":\" Ideally jog on a walking/jogging track.\",\"3\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 05:43:00', '2022-06-30 05:43:00'),
(8, 'Massage', 'adjunct', '{\"0\":\"हाथों/ मसाज स्टिक से पीड़ादायी क्षेत्र की मालिश करें।\",\"1\":\"घर्षण कम करने के लिए तेल/दर्द निवारक क्रीम/लोशन लगाएं। * हड्डी के ऊपर मालिश न करें*।\",\"2\":\"केवल थेरेपिस्ट ने दिखाई गई जगह की मालिश करें।\"}', '{\"0\":\"Massage the affected area with hands/massage stick.\",\"1\":\"Apply oil/pain reliever cream/lotion to reduce friction. *Do not massage over bony area*.\",\"2\":\"Massage only the area which is demonstrated by your therapist.\"}', NULL, 1, '2022-06-30 05:45:45', '2022-06-30 05:45:45'),
(9, 'Meditation', 'adjunct', '{\"0\":\"ध्यान धारण करें। आप विशिष्ट ऑनलाइन मार्गदर्शन का उपयोग कर सकते हैं या किसी उचित वर्ग में दाखिल हो सकते हैं।\",\"1\":\"यदि थेरेपिस्ट ने कुछ आसन/ क्रियाएं न करने की सलाह दी हो तो उसका पालन करें।\"}', '{\"0\":\"Practice meditation. You can use specific online modules or join classes for the same.\",\"1\":\"Avoid certain positions/activities if advised by the therapist.\"}', NULL, 1, '2022-06-30 05:47:23', '2022-06-30 05:47:23'),
(10, 'Movement in water', 'adjunct', '{\"0\":\"थेरेपिस्ट के निर्देशानुसार पीड़ादायी हिस्से को गुनगुने पानी में डुबोएं और सभी सुरक्षित दिशा और सीमा में संचलन कीजिये।\",\"1\":\"आप अंतिम सीमा पर आरामदायक तरीके से हल्का अधिक दबाव देने के लिए शरीर के दूसरे हिस्से का उपयोग कर सकते हैं।\"}', '{\"0\":\"Immerse the part in lukewarm water and perform movements in all safe directions and range as told by the therapist.\",\"1\":\"You can use the other limb to give comfortable overpressure at the end range.\"}', NULL, 1, '2022-06-30 05:49:20', '2022-06-30 05:49:20'),
(11, 'Skipping', 'adjunct', '{\"0\":\"स्किप्पिंग का व्यायाम करें। व्यायाम से पहले और बाद में पर्याप्त वार्म-अप और कूल-डाउन करें।\",\"1\":\"थकान महसूस होने पर छोटे-छोटे ब्रेक लें।\"}', '{\"0\":\"Perform skipping activity. Have adequate warm-up and cool-down before and after the activity.\",\"1\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 05:50:15', '2022-06-30 05:50:15'),
(12, 'Cycling', 'adjunct', '{\"0\":\"थेरेपिस्ट की सलाह अनुसार साइकिल की ऊंचाई और विशेष विवरण को समायोजित करें और साइकिल का व्यायाम करें।\",\"1\":\"व्यायाम से पहले और बाद में पर्याप्त वार्म-अप और कूल-डाउन करें।\",\"2\":\"जब तक थेरेपिस्ट द्वारा निर्दिष्ट नहीं किया जाता है, तब तक केवल एक जगह पर स्थिर रहने वाली व्यायाम की साइकल ही चलाएं।\",\"3\":\"थकान महसूस होने पर छोटे-छोटे ब्रेक लें।\"}', '{\"0\":\"Adjust bicycle height and specifications according to therapist’s advice and cycle.\",\"1\":\"Perform adequate warm-up and cool-down prior to and after the activity.\",\"2\":\". Unless specified by the therapist, use only stationary exercise bike.\",\"3\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 05:57:55', '2022-06-30 05:57:55'),
(13, 'Walking Stick', 'adjunct', '{\"0\":\"चलने के लिए एक सहारे के रूप में अपने थेरेपिस्ट द्वारा निर्धारित छड़ी का प्रयोग करें।\",\"1\":\"छड़ी की ऊंचाई अपने थेरेपिस्ट द्वारा किये हुए निर्दिष्ट की रखें। \",\"2\":\"अपने थेरेपिस्ट द्वारा सुझाए गए हाथ में छड़ी को पकड़ें।\"}', '{\"0\":\"Use a walking stick prescribed by your therapist as a support to walk around.\",\"1\":\"Adjust the height of the stick as specified by your therapist.\",\"2\":\"Hold the stick on the side suggested by your therapist.\"}', NULL, 1, '2022-06-30 06:00:55', '2022-06-30 06:00:55'),
(14, 'Swimming', 'adjunct', '{\"0\":\"एरोबिक व्यायाम के लिए तैरने जाएं।\",\"1\":\"केवल थेरेपिस्ट द्वारा बताए गए स्ट्रोक ही करें।\",\"2\":\"थकान महसूस होने पर छोटे-छोटे ब्रेक लें।\"}', '{\"0\":\"Go for swimming as an aerobic activity.\",\"1\":\"Perform only the strokes advised by the therapist.\",\"2\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 06:01:58', '2022-06-30 06:01:58'),
(15, 'Walker', 'adjunct', '{\"0\":\"पीड़ादायी हिस्से को आधार देने के लिए वॉकर का उपयोग करे और थेरेपिस्ट ने बताये हुए ढंग से उतना ही जोर दे या बिना जोर दिए चले।\",\"1\":\"वॉकर की ऊंचाई अपने थेरेपिस्ट द्वारा किये हुए निर्दिष्ट की रखें।\"}', '{\"0\":\"Use a walker prescribed by your therapist to move around without or with weight bearing to a percentage on the affected area as taught by the therapist.\",\"1\":\"Adjust the height as specified by your therapist.\"}', NULL, 1, '2022-06-30 06:03:28', '2022-06-30 06:03:28'),
(16, 'Walking', 'adjunct', '{\"0\":\"चलने के लिए जाइए। व्यायाम से पहले पर्याप्त वार्म-अप करें और व्यायाम के बाद कूल-डाउन करें।\",\"1\":\"आदर्श रूप से वॉकिंग/जॉगिंग ट्रैक पर चलें।\",\"2\":\"थकान महसूस होने पर छोटे-छोटे ब्रेक लें।\"}', '{\"0\":\"Go for a walk. Have adequate warm-up prior to and cool-down after the activity.\",\"1\":\"Ideally walk on a walking/jogging track.\",\"2\":\"Take small breaks if you feel tired\"}', NULL, 1, '2022-06-30 06:04:46', '2022-06-30 06:04:46'),
(17, 'Walking in water', 'adjunct', '{\"0\":\"अपने थेरेपिस्ट की सलाह के अनुसार कमर/छाती की गहराई तक के पानी में चलें।\",\"1\":\"अपने थेरेपिस्ट द्वारा सुझाई गई दिशाओं में चलें।\",\"2\":\"थकान महसूस होने पर छोटे-छोटे ब्रेक लें।\"}', '{\"0\":\"Walk inside waist/chest deep water as per your therapist’s advice.\",\"1\":\"Walk in the directions suggested by your therapist.\",\"2\":\"Take small breaks if you feel tired.\"}', NULL, 1, '2022-06-30 06:05:32', '2022-06-30 06:05:32'),
(18, 'Bracing', 'adjunct', '{\"0\":\"ब्रेस लगाकर भाग को सहारा दें। थेरेपिस्ट के निर्देशानुसार ब्रेस पहनें और निकालें।\",\"1\":\"ब्रेस को गंदा न रखें। त्वचा को किसी भी प्रकार की हानि से बचने के लिए उस हिस्से पर उचित मात्रा में पाउडर लगाएं जहां पाउडर का उपयोग सुरक्षित है।\",\"2\":\"ध्यान रखें कि किसी भी टांके या घाव और ब्रेस के बीच कोई घर्षण न हो।\"}', '{\"0\":\"Keep the part supported with a brace. Wear and remove the brace as per direction from the therapist.\",\"1\":\"Do not keep the brace dirty. Apply sufficient powder to the part where it is safe to, to avoid any harmful skin reactions.\",\"2\":\"Take care that there is no friction between any stitches or wounds and the brace.\"}', NULL, 1, '2022-06-30 12:54:58', '2022-06-30 12:54:58'),
(19, 'Scar tissue mobilization', 'adjunct', '{\"0\":\"अपके थेरेपिस्ट के निर्देशानुसार उंगलियों का उपयोग करके ऑपरेशन/चोट के निशान के ऊपर या फिर उसके आसपास हल्की मालिश करें।\",\"1\":\"केवल उसी गहराई, दिशा और तीव्रता के अनुसार मालिश करें जैसा कि थेरेपिस्ट द्वारा सिखाया गया है और जैसे करने की सलाह दी गयी है।\",\"2\":\"यदि थेरेपिस्ट द्वारा अनुमति दी जाती है तो अपनी उंगलियों का उपयोग करके ऑपरेशन/चोट के निशान पर हल्का खिंचाव दें।\",\"3\":\"कृपया अपने थेरेपिस्ट/सर्जन को सूचित करें यदि आपको निशान क्षेत्र खुल गयी हो ऐसे लगे।\"}', '{\"0\":\"Perform light massage over or around the scar tissue using your fingers whichever is mentioned by your therapist.\",\"1\":\"Perform only to a depth, direction and intensity as taught and, in a manner, as advised by the therapist.\",\"2\":\"If allowed by the therapist perform light stretches over the scar tissue using your fingers.\",\"3\":\"Please report to your therapist/surgeon if you find any opening over the scar area.\"}', NULL, 1, '2022-07-01 05:01:23', '2022-07-01 05:01:23'),
(20, 'Adductor Copenhagen Level 1', 'exercise', '{\"0\":\"शरीर के जिस अंग को व्यायाम करने की जरुरत है, उस कर्वट पर लेटिए।\",\"1\":\"उपरी पाव को नितंब और घुटने मे मोडकर पेट के सामने रखे।\",\"2\":\"अब निचला पैर घुटना बिना मोडे सीधे छत की ओर उठाइए।\"}', '{\"0\":\"Lie down on the exercising side.\",\"1\":\"Bend the top leg from hip and knee and keep it near your stomach.\",\"2\":\"Now lift your lower leg towards the ceiling keeping the knee straight.\"}', NULL, 1, '2022-07-01 05:29:51', '2022-07-01 05:29:51'),
(21, 'Adductor Copenhagen Level 1A', 'exercise', '{\"0\":\"शरीर के जिस अंग को व्यायाम करने की जरुरत है, उस कर्वट पर लेटिए।\",\"1\":\"उपरी पाव घुटने तक कुर्सी पर रखीए।\",\"2\":\"अब निचला पैर घुटना बिना मोडे सीधे छत की ओर कुर्सी के निचले भाग तक उठाइए।\"}', '{\"0\":\"Lie down on the exercising side.\",\"1\":\"Keep your top leg on a chair supporting till just below your knee.\",\"2\":\"Lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\"}', NULL, 1, '2022-07-01 05:31:43', '2022-07-01 05:31:43'),
(22, 'Adductor Copenhagen Level 2', 'exercise', '{\"0\":\"शरीर के जिस अंग को व्यायाम करने की जरुरत है, उस कर्वट पर लेटिए। उपरी पाव घुटने तक कुर्सी पर रखीए।\",\"1\":\"जिस बाजू लेटे हो उस बाजू की कोनी कंधे के नीचे रखीए। कोनी से कलाई तक का हाथ, कुर्सी पे रखे हुए पैर ओर निचले पैर पर वजन का भार लेते हुए अपने शरीर उपर उठाइए।\",\"2\":\"अपने शरीर का भार कोनी से कलाई तक ओर कुर्सी पे रखे हुए पैर पर लेकर निचला पैर घुटना बिना मोडे सीधे छत की ओर कुर्सी के निचले भाग तक उठाइए।\"}', '{\"0\":\"Lie down on the exercising side. Keep your top leg on a chair supporting your ankle and shin bone in line with your body.\",\"1\":\"Lift up your body sideways by supporting it on your exercising side forearm (in line with your shoulder), non-exercising leg on the chair and exercising leg below the chair (side plank).\",\"2\":\"Transfer the weight to forearm and non-exercising leg and lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\"}', NULL, 1, '2022-07-01 05:34:21', '2022-07-01 05:34:21'),
(23, 'Adductor Squeeze', 'exercise', '{\"0\":\"पीठ के बल लेटकर दोनो घुटनो को मोडकर रखीए।\",\"1\":\"घुटनों के बीच तकिया / रोलर / फुटबॉल दबाकर रखीए।\",\"2\":\"तकिया / रोलर / फुटबॉल को दोनों घुटनों से दबाकर रखें।\"}', '{\"0\":\"Lie down on your back keeping both your knees bent.\",\"1\":\"Place a pillow/roller/football between your knees.\",\"2\":\"Press and hold both your knees on to the pillow/roller/football.\"}', NULL, 1, '2022-07-01 05:38:57', '2022-07-01 05:38:57'),
(24, 'Adductor Squeeze with knee to chest', 'exercise', '{\"0\":\"पीठ के बल लेटकर दोनो घुटनो को मोडकर रखीए ।\",\"1\":\"घुटनों के बीच तकिया / रोलर / फुटबॉल दबाकर रखीए।\",\"2\":\"तकिया / रोलर / फुटबॉल को दबाते हुऐ, दोनो घटनो को अपने सीने की ओर लाइये।\"}', '{\"0\":\"Lie down on your back keeping both your knees bent.\",\"1\":\"Place a pillow/roller/football between your knees.\",\"2\":\"Press both your knees on to the pillow/roller/football and try to touch your knees to chest.\"}', NULL, 1, '2022-07-01 05:46:44', '2022-07-01 05:46:44'),
(25, 'Air Cyling', 'exercise', '{\"0\":\"पीठ के बल लेटकर दोनो पैर घुटनो से मोडकर सीधा उपर उठाइये।\",\"1\":\"दोनो पैर को घुमाना जैसे कि सायकल के पहिये घुमा रहे हो।\",\"2\":\"इस व्यायाम को अधिक कठिण करने के लिए दोनो बाहे हवा मे उपर उठाकर रखीए।\"}', '{\"0\":\"Lie down on your back. Keep both arms by the side.\",\"1\":\"Lift both your legs up with your knees bent.\",\"2\":\"Imagine you are sitting on a cycle and move your legs in a circular motion as if pedaling.\",\"3\":\"To increase the difficulty, raise both arms up straight towards the sky and hold the position while pedaling.\"}', NULL, 1, '2022-07-01 05:50:39', '2022-07-01 05:50:39'),
(26, 'Alternating Shoulder taps', 'exercise', '{\"0\":\"पेट के बल लेट जाइए। अपने शरीर का भार हात और पैर पर लेकर अपना शरीर ऊपर उठाइये।\",\"1\":\"अपने हात को कंधो के नीचे और सिने के स्तर पे रखकर दो अंक के चौढाई से अंदर की ओर लाये। पैर को नितंब की दुरी और कंधो के समान अंतर पे रखे। अपनी पीठ को सीधे रखे।\",\"2\":\"अपने कोर का हिस्सा कसने के लिए अपनी नाभि को अंदर और ऊपर की और खिचीए।\",\"3\":\"अपना एक हात ऊपर उठाकर विपरित कंधे को छुनेका प्रयास करें। ये कसरत दोनों बाजू बारी बारी से करें।\"}', '{\"0\":\"Lie down on your stomach. Supporting your body on your hands and toes rise up.\",\"1\":\"Keep your hands two finger distance inwards from shoulder width and at chest level. Feet should be hip width apart and in line with shoulder. Keep a neutral lower back.\",\"2\":\"Tuck your tummy in by pulling your belly button in and upwards to tighten your core.\",\"3\":\"Lift one hand off the ground to touch the opposite shoulder. Perform the movement alternating between both the sides.\"}', NULL, 1, '2022-07-01 05:56:39', '2022-07-01 05:56:39'),
(27, 'Ankle Dorsiflexors strengthening', 'exercise', '{\"0\":\"पीठ पर लेटिए या फिर पैर फैलाकर नीचे बैठिये। \",\"1\":\"जीस तखने को व्यायाम करने की जरुरत है उस पैरों की उंगलीयों के नीचे रेंजीस्टन्स बॅन्ड बांधीए।\",\"2\":\"बॅन्ड का दुसरा छौर अपने दोनो पैरों के दिशा मे आपके शरीर से दूर बांधीए या किसी को बॅन्ड को पकडने की गुजारिश किजीए।\",\"3\":\"अपना तखना शरीर की ओर बॅन्ड के खिलाफ मोडीए।\"}', '{\"0\":\"Lie down or be in a long sitting position.\",\"1\":\"Place a band just below your toes and tie/ ask someone to hold it in line with your leg away from the body.\",\"2\":\"Pull your ankle towards your body against the resistance.\"}', NULL, 1, '2022-07-01 06:00:48', '2022-07-01 06:00:48'),
(28, 'Ankle Dorsiflexion Mobilization', 'exercise', '{\"0\":\"घुटने टेकने के स्थिती में आएं जिस पैर का व्यायाम करना है उस पैर को तलवों पर आगे रखीए।\",\"1\":\"अपनी एडी जमीन से उपर उठाये बिना अगले पैर का घुटना मोडते हुए अपने शरीर का भार आगे की तरफ लेकर जाइए।\"}', '{\"0\":\"Come in a kneeling position.\",\"1\":\"Keep the exercising leg ahead.\",\"2\":\"Lunge ahead reaching forward with your knee keeping the heel in contact with the ground.\"}', NULL, 1, '2022-07-01 06:02:35', '2022-07-01 06:02:35'),
(29, 'Ankle Dorsiflexion 3-way mobilization', 'exercise', '{\"0\":\"घुटने टेकने के स्थिती में (दोनों घुटनों के बल) आएं।\",\"1\":\"जिस पैर का व्यायाम करना है उस पैर को तलवों पर आगे रखीए।\",\"2\":\"अपनी एडी जमीन से उपर उठाये बिना अगले पैर का घुटना मोडते हुए अपने शरीर का भार i) आगे की तरफ ii) बाहरी ओर फिर iii) अंदरुनी ओर लेकर जाइए।\"}', '{\"0\":\"Come in a kneeling position.\",\"1\":\"Keep the exercising leg ahead.\",\"2\":\"Lunge ahead reaching i) forward ii) diagonally outwards iii) diagonally inwards with your knee, keeping the heel in contact with the ground.\"}', NULL, 1, '2022-07-01 06:06:45', '2022-07-01 06:06:45'),
(30, 'Ankle evertors strengthening', 'exercise', '{\"0\":\"पीठ पर लेटिए या फिर पैर फैलाकर नीचे बैठिये।\",\"1\":\"रेंजीस्टन्स बॅन्ड अपने दोनो पैरों की उंगलीयों के नीचे बांधीए या फिर जीस तखने को व्यायाम करने की जरुरत है उस पैर की उंगलीयों के नीचे बांधकर उसका दुसरा छौर अपने दुसरे पैर के दिशा मे बांधीए या किसी को बॅन्ड को पकडने की गुजारिश किजीए।\",\"2\":\"अपने पैर को दुसरे पैर के विरुध्द दिशा मे तखने से बॅन्ड के खिलाफ मोडीए।\"}', '{\"0\":\"Lie down or be in a long sitting position.\",\"1\":\"Place a band just below your toes and tie it around your other foot / ask someone to hold it. Keep it in direction of your other foot in line with your feet and parallel to the ground.\",\"2\":\"Move your ankle away from the other foot against resistance.\"}', NULL, 1, '2022-07-01 06:13:45', '2022-07-01 06:13:45'),
(31, 'Ankle invertors strengthening', 'exercise', '{\"0\":\"पीठ पर लेटिए या फिर पैर फैलाकर नीचे बैठिये।\",\"1\":\"रेंजीस्टन्स बॅन्ड अपने दोनो पैरों की उंगलीयों के नीचे बांधकर जिस पैर का व्यायाम करना है वो पैर नीचे रखकर एक जांग दुसरे जांग के उपर रखीए या फिर जीस तखने का व्यायाम करने की जरुरत है उस पैर की उंगलीयों के नीचे बांधकर उसी पैर के दिशा मे बांधीए या किसी को बॅन्ड को पकडने की गुजारिश किजीए।\",\"2\":\"अपने पैर को दुसरे पैर के दिशा मे तखने से बॅन्ड के खिलाफ मोडीए।\"}', '{\"0\":\"Lie down or be in a long sitting position with one hand on the knee.\",\"1\":\"Place a band just below your toes and tie it around your other foot keeping your feet crossed with one thigh on top of the other / ask someone to hold it in opposite direction of your other foot in line with your feet and parallel to the ground.\",\"2\":\"Move your ankle towards the great toe against resistance.\"}', NULL, 1, '2022-07-01 06:16:48', '2022-07-01 06:16:48'),
(32, 'Ankle plantarflexors open chain strengthening', 'exercise', '{\"0\":\"पीठ पर लेटिए या फिर पैर फैलाकर नीचे बैठिये।\",\"1\":\"जीस तखने को व्यायाम करने की जरुरत है उस पैर की उंगलीयों के नीचे रेंजीस्टन्स बॅन्ड बांधकर उसका दुसरा छौर अपनी कमर की दिशा मे पकडकर रखीए।\",\"2\":\"अपने तखने को कमर के विरुध्द दिशा मे बॅन्ड के खिलाफ मोडीए।\"}', '{\"0\":\"Lie down or be in a long sitting position.\",\"1\":\"Place a band just below your toes and hold it in line with your leg towards your waist.\",\"2\":\"Push your foot away from your body against the resistance.\"}', NULL, 1, '2022-07-01 06:19:29', '2022-07-01 06:19:29'),
(33, 'Ankle pumps', 'exercise', '{\"0\":\"पीठ पर लेटीए।\",\"1\":\"अपने तखनों को अपनी और मोडीये। बाद में दोनों तखने अपने शरीर से दूर पिंडली की मांसपेशियों को कसते हुऐ मोडीये।\",\"2\":\"इन दो चीजों को एक के बाद एक करते रहें।\"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Pull your ankles towards your body. Next push them away from the body squeezing your calves.\",\"2\":\"Alternate these movements.\"}', NULL, 1, '2022-07-01 06:24:26', '2022-07-01 06:24:26'),
(34, 'Ankle pumps elevated', 'exercise', '{\"0\":\"पीठ पर लेटीए।\",\"1\":\"जिस पैर को व्यायाम करना है वो पैर तखिंयों के सहारे उंचा कर के रखें। \",\"2\":\"अपने तखनों को अपनी और मोडीये। बाद में दोनों तखने अपने शरीर से दूर पिंडली की मांसपेशियों को कसते हुऐ मोडीये।\",\"3\":\"इन दो चीजों को एक के बाद एक करते रहें।\"}', '{\"0\":\"Lie down on your back.\",\"1\":\"Keep your targeted leg elevated with support of pillows/wedge.\",\"2\":\"Pull your ankles towards your body. Next push them away from the body squeezing your calves.\",\"3\":\"Alternate between these movements.\"}', NULL, 1, '2022-07-01 06:27:06', '2022-07-01 06:27:06'),
(35, 'Anterior step down', 'exercise', '{\"0\":\"अपनी कमर पर हाथ रखते हुए, एक ऊँचे मंच पर खड़े हो जाएँ (अधिमानतः एक कोने में/दीवार के पास) । \",\"1\":\"अपनी कमर को गिराए बिना (दोनों हाथों को समान स्तर पर रखते हुए) जिस पैर का व्यायाम नहीं करना है उस पैर की एड़ी को सामने की ओर रखते हुए जमीन को स्पर्श करके वापिस ऊपर की और आए।\"}', '{\"0\":\"Keeping hands on your waist, stand on a raised platform (preferably in a corner/near wall).\",\"1\":\"Without dropping your waist (maintaining both the hands at same level) touch the ground with the heel of non-exercising leg in the front and come back up.\"}', NULL, 1, '2022-07-01 06:28:21', '2022-07-01 06:28:21'),
(36, 'Adductor Copenhagen- Level 3', 'exercise', '{\"0\":\"शरीर के जिस अंग को व्यायाम करने की जरुरत है, उस कर्वट पर लेटिए। उपरी पाव का टखना कुर्सी पर रखीए।\",\"1\":\"जिस बाजू लेटे हो उस बाजू की कोनी कंधे के नीचे रखीए। कोनी से कलाई तक का हाथ, कुर्सी पे रखे हुए पैर ओर निचले पैर पर वजन का भार लेते हुए अपने शरीर उपर उठाइए।\",\"2\":\"अपने शरीर का भार कोनी से कलाई तक ओर कुर्सी पे रखे हुए पैर पर लेकर निचला पैर घुटना बिना मोडे सीधे छत की ओर कुर्सी के निचले भाग तक उठाइए।\"}', '{\"0\":\"Lie down on the exercising side. Keep your top leg on a chair supporting only your ankle in line with your body.\",\"1\":\"Lift up your body sideways by supporting it on your exercising side forearm (in line with your shoulder), non-exercising leg on the chair and exercising leg below the chair (side plank).\",\"2\":\"Transfer the weight to forearm and non-exercising leg and lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\"}', NULL, 1, '2022-07-02 07:24:23', '2022-07-02 07:24:23'),
(37, 'Adductor Copenhagen with knee drive', 'exercise', '{\"0\":\"शरीर के जिस अंग को व्यायाम करने की जरुरत है, उस कर्वट पर लेटिए। उपरी पाव का टखना कुर्सी पर रखीए।\",\"1\":\"जिस बाजू लेटे हो उस बाजू की कोनी कंधे के नीचे रखीए। कोनी से कलाई तक का हाथ, कुर्सी पे रखे हुए पैर ओर निचले पैर पर वजन का भार लेते हुए अपने शरीर उपर उठाइए।\",\"2\":\"अपने शरीर का भार कोनी से कलाई तक ओर कुर्सी पे रखे हुए पैर पर लेकर निचला पैर घुटना बिना मोडे सीधे छत की ओर कुर्सी के निचले भाग तक उठाइए।\",\"3\":\"इस स्थिति में बने रहें और निचले पैर को मोड़कर घुटने को छाती की तरफ लाने की कोशिश करें।\"}', '{\"0\":\"Lie down on the exercising side. Keep your top leg on a chair supporting only your ankle in line with your body.\",\"1\":\"Lift up your body sideways by supporting it on your exercising side forearm (in line with your shoulder), non-exercising leg on the chair and exercising leg below the chair (side plank).\",\"2\":\"Transfer the weight to forearm and non-exercising leg and lift your lower leg straight up towards the ceiling keeping the knee straight trying to touch the bottom of the chair.\",\"3\":\"Hold this position and bend the bottom leg trying to bring the knee towards the chest.\"}', NULL, 1, '2022-07-02 07:25:37', '2022-07-02 07:25:37'),
(38, 'Low back extension- Standing', 'exercise', '{\"0\":\"सीधे खडे रहिए, दोनों हात कमर पर रखींए और अपने पैरों को नितंब की चोड़ाई पर रखींए।\",\"1\":\"अपना संतुलन बनाए रख सकें ऐसी आरामदायक स्थिती में अपने पीठ से पिछे की ओर झुके।\"}', '{\"0\":\"Stand tall with both your hands on the waist and your feet hip width apart.\",\"1\":\"Bend your back behind keeping the knees straight till a comfortable position and in a position where you can keep your balance.\"}', NULL, 1, '2022-07-02 08:33:49', '2022-07-02 08:33:49'),
(39, 'Low back forward flexion- Standing', 'exercise', '{\"0\":\"सीधे खडे रहिए, दोनों हात कमर पर रखींए और अपने पैरों को नितंब की चोड़ाई पर रखींए।\",\"1\":\"अपना संतुलन बनाए रख सकें ऐसी आरामदायक स्थिती में अपने पीठ से सामने की ओर झुके।\"}', '{\"0\":\"Stand tall with both your hands on the waist and your feet hip width apart.\",\"1\":\"Bend forward and down from the back keeping the knees straight till a comfortable position and in a position where you can keep your balance.\"}', NULL, 1, '2022-07-02 08:36:14', '2022-07-02 08:36:14'),
(40, 'Balance Training- Feet together', 'exercise', '{\"0\":\"यदी आवश्यक हो तो तत्काल सहायता लेने के लिए या तो किसी दिवार के पास खडे हो जाएं या फिर किसी व्यक्ती को पास रखें।\",\"1\":\"अपने दोनों हात कमर पर रखें। अपने पैर जीतना हो सके उतना एक दुसरे के करीब रखें।\",\"2\":\"एक अच्छी दशा में अपना संतुलन बनाए हुए खडे रहीए।\"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your both feet as close to each other as possible pointing forwards.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:39:57', '2022-07-02 08:39:57'),
(41, 'Balance Training- Feet together (Eyes closed)', 'exercise', '{\"0\":\"यदी आवश्यक हो तो तत्काल सहायता लेने के लिए या तो किसी दिवार के पास खडे हो जाएं या फिर किसी व्यक्ती को पास रखें।\",\"1\":\"अपने दोनों हात कमर पर रखें। अपने पैर जीतना हो सके उतना एक दुसरे के करीब रखें।\",\"2\":\"अपनी दोनों ऑंखे बंद करके एक अच्छी दशा में अपना संतुलन बनाए हुए खडे रहीए।\"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Keep your both feet as close to each other as possible pointing forwards.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:41:56', '2022-07-02 08:41:56'),
(42, 'Balance Training- Single leg standing', 'exercise', '{\"0\":\"यदी आवश्यक हो तो तत्काल सहायता लेने के लिए या तो किसी दिवार के पास खडे हो जाएं या फिर किसी व्यक्ती को पास रखें।\",\"1\":\"अपने दोनों हात कमर पर रखें। अपना दुसरा पैर मोडकर जिस पैर का व्यायाम करना है उस पैर पर खडे रहिए।\",\"2\":\"एक अच्छी दशा में अपना संतुलन बनाए हुए खडे रहीए।\"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot pointing forwards.\",\"2\":\"Maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:44:07', '2022-07-02 08:44:07'),
(43, 'Balance Training- Single leg standing (Eyes closed)', 'exercise', '{\"0\":\"यदी आवश्यक हो तो तत्काल सहायता लेने के लिए या तो किसी दिवार के पास खडे हो जाएं या फिर किसी व्यक्ती को पास रखें।\",\"1\":\"अपने दोनों हात कमर पर रखें। अपना दुसरा पैर मोडकर जिस पैर का व्यायाम करना है उस पैर पर खडे रहिए।\",\"2\":\"अपनी दोनों ऑंखे बंद करके एक अच्छी दशा में अपना संतुलन बनाए हुए खडे रहीए।\"}', '{\"0\":\"Stand tall near a wall for taking immediate support if necessary/keep a person close for help.\",\"1\":\"Keep both your hands on the waist. Bend the non-exercising side leg from the knee and support your body one on foot pointing forwards.\",\"2\":\"Close your eyes, maintain a good posture and try and keep your balance.\"}', NULL, 1, '2022-07-02 08:47:28', '2022-07-02 08:47:28'),
