o_name\":\"Knee to chest mobilization- Unilateral\",\"video_type\":\"exercise\",\"video_youtube_id\":\"cLYAvniPjrg\",\"isActive\":1,\"created_at\":\"2022-07-07T06:25:19.000Z\",\"updated_at\":\"2022-07-07T06:25:19.000Z\",\"id\":1},\"instructionObj\":{\"instruction_id\":166,\"instruction_name\":\"Knee to chest unilateral mobilization\",\"instruction_type\":\"exercise\",\"instruction_description_hindi\":\"{\\\"0\\\":\\\"अपनी पीठ के बल लेट जाएं। \\\",\\\"1\\\":\\\"अपने एक घुटने को छाती की ओर ले आएं। \\\",\\\"2\\\":\\\"अपनी बाहों की मदद से घुटने को छाती से छूने की कोशिश करें।\\\"}\",\"instruction_description_english\":\"{\\\"0\\\":\\\"Lie down on your back.\\\",\\\"1\\\":\\\"Bring your knee towards the chest.\\\",\\\"2\\\":\\\"Try to touch the knee to the chest with help from your arms.\\\"}\",\"instruction_description_marathi\":null,\"isActive\":1,\"created_at\":\"2022-07-07T06:07:44.000Z\",\"updated_at\":\"2022-07-07T06:07:44.000Z\"}},\"2\":{\"exercise_id\":154,\"exercise_name\":\"Pelvic tilts- Supine\",\"exercise_reps\":2,\"exercise_holds\":0,\"exercise_sets\":0,\"exercise_rests\":30,\"exercise_body_part_id\":7,\"exercise_video_id\":159,\"exercise_instruction_id\":198,\"isActive\":1,\"created_at\":\"2022-07-08T23:05:10.000Z\",\"updated_at\":\"2022-07-08T23:05:10.000Z\",\"exercise_time\":30,\"isMultidirectional\":0,\"isTimeControlled\":1,\"instruction_name\":\"Pelvic tilts- Supine \",\"body_part_name\":\"LOW BACK & FLANK\",\"video_name\":\"Pelvic tilts- Supine\",\"exercise_days\":7,\"start_date\":\"2022-08-17\",\"end_date\":\"2022-08-17\",\"audioFilePath\":\"/uploads/audios/prakash_date2003@yahoo_com_Pelvic tilts- Supine_August_17_2022_audio.mp3\",\"videoObj\":{\"video_id\":159,\"video_name\":\"Pelvic tilts- Supine\",\"video_type\":\"exercise\",\"video_youtube_id\":\"kvWc2x0zzdc\",\"isActive\":1,\"created_at\":\"2022-07-08T22:56:00.000Z\",\"updated_at\":\"2022-07-08T22:56:00.000Z\",\"id\":1},\"instructionObj\":{\"instruction_id\":198,\"instruction_name\":\"Pelvic tilts- Supine \",\"instruction_type\":\"exercise\",\"instruction_description_hindi\":\"{\\\"0\\\":\\\"नितंब और घुटने को मोड़कर अपनी पीठ के बल लेट जाएं। \\\",\\\"1\\\":\\\"व्यायाम की सराहना करने के लिए एक हाथ को पीठ के निचले हिस्से के नीचे रखें। \\\",\\\"2\\\":\\\"अपने पीठ के निचले हिस्से को चपटा करें और इसे अपने हाथ के ऊपर धकेलें, बाद में इसे अपने हाथ से दूर करने के लिए पीठ के निचले हिस्से के बक्रता को अधिक करें।\\\"}\",\"instruction_description_english\":\"{\\\"0\\\":\\\"Lie down on your back with hip and knee bent.\\\",\\\"1\\\":\\\"Keep one hand below the lower back to appreciate the movement.\\\",\\\"2\\\":\\\"Flatten your lower and push it over your hand afterwards arch the lower back to take it away from your hand.\\\"}\",\"instruction_description_marathi\":null,\"isActive\":1,\"created_at\":\"2022-07-08T22:51:13.000Z\",\"updated_at\":\"2022-07-08T22:51:13.000Z\"}},\"3\":{\"exercise_id\":264,\"exercise_name\":\"Straight leg raise stretch\",\"exercise_reps\":1,\"exercise_holds\":15,\"exercise_sets\":\"4\",\"exercise_rests\":\"10\",\"exercise_body_part_id\":7,\"exercise_video_id\":275,\"exercise_instruction_id\":311,\"isActive\":1,\"created_at\":\"2022-07-16T06:02:19.000Z\",\"updated_at\":\"2022-07-16T06:02:19.000Z\",\"exercise_time\":0,\"isMultidirectional\":0,\"isTimeControlled\":0,\"instruction_name\":\"Straight leg raise stretch\",\"body_part_name\":\"LOW BACK & FLANK\",\"video_name\":\"Straight leg raise stretch\",\"exercise_days\":7,\"start_date\":\"2022-08-17\",\"end_date\":\"2022-08-17\",\"audioFilePath\":\"/uploads/audios/prakash_date2003@yahoo_com_Straight leg raise stretch_August_17_2022_audio.mp3\",\"videoObj\":{\"video_id\":275,\"video_name\":\"Straight leg raise stretch\",\"video_type\":\"exercise\",\"video_youtube_id\":\"aWYqVxL206M\",\"isActive\":1,\"created_at\":\"2022-07-16T05:38:10.000Z\",\"updated_at\":\"2022-07-16T05:38:10.000Z\",\"id\":1},\"instructionObj\":{\"instruction_id\":311,\"instruction_name\":\"Straight leg raise stretch\",\"instruction_type\":\"exercise\",\"instruction_description_hindi\":\"{\\\"0\\\":\\\"अपनी पीठ के बल लेट जाएं।\\\",\\\"1\\\":\\\"अपने व्यायाम करने वाले पैर में एक तौलिया/बेल्ट/दुपट्टा फसाते हुए उसके दोनों सिरों को अपने हाथों में पकड़ें। \\\",\\\"2\\\":\\\"दोनों सिरों को अपनी ओर खींचते हुए और घुटने को झुकाए बिना पैर को सीधा ऊपर उठाएं।\\\",\\\"3\\\":\\\"इसे तब तक उठाएं जब तक आप अपने पैर के पिछले हिस्से में आरामदायक खिंचाव महसूस न करें।\\\"}\",\"instruction_description_english\":\"{\\\"0\\\":\\\"Lie down on your back.\\\",\\\"1\\\":\\\"Hook a towel/belt/dupatta around your exercising side foot and hold the ends in your hands.\\\",\\\"2\\\":\\\"Pull both the ends towards you to pull the foot towards yourself and lift the leg straight up without bending the knee.\\\",\\\"3\\\":\\\"Take it up till you feel a comfortable stretch in the back of your leg.\\\"}\",\"instruction_description_marathi\":null,\"isActive\":1,\"created_at\":\"2022-07-16T05:22:47.000Z\",\"updated_at\":\"2022-07-16T05:22:47.000Z\"}},\"4\":{\"exercise_id\":79,\"exercise_name\":\"Figure 4 stretch\",\"exercise_reps\":1,\"exercise_holds\":\"15\",\"exercise_sets\":\"4\",\"exercise_rests\":\"10\",\"exercise_body_part_id\":8,\"exercise_video_id\":83,\"exercise_instruction_id\":106,\"isActive\":1,\"created_at\":\"2022-07-05T21:46:25.000Z\",\"updated_at\":\"2022-07-05T21:46:25.000Z\",\"exercise_time\":0,\"isMultidirectional\":0,\"isTimeControlled\":0,\"instruction_name\":\"Figure 4 stretch\",\"body_part_name\":\"HIP\",\"video_name\":\"Figure 4 stretch\",\"exercise_days\":7,\"start_date\":\"2022-08-17\",\"end_date\":\"2022-08-17\",\"audioFilePath\":\"/uploads/audios/prakash_date2003@yahoo_com_Figure 4 stretch_August_17_2022_audio.mp3\",\"videoObj\":{\"video_id\":83,\"video_name\":\"Figure 4 stretch\",\"video_type\":\"exercise\",\"video_youtube_id\":\"VyVnUS6qEDk\",\"isActive\":1,\"created_at\":\"2022-07-05T21:26:38.000Z\",\"updated_at\":\"2022-07-05T21:26:38.000Z\",\"id\":1},\"instructionObj\":{\"instruction_id\":106,\"instru